'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Content } from '@radix-ui/react-alert-dialog';
import { useComponentConfig } from '../config-provider/context';
import AlertDialogUI from './AlertDialogUI';
import type { AlertDialogProps } from './types';

const AlertDialog = forwardRef<ComponentRef<typeof Content>, AlertDialogProps>((props, ref) => {
  const config = useComponentConfig('alertDialog');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <AlertDialogUI
      {...mergedProps}
      ref={ref}
    />
  );
});

AlertDialog.displayName = 'AlertDialog';

export default AlertDialog;

import { forwardRef } from 'react';
import { Action } from '@radix-ui/react-alert-dialog';
import { Button, type ButtonProps } from '../button';

const AlertDialogAction = forwardRef<HTMLButtonElement, ButtonProps>((props, ref) => {
  return (
    <Action asChild>
      <Button
        data-slot="alert-dialog-action"
        ref={ref}
        {...props}
      />
    </Action>
  );
});

AlertDialogAction.displayName = 'AlertDialogAction';

export default AlertDialogAction;

import { forwardRef } from 'react';
import { Cancel } from '@radix-ui/react-alert-dialog';
import { Button, type ButtonProps } from '../button';

const AlertDialogCancel = forwardRef<HTMLButtonElement, ButtonProps>((props, ref) => {
  const { variant = 'plain', ...rest } = props;

  return (
    <Cancel asChild>
      <Button
        data-slot="alert-dialog-cancel"
        ref={ref}
        variant={variant}
        {...rest}
      />
    </Cancel>
  );
});

AlertDialogCancel.displayName = 'AlertDialogCancel';

export default AlertDialogCancel;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Content } from '@radix-ui/react-alert-dialog';
import { cn } from '@/lib/utils';
import { dialogVariants } from './alert-dialog-variants';
import type { AlertDialogContentProps } from './types';

const AlertDialogContent = forwardRef<ComponentRef<typeof Content>, AlertDialogContentProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { content } = dialogVariants({ size });

  const mergedClass = cn(content(), className);
  return (
    <Content
      className={mergedClass}
      data-slot="alert-dialog-content"
      {...rest}
      ref={ref}
    />
  );
});

AlertDialogContent.displayName = 'AlertDialogContent';

export default AlertDialogContent;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Description } from '@radix-ui/react-alert-dialog';
import { cn } from '@/lib/utils';
import { dialogVariants } from './alert-dialog-variants';
import type { AlertDialogDescriptionProps } from './types';

const AlertDialogDescription = forwardRef<ComponentRef<typeof Description>, AlertDialogDescriptionProps>(
  (props, ref) => {
    const { className, size, ...rest } = props;

    const { description } = dialogVariants({ size });

    const mergedClass = cn(description(), className);
    return (
      <Description
        className={mergedClass}
        data-slot="alert-dialog-description"
        {...rest}
        ref={ref}
      />
    );
  }
);

AlertDialogDescription.displayName = 'AlertDialogDescription';

export default AlertDialogDescription;

