AlertDialogTitle.displayName = 'AlertDialogTitle';

export default AlertDialogTitle;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Content } from '@radix-ui/react-alert-dialog';
import { AlertDialog as AlertDialogRoot, Portal, Trigger } from '@radix-ui/react-alert-dialog';
import { Slot } from '@radix-ui/react-slot';
import { CircleAlert, CircleCheck, CircleX, Info } from 'lucide-react';
import AlertDialogAction from './AlertDialogAction';
import AlertDialogCancel from './AlertDialogCancel';
import AlertDialogContent from './AlertDialogContent';
import AlertDialogDescription from './AlertDialogDescription';
import AlertDialogFooter from './AlertDialogFooter';
import AlertDialogHeader from './AlertDialogHeader';
import AlertDialogOverlay from './AlertDialogOverlay';
import AlertDialogTitle from './AlertDialogTitle';
import type { AlertDialogProps, AlertType } from './types';

const iconRecord: Record<AlertType, React.ReactNode> = {
  destructive: <CircleX className="text-destructive" />,
  info: <Info className="text-info" />,
  success: <CircleCheck className="text-success" />,
  warning: <CircleAlert className="text-warning" />
};

const AlertDialogUI = forwardRef<ComponentRef<typeof Content>, AlertDialogProps>((props, ref) => {
  const {
    cancelText = 'Cancel',
    cancelButtonProps,
    children,
    className,
    classNames,
    footer,
    forceMountOverlay,
    forceMountPortal,
    icon,
    okText = 'OK',
    okButtonProps,
    onCancel,
    onOk,
    showCancel = true,
    size,
    title,
    description,
    trigger,
    type,
    overlayProps,
    contentProps,
    headerProps,
    titleProps,
    descriptionProps,
    footerProps,
    ...rest
  } = props;

  return (
    <AlertDialogRoot {...props}>
      <Trigger asChild>{trigger}</Trigger>

      <Portal forceMount={forceMountPortal}>
        <AlertDialogOverlay
          className={classNames?.overlay}
          forceMount={forceMountOverlay}
          {...overlayProps}
        />

        <AlertDialogContent
          {...rest}
          className={className || classNames?.content}
          ref={ref}
          size={size}
          {...contentProps}
        >
          <AlertDialogHeader
            className={classNames?.header}
            size={size}
            {...headerProps}
          >
            <AlertDialogTitle
              className={classNames?.title}
              size={size}
              {...titleProps}
            >
              {icon || (type && <Slot className={classNames?.icon || ''}>{iconRecord[type]}</Slot>)}
              {title}
            </AlertDialogTitle>

            <AlertDialogDescription
              className={classNames?.description}
              size={size}
              {...descriptionProps}
            >
              {description}
            </AlertDialogDescription>

          </AlertDialogHeader>

          {children}

          {footer !== null && footer !== false && (
            <AlertDialogFooter
              className={classNames?.footer}
              size={size}
              {...footerProps}
            >
              {footer || (
                <>
                  {showCancel
                    ? (
                      <AlertDialogCancel
                        onClick={onCancel}
                        {...cancelButtonProps}
                      >
                        {cancelText}
                      </AlertDialogCancel>
                    )
                    : null}

                  <AlertDialogAction
                    onClick={onOk}
                    {...okButtonProps}
                  >
                    {okText}
                  </AlertDialogAction>
                </>
              )}
            </AlertDialogFooter>
          )}
        </AlertDialogContent>
      </Portal>
    </AlertDialogRoot>
  );
});

AlertDialogUI.displayName = 'AlertDialogUI';

export default AlertDialogUI;

import { tv } from 'tailwind-variants';

export const dialogVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    closeIcon: `absolute`,
    content: [
      `fixed left-[50%] top-[50%] z-50 flex flex-col w-full border bg-background shadow-lg translate-x-[-50%] translate-y-[-50%] md:w-full duration-200 sm:rounded-lg`,
      `data-[state=open]:animate-in data-[state=open]:fade-in-0 data-[state=open]:zoom-in-95 data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%]`,
      `data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=closed]:zoom-out-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%]`
    ],
    description: `text-muted-foreground`,
    footer: `flex flex-col-reverse sm:flex-row sm:justify-end`,
    header: `flex flex-col text-center sm:text-left`,
    overlay: [
      `fixed inset-0 z-50 bg-black/80`,
      `data-[state=open]:animate-in data-[state=open]:fade-in-0`,
      `data-[state=closed]:animate-out data-[state=closed]:fade-out-0`
    ],
    title: `flex items-center font-semibold leading-none tracking-tight`
  },
  variants: {
    size: {
      '2xl': {
        closeIcon: 'right-6 top-6',
        content: `gap-y-6 max-w-3xl px-7 py-6 text-xl`,
        description: 'text-xl',
        footer: 'gap-6',
        header: 'gap-y-6',
        title: 'gap-x-3.5 text-2xl'
      },
      'lg': {
        closeIcon: 'right-4 top-4',
        content: `gap-y-4 max-w-xl px-5 py-4 text-base`,
        description: 'text-base',
        footer: 'gap-4',
        header: 'gap-y-4',
        title: 'gap-x-2.5 text-lg'
      },
      'md': {
        closeIcon: 'right-3 top-3',
        content: `gap-y-3 max-w-lg px-4 py-3 text-sm`,
        description: 'text-sm',
        footer: 'gap-3',
        header: 'gap-y-3',
        title: 'gap-x-2 text-base'
      },
      'sm': {
        closeIcon: 'right-2 top-2',
        content: `gap-y-2 max-w-md px-3 py-2 text-xs`,
        description: 'text-xs',
        footer: 'gap-2',
        header: 'gap-y-2',
        title: 'gap-x-1.75 text-sm'
      },
      'xl': {
        closeIcon: 'right-5 top-5',
        content: `gap-y-5 max-w-2xl px-6 py-5 text-lg`,
        description: 'text-lg',
        footer: 'gap-5',
        header: 'gap-y-5',
        title: 'gap-x-3 text-xl'
      },
      'xs': {
        closeIcon: 'right-1.5 top-1.5',
        content: `gap-y-1.5 max-w-md px-2 py-1.5 text-2xs`,
        description: 'text-2xs',
        footer: 'gap-1.5',
        header: 'gap-y-1.5',
        title: 'gap-x-1.5 text-xs'
      }
    }
  }
});

export type DialogSlots = keyof typeof dialogVariants.slots;

export { default as AlertDialog } from './AlertDialog';

export { default as AlertDialogAction } from './AlertDialogAction';
export { default as AlertDialogCancel } from './AlertDialogCancel';
export { default as AlertDialogContent } from './AlertDialogContent';
export { default as AlertDialogDescription } from './AlertDialogDescription';
export { default as AlertDialogFooter } from './AlertDialogFooter';
export { default as AlertDialogHeader } from './AlertDialogHeader';
export { default as AlertDialogOverlay } from './AlertDialogOverlay';
export { default as AlertDialogTitle } from './AlertDialogTitle';

export * from './types';

import type {
  AlertDialogContentProps as _AlertDialogContentProps,
  AlertDialogDescriptionProps as _AlertDialogDescriptionProps,
  AlertDialogOverlayProps as _AlertDialogOverlayProps,
  AlertDialogPortalProps as _AlertDialogPortalProps,
  AlertDialogProps as _AlertDialogProps,
  AlertDialogTitleProps as _AlertDialogTitleProps
} from '@radix-ui/react-alert-dialog';
import type { ClassValue, HTMLComponentProps, StyledComponentProps, ThemeColor } from '@/types/shared';
import type { ButtonProps } from '../button';
import type { DialogSlots } from './alert-dialog-variants';

/**
 * Class names for different slots in the alert dialog component.
 * Allows customizing styles for specific parts of the alert dialog, including the icon slot.
 */
export type AlertDialogClassNames = Partial<Record<DialogSlots, ClassValue>> & {
  /**
   * Class names for the icon element within the alert dialog.
   */
  icon?: string;
};

/**
 * Alert type enumeration that restricts theme colors to alert-specific types.
 * Supports destructive, info, success, and warning alert types.
 */
export type AlertType = Extract<ThemeColor, 'destructive' | 'info' | 'success' | 'warning'>;

/**
 * Props for the alert dialog content component.
 * Contains the main content area of the alert dialog.
 */
export interface AlertDialogContentProps extends StyledComponentProps<_AlertDialogContentProps> {}

/**
 * Props for the alert dialog description component.
 * Provides descriptive text or additional information in the alert dialog.
 */
export interface AlertDialogDescriptionProps extends StyledComponentProps<_AlertDialogDescriptionProps> {}

/**
 * Props for the alert dialog footer component.
 * Container for action buttons or additional footer content.
 */
export type AlertDialogFooterProps = HTMLComponentProps<'div'>;

/**
 * Props for the alert dialog header component.
 * Container for title, icon, and other header content.
 */
export type AlertDialogHeaderProps = HTMLComponentProps<'div'>;

/**
 * Props for the alert dialog overlay component.
 * Semi-transparent backdrop that appears behind the alert dialog.
 */
export interface AlertDialogOverlayProps extends StyledComponentProps<_AlertDialogOverlayProps> {}

/**
 * Props for the alert dialog title component.
 * Displays the primary heading text of the alert dialog.
 */
export interface AlertDialogTitleProps extends StyledComponentProps<_AlertDialogTitleProps> {}

/**
 * Props for the main AlertDialog component.
 * Combines base Radix UI alert dialog props with custom styling and content options.
 *
 * @example
 * ```jsx
 * <AlertDialog
 *   type="destructive"
 *   title="Confirm Deletion"
 *   description="This action cannot be undone."
 *   icon={<TrashIcon />}
 * >
 *   <Button>Delete</Button>
 * </AlertDialog>
 * ```
 */
export type AlertDialogProps = StyledComponentProps<_AlertDialogProps>
  & AlertDialogContentProps
  & _AlertDialogPortalProps & {
    /**
     * Text of the cancel button. Default is "Cancel".
     */
    cancelText?: React.ReactNode;
    /**
     * Props for the cancel button.
     */
    cancelButtonProps?: ButtonProps;
    /**
     * Class names for customizing different parts of the alert dialog.
     */
    classNames?: AlertDialogClassNames;
    /**
     * Whether to disable portal rendering for the alert dialog.
     */
    disabledPortal?: boolean;
    /**
     * Description text displayed in the alert dialog.
     */
    description: string;
    /**
     * Content to display in the footer section (typically action buttons).
     * Set to `null` to hide the default footer buttons.
     * Set to a React node to render custom footer content.
     */
    footer?: React.ReactNode | null | false;
    /**
     * Force mount the overlay element even when not visible.
     */
    forceMountOverlay?: true;
    /**
     * Force mount the portal element for the alert dialog.
     */
    forceMountPortal?: true;
    /**
     * Icon or visual element displayed at the top of the alert dialog.
     */
    icon?: React.ReactNode;
    /**
     * Text of the OK button. Default is "OK".
     */
    okText?: React.ReactNode;
    /**
     * Props for the OK button.
     */
    okButtonProps?: ButtonProps;
    /**
     * Callback when cancel button is clicked.
     */
    onCancel?: (e: React.MouseEvent<HTMLButtonElement>) => void;
    /**
     * Callback when OK button is clicked.
     */
    onOk?: (e: React.MouseEvent<HTMLButtonElement>) => void;
    /**
     * Whether to show the cancel button. Default is true.
     */
    showCancel?: boolean;
    /**
     * Title or heading text displayed at the top of the alert dialog.
     */
    title?: React.ReactNode;
    /**
     * Element that triggers the alert dialog to open when clicked.
     */
    trigger?: React.ReactNode;
    /**
     * The alert type which determines styling (destructive, info, success, or warning).
     */
    type?: AlertType;
    /**
     * Props for the alert dialog overlay component.
     */
    overlayProps?: AlertDialogOverlayProps;
    /**
     * Props for the alert dialog content component.
     */
    contentProps?: AlertDialogContentProps;
    /**
     * Props for the alert dialog header component.
     */
    headerProps?: AlertDialogHeaderProps;
    /**
     * Props for the alert dialog title component.
     */
    titleProps?: AlertDialogTitleProps;
    /**
     * Props for the alert dialog description component.
     */
    descriptionProps?: AlertDialogDescriptionProps;
    /**
     * Props for the alert dialog footer component.
     */
    footerProps?: AlertDialogFooterProps;
  };
