'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Image } from '@radix-ui/react-avatar';
import { useComponentConfig } from '../config-provider/context';
import AvatarUI from './AvatarUI';
import type { AvatarProps } from './types';

const Avatar = forwardRef<ComponentRef<typeof Image>, AvatarProps>((props, ref) => {
  const config = useComponentConfig('avatar');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <AvatarUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Avatar.displayName = 'Avatar';

export default Avatar;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Fallback } from '@radix-ui/react-avatar';
import { cn } from '@/lib/utils';
import { avatarVariants } from './avatar-variants';
import type { AvatarFallbackProps } from './types';

const AvatarFallback = forwardRef<ComponentRef<typeof Fallback>, AvatarFallbackProps>((props, ref) => {
  const { className, ...rest } = props;

  const { fallback } = avatarVariants();

  const mergedCls = cn(fallback(), className);

  return (
    <Fallback
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

AvatarFallback.displayName = Fallback.displayName;

export default AvatarFallback;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Image } from '@radix-ui/react-avatar';
import { cn } from '@/lib/utils';
import { avatarVariants } from './avatar-variants';
import type { AvatarImageProps } from './types';

const AvatarImage = forwardRef<ComponentRef<typeof Image>, AvatarImageProps>((props, ref) => {
  const { className, ...rest } = props;

  const { image } = avatarVariants();

  const mergedCls = cn(image(), className);

  return (
    <Image
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

AvatarImage.displayName = Image.displayName;

export default AvatarImage;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root } from '@radix-ui/react-avatar';
import { cn } from '@/lib/utils';
import { avatarVariants } from './avatar-variants';
import type { AvatarRootProps } from './types';

const AvatarRoot = forwardRef<ComponentRef<typeof Root>, AvatarRootProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { root } = avatarVariants({ size });

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

AvatarRoot.displayName = Root.displayName;

export default AvatarRoot;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Image } from '@radix-ui/react-avatar';
import AvatarFallback from './AvatarFallback';
import AvatarImage from './AvatarImage';
import AvatarRoot from './AvatarRoot';
import type { AvatarProps } from './types';

const Avatar = forwardRef<ComponentRef<typeof Image>, AvatarProps>((props, ref) => {
  const { className, classNames, delayMs, fallback, size, ...rest } = props;

  return (
    <AvatarRoot
      className={classNames?.root}
      size={size}
    >
      <AvatarImage
        className={className || classNames?.image}
        ref={ref}
        {...rest}
      />

      <AvatarFallback
        className={classNames?.fallback}
        delayMs={delayMs}
      >
        {fallback}
      </AvatarFallback>
    </AvatarRoot>
  );
});

Avatar.displayName = 'AvatarUI';

export default Avatar;

import { tv } from 'tailwind-variants';

export const avatarVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    fallback: 'flex justify-center items-center size-full rounded-full bg-muted font-medium',
    image: 'aspect-square size-full object-cover',
    root: 'relative flex shrink-0 overflow-hidden rounded-full'
  },
  variants: {
    size: {
      '2xl': {
        root: 'size-16'
      },
      'lg': {
        root: 'size-12'
      },
      'md': {
        root: 'size-10'
      },
      'sm': {
        root: 'size-8'
      },
      'xl': {
        root: 'size-14'
      },
      'xs': {
        root: 'size-6'
      }
    }
  }
});

export type AvatarSlots = keyof typeof avatarVariants.slots;

export { default as Avatar } from './Avatar';
export { default as AvatarFallback } from './AvatarFallback';
export { default as AvatarImage } from './AvatarImage';
export { default as AvatarRoot } from './AvatarRoot';

export * from './types';

import type {
  AvatarFallbackProps as _AvatarFallbackProps,
  AvatarImageProps as _AvatarImageProps,
  AvatarProps as _AvatarRootProps
} from '@radix-ui/react-avatar';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { AvatarSlots } from './avatar-variants';

/**
 * Class names for different slots in the avatar component.
 * Allows customizing styles for specific parts of the avatar.
 */
export type AvatarUi = Partial<Record<AvatarSlots, ClassValue>>;

/**
 * Props for the avatar root component.
 * Serves as the main container for the avatar element.
 */
export interface AvatarRootProps extends StyledComponentProps<_AvatarRootProps> {}

/**
 * Props for the avatar fallback component.
 * Displays alternative content when the avatar image fails to load.
 */
export interface AvatarFallbackProps extends StyledComponentProps<_AvatarFallbackProps> {}

/**
 * Props for the avatar image component.
 * Displays the avatar image from a specified source.
 */
export interface AvatarImageProps extends StyledComponentProps<_AvatarImageProps> {}

/**
 * Props for the main Avatar component.
 * Combines image and fallback content for displaying user avatars with graceful degradation.
 *
 * @example
 * ```jsx
 * <Avatar
 *   src="https://example.com/avatar.jpg"
 *   alt="John Doe"
 *   fallback="JD"
 *   delayMs={600}
 * />
 * ```
 */
export interface AvatarProps extends AvatarImageProps, Pick<AvatarFallbackProps, 'delayMs'> {
  /**
   * Class names for customizing different parts of the avatar.
   */
  classNames?: AvatarUi;
  /**
   * Fallback content displayed when the image fails to load or is still loading.
   * Typically shows initials or a placeholder icon.
   */
  fallback?: React.ReactNode;
}
