'use client';

import { useComponentConfig } from '../config-provider/context';
import BadgeUI from './BadgeUI';
import type { BadgeProps } from './types';

const Badge = (props: BadgeProps) => {
  const config = useComponentConfig('badge');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <BadgeUI
      {...mergedProps}
    />
  );
};

Badge.displayName = 'Badge';

export default Badge;

import { cn } from '@/lib/utils';
import { badgeVariants } from './badge-variants';
import type { BadgeProps } from './types';

const BadgeUI = (props: BadgeProps) => {
  const { className, color, shape, size, variant, ...rest } = props;

  const mergedCls = cn(badgeVariants({ color, shape, size, variant }), className);

  return (
    <div
      {...rest}
      className={mergedCls}
    />
  );
};

BadgeUI.displayName = 'BadgeUI';

export default BadgeUI;

import { tv } from 'tailwind-variants';
import type { VariantProps } from 'tailwind-variants';

export const badgeVariants = tv({
  base: 'inline-flex items-center border font-semibold',
  compoundVariants: [
    {
      class: `bg-primary text-primary-foreground`,
      color: 'primary',
      variant: 'solid'
    },
    {
      class: `bg-destructive text-destructive-foreground`,
      color: 'destructive',
      variant: 'solid'
    },
    {
      class: `bg-success text-success-foreground`,
      color: 'success',
      variant: 'solid'
    },
    {
      class: `bg-warning text-warning-foreground`,
      color: 'warning',
      variant: 'solid'
    },
    {
      class: `bg-info text-info-foreground`,
      color: 'info',
      variant: 'solid'
    },
    {
      class: `bg-carbon text-carbon-foreground`,
      color: 'carbon',
      variant: 'solid'
    },
    {
      class: `bg-secondary text-secondary-foreground`,
      color: 'secondary',
      variant: 'solid'
    },
    {
      class: `bg-accent text-accent-foreground`,
      color: 'accent',
      variant: 'solid'
    },
    {
      class: 'bg-primary/10',
      color: 'primary',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-destructive/10',
      color: 'destructive',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-success/10',
      color: 'success',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-warning/10',
      color: 'warning',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-info/10',
      color: 'info',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-carbon/10',
      color: 'carbon',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-secondary-foreground/5',
      color: 'secondary',
      variant: ['soft', 'ghost']
    },
    {
      class: 'bg-accent-foreground/5',
      color: 'accent',
      variant: ['soft', 'ghost']
    }
  ],
  defaultVariants: {
    color: 'primary',
    shape: 'auto',
    size: 'md',
    variant: 'solid'
  },
  variants: {
    color: {
      accent: 'border-accent-foreground/50 text-accent-foreground',
      carbon: 'border-carbon text-carbon',
      destructive: 'border-destructive text-destructive',
      info: 'border-info text-info',
      primary: 'border-primary text-primary',
      secondary: 'border-secondary-foreground/50 text-secondary-foreground',
      success: 'border-success text-success',
      warning: 'border-warning text-warning'
    },
    shape: {
      auto: 'rounded-md',
      rounded: 'rounded-full'
    },
    size: {
      '2xl': 'gap-2 h-7 px-4 text-base',
      'lg': 'gap-1.25 h-5 px-2.5 text-xs',
      'md': 'gap-1 h-4.5 px-2 text-2xs',
      'sm': 'gap-0.75 h-4 px-1.5 text-3xs',
      'xl': 'gap-1.5 h-6 px-3 text-sm',
      'xs': 'gap-0.5 h-3.5 px-1 text-4xs'
    },
    variant: {
      ghost: '',
      outline: 'bg-background',
      pure: 'bg-background text-foreground border-border',
      raw: 'bg-transparent border-0',
      soft: 'border-0',
      solid: ''
    }
  }
});

type BadgeVariants = VariantProps<typeof badgeVariants>;

export type BadgeVariant = NonNullable<BadgeVariants['variant']>;

export type BadgeShape = NonNullable<BadgeVariants['shape']>;

import type { StyledComponentProps, ThemeColor } from '@/types/shared';
import type { BadgeShape, BadgeVariant } from './badge-variants';

/**
 * Props for the Badge component.
 * A small, styled label component used to highlight, categorize, or mark items.
 *
 * @example
 * ```jsx
 * <Badge color="primary" variant="solid" shape="pill">
 *   New
 * </Badge>
 * ```
 */
export interface BadgeProps extends StyledComponentProps<React.ComponentProps<'div'>> {
  /**
   * The color theme of the badge (e.g., primary, secondary, destructive, success, etc.).
   */
  color?: ThemeColor;
  /**
   * The shape variant of the badge (e.g., square, rounded, pill).
   */
  shape?: BadgeShape;
  /**
   * The visual style variant of the badge (e.g., solid, outline, subtle, ghost).
   */
  variant?: BadgeVariant;
}

export type { BadgeShape, BadgeVariant };
