'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Content } from 'vaul';
import { useComponentConfig } from '../config-provider/context';
import BottomSheetUI from './BottomSheetUI';
import type { BottomSheetProps } from './types';

const BottomSheet = forwardRef<ComponentRef<typeof Content>, BottomSheetProps>((props, ref) => {
  const config = useComponentConfig('bottomSheet');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <BottomSheetUI
      {...mergedProps}
      ref={ref}
    />
  );
});

BottomSheet.displayName = 'BottomSheet';

export default BottomSheet;

import { Drawer } from 'vaul';
import { DialogClose } from '../dialog';
import type { BottomSheetCloseProps } from './types';

const BottomSheetClose = (props: BottomSheetCloseProps) => {
  return (
    <DialogClose
      component={Drawer.Close}
      {...props}
    />
  );
};

export default BottomSheetClose;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Content, Portal } from 'vaul';
import { cn } from '@/lib/utils';
import BottomSheetContentBody from './BottomSheetContentBody';
import BottomSheetKnob from './BottomSheetKnob';
import BottomSheetOverlay from './BottomSheetOverlay';
import { bottomSheetVariants } from './bottom-sheet-variants';
import type { BottomSheetContentProps } from './types';

const BottomSheetContent = forwardRef<ComponentRef<typeof Content>, BottomSheetContentProps>((props, ref) => {
  const { children, className, classNames, size, ...rest } = props;

  const { content } = bottomSheetVariants({ size });

  const mergedCls = cn(content(), className, classNames?.content);

  return (
    <Portal>
      <BottomSheetOverlay className={classNames?.overlay} />

      <Content
        className={mergedCls}
        data-slot="bottom-sheet-content"
        ref={ref}
        {...rest}
      >
        <BottomSheetContentBody className={classNames?.contentBody}>
          <BottomSheetKnob className={classNames?.knob} />
          {children}
        </BottomSheetContentBody>
      </Content>
    </Portal>
  );
});

BottomSheetContent.displayName = 'BottomSheetContent';

export default BottomSheetContent;

import { cn } from '@/lib/utils';
import { bottomSheetVariants } from './bottom-sheet-variants';
import type { BottomSheetContentBodyProps } from './types';

const BottomSheetContentBody = (props: BottomSheetContentBodyProps) => {
  const { className, size, ...rest } = props;

  const { contentBody } = bottomSheetVariants({ size });

  const mergedCls = cn(contentBody(), className);
  return (
    <div
      className={mergedCls}
      {...rest}
    />
  );
};

export default BottomSheetContentBody;

import { Drawer } from 'vaul';
import { DialogDescription } from '../dialog';
import type { BottomSheetDescriptionProps } from './types';

const BottomSheetDescription = (props: BottomSheetDescriptionProps) => {
  return (
    <DialogDescription
      component={Drawer.Description}
      {...props}
    />
  );
};

export default BottomSheetDescription;

import { cn } from '@/lib/utils';
import { bottomSheetVariants } from './bottom-sheet-variants';
import type { BottomSheetKnobProps } from './types';

const BottomSheetKnob = (props: BottomSheetKnobProps) => {
  const { className, size, ...rest } = props;

  const { knob } = bottomSheetVariants({ size });

  const mergedCls = cn(knob(), className);

  return (
    <div
      className={mergedCls}
      {...rest}
    />
  );
};

export default BottomSheetKnob;

import { Overlay as _Overlay } from 'vaul';
import { DialogOverlay } from '../dialog';
import type { BottomSheetOverlayProps } from './types';

const BottomSheetOverlay = (props: BottomSheetOverlayProps) => {
  return (
    <DialogOverlay
      component={_Overlay}
      {...props}
    />
  );
};

export default BottomSheetOverlay;

import { Drawer } from 'vaul';
import { DialogTitle } from '../dialog';
import type { BottomSheetTitleProps } from './types';

const BottomSheetTitle = (props: BottomSheetTitleProps) => {
  return (
    <DialogTitle
      component={Drawer.Title}
      {...props}
    />
  );
};

export default BottomSheetTitle;

import { type ComponentRef, forwardRef } from 'react';
import type { Content } from 'vaul';
import { Root as _Root } from 'vaul';
import { DialogFooter, DialogHeader, DialogTrigger } from '../dialog';
import BottomSheetClose from './BottomSheetClose';
import BottomSheetContent from './BottomSheetContent';
import BottomSheetDescription from './BottomSheetDescription';
import BottomSheetTitle from './BottomSheetTitle';
import type { BottomSheetProps } from './types';

const BottomSheetUI = forwardRef<ComponentRef<typeof Content>, BottomSheetProps>((props, ref) => {
  const {
    children,
    classNames,
    contentProps,
    description,
    footer,
    shouldScaleBackground = true,
    showClose,
    size,
    title,
    trigger,
    headerProps,
    titleProps,
    descriptionProps,
    closeProps,
    footerProps
  } = props;

  return (
    <_Root
      shouldScaleBackground={shouldScaleBackground}
      {...props}
    >
      <DialogTrigger asChild>{trigger}</DialogTrigger>

      <BottomSheetContent
        classNames={classNames}
        ref={ref}
        {...contentProps}
      >
        <DialogHeader
          className={classNames?.header}
          size={size}
          {...headerProps}
        >
          <BottomSheetTitle
            className={classNames?.title}
            size={size}
            {...titleProps}
          >
            {title}
          </BottomSheetTitle>

          <BottomSheetDescription
            className={classNames?.description}
            size={size}
            {...descriptionProps}
          >
            {description}
          </BottomSheetDescription>
        </DialogHeader>

        {showClose
          ? (
            <BottomSheetClose
              className={classNames?.close}
              size={size}
              {...closeProps}
            />
          )
          : null}

        {children}

        {footer
          ? (
            <DialogFooter
              className={classNames?.footer || '!flex-col-reverse'}
              size={size}
              {...footerProps}
            >
              {footer}
            </DialogFooter>
          )
          : null}
      </BottomSheetContent>
    </_Root>
  );
});

BottomSheetUI.displayName = 'BottomSheetUI';

export default BottomSheetUI;

import { tv } from 'tailwind-variants';
import { dialogVariants } from '../dialog/dialog-variants';

export const bottomSheetVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    ...dialogVariants.slots,
    content: `fixed inset-x-0 bottom-0 z-50 border bg-background`,
    contentBody: `flex h-auto flex-col`,
    knob: `mx-auto shrink-0 cursor-grab active:cursor-grabbing rounded-full bg-muted`
  },
  variants: {
    size: {
      '2xl': {
        content: `mt-30 rounded-t-[16px]`,
        contentBody: `gap-y-6 max-w-3xl px-7 py-6 text-xl`,
        knob: 'mt-7 h-3.5 w-40'
      },
      'lg': {
        content: `mt-26 rounded-t-[12px]`,
        contentBody: `gap-y-4 max-w-xl px-5 py-4 text-base`,
        knob: 'mt-5 h-2.5 w-30'
      },
      'md': {
        content: `mt-24 rounded-t-[10px]`,
        contentBody: `gap-y-3 max-w-lg px-4 py-3 text-sm`,
        knob: 'mt-4 h-2 w-25'
      },
      'sm': {
        content: `mt-22 rounded-t-[8px]`,
        contentBody: `gap-y-2 max-w-md px-3 py-2 text-xs`,
        knob: 'mt-3.5 h-1.75 w-22'
      },
      'xl': {
        content: `mt-28 rounded-t-[14px]`,
        contentBody: `gap-y-5 max-w-2xl px-6 py-5 text-lg`,
        knob: 'mt-6 h-3 w-35'
      },
      'xs': {
        content: `mt-20 rounded-t-[6px]`,
        contentBody: `gap-y-1.5 max-w-md px-2 py-1.5 text-2xs`,
        knob: 'mt-3 h-1.5 w-20'
      }
    }
  }
});

export type BottomSheetSlots = keyof typeof bottomSheetVariants.slots;

export { Root as BottomSheetRoot } from 'vaul';

export { DialogFooter as BottomSheetFooter, DialogHeader as BottomSheetHeader, DialogTrigger as BottomSheetTrigger } from '../dialog';

export { default as BottomSheet } from './BottomSheet';

export { default as BottomSheetClose } from './BottomSheetClose';
export { default as BottomSheetContent } from './BottomSheetContent';
export { default as BottomSheetContentBody } from './BottomSheetContentBody';
export { default as BottomSheetDescription } from './BottomSheetDescription';
export { default as BottomSheetKnob } from './BottomSheetKnob';
export { default as BottomSheetOverlay } from './BottomSheetOverlay';
export { default as BottomSheetTitle } from './BottomSheetTitle';

export * from './types';

import type { ContentProps, DialogProps } from 'vaul';
import type { HTMLComponentProps, StyledComponentProps, ClassValue, ThemeSize } from '@/types/shared';
import type {
  DialogCloseProps,
  DialogDescriptionProps,
  DialogHeaderProps,
  DialogOverlayProps,
  DialogTitleProps
} from '../dialog';
import type { DialogSlots } from '../dialog/dialog-variants';
import type { BottomSheetSlots } from './bottom-sheet-variants';

/**
 * Class names for different slots in the bottom-sheet component.
 * Allows customizing styles for bottom-sheet-specific and dialog-shared parts.
 */
export type BottomSheetClassNames = Partial<Record<BottomSheetSlots | DialogSlots, ClassValue>>;

/**
 * Props for the bottom-sheet content component.
 * Wraps the main content area of the bottom-sheet with styling and slot customization.
 *
 * @example
 * ```tsx
 * <BottomSheet.Content
 *   contentProps={{
 *     classNames: {
 *       content: 'custom-content-class',
 *       knob: 'custom-knob-class'
 *     }
 *   }}
 * >
 *   Content here
 * </BottomSheet.Content>
 * ```
 */
export type BottomSheetContentProps = StyledComponentProps<ContentProps> & {
  /**
   * Class names for customizing specific parts of the bottom-sheet content area.
   * Includes content, contentBody, knob, and overlay styling slots.
   */
  classNames?: Pick<BottomSheetClassNames, 'content' | 'contentBody' | 'knob' | 'overlay'>;
};

/**
 * Props for the bottom-sheet content body component.
 * Standard div element props for the inner content wrapper.
 */
export type BottomSheetContentBodyProps = HTMLComponentProps<'div'>;

/**
 * Props for the bottom-sheet footer component.
 * Standard div element props for the footer section of the bottom-sheet.
 */
export type BottomSheetFooterProps = HTMLComponentProps<'div'>;

/**
 * Props for the bottom-sheet knob component.
 * The draggable indicator typically shown at the top of mobile bottom-sheet implementations.
 */
export type BottomSheetKnobProps = HTMLComponentProps<'div'>;

/**
 * Props for the bottom-sheet overlay component.
 * The backdrop that appears behind the bottom-sheet when it's open.
 */
export type BottomSheetOverlayProps = Omit<DialogOverlayProps, 'component'>;

/**
 * Props for the bottom-sheet header component.
 * Container for the bottom-sheet's header content, typically including title and close button.
 */
export type BottomSheetHeaderProps = DialogHeaderProps;

/**
 * Props for the bottom-sheet description component.
 * Accessible description text for the bottom-sheet content.
 */
export type BottomSheetDescriptionProps = Omit<DialogDescriptionProps, 'component'>;

/**
 * Props for the bottom-sheet title component.
 * The main heading/title displayed in the bottom-sheet header.
 */
export type BottomSheetTitleProps = Omit<DialogTitleProps, 'component'>;

/**
 * Props for the bottom-sheet close button component.
 * Button trigger to dismiss and close the bottom-sheet.
 */
export type BottomSheetCloseProps = Omit<DialogCloseProps, 'component'>;

/**
 * Props for the main BottomSheet component.
 * A slide-out panel component with header, content, footer, and overlay.
 *
 * @example
 * ```tsx
 * <BottomSheet
 *   trigger={<button>Open BottomSheet</button>}
 *   title="Settings"
 *   description="Manage your preferences"
 *   footer={<button>Save</button>}
 *   showClose
 * >
 *   BottomSheet content goes here
 * </BottomSheet>
 * ```
 */
export type BottomSheetProps = DialogProps & {
  /**
   * Class names for customizing different parts of the bottom-sheet component.
   */
  classNames?: BottomSheetClassNames;
  /**
   * Props to customize the content area of the bottom-sheet.
   */
  contentProps?: BottomSheetContentProps;
  /**
   * Accessible description text for the bottom-sheet.
   * Useful for screen readers and accessibility.
   */
  description?: React.ReactNode;
  /**
   * Content to display in the bottom-sheet footer section.
   * Typically used for action buttons like Save/Cancel.
   */
  footer?: React.ReactNode;
  /**
   * Whether to show the close button in the bottom-sheet header.
   * Defaults to false.
   */
  showClose?: boolean;
  /**
   * Size variant for the bottom-sheet (sm, md, lg, etc).
   * Controls the width/dimensions of the bottom-sheet panel.
   */
  size?: ThemeSize;
  /**
   * Title text displayed in the bottom-sheet header.
   */
  title?: React.ReactNode;
  /**
   * Element that triggers the opening of the bottom-sheet.
   * Usually a button or interactive element.
   */
  trigger?: React.ReactNode;
  /**
   * Props for the bottom-sheet header component.
   */
  headerProps?: BottomSheetHeaderProps;
  /**
   * Props for the bottom-sheet title component.
   */
  titleProps?: BottomSheetTitleProps;
  /**
   * Props for the bottom-sheet description component.
   */
  descriptionProps?: BottomSheetDescriptionProps;
  /**
   * Props for the bottom-sheet close button component.
   */
  closeProps?: BottomSheetCloseProps;
  /**
   * Props for the bottom-sheet footer component.
   */
  footerProps?: BottomSheetFooterProps;
};
