'use client';

import type { Ref } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import BreadcrumbUI from './BreadcrumbUI';
import type { BreadcrumbItem, BreadcrumbProps } from './types';

const BreadcrumbInner = <T extends BreadcrumbItem>(props: BreadcrumbProps<T>, ref: Ref<HTMLElement>) => {
  const config = useComponentConfig('breadcrumb');

  const mergedProps = {
    ...config,
    ...props
  } as BreadcrumbProps<T>;

  return (
    <BreadcrumbUI<T>
      {...mergedProps}
      ref={ref}
    />
  );
};

BreadcrumbInner.displayName = 'Breadcrumb';

const Breadcrumb = forwardRef(BreadcrumbInner) as <T extends BreadcrumbItem>(
  props: BreadcrumbProps<T> & { ref?: Ref<HTMLElement> }
) => React.ReactElement;

export default Breadcrumb;

import { Ellipsis } from 'lucide-react';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbEllipsisProps } from './types';

const BreadcrumbEllipsis = (props: BreadcrumbEllipsisProps) => {
  const { children, className, ...rest } = props;

  const { ellipsis } = breadcrumbVariants();

  const mergedCls = cn(ellipsis, className);
  return (
    <span
      aria-hidden="true"
      className={mergedCls}
      role="presentation"
      {...rest}
    >
      {children || <Ellipsis />}
      <span className="sr-only">More</span>
    </span>
  );
};

BreadcrumbEllipsis.displayName = 'BreadcrumbEllipsis';

export default BreadcrumbEllipsis;

import { forwardRef } from 'react';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbItemProps } from './types';

const BreadcrumbItem = forwardRef<HTMLLIElement, BreadcrumbItemProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { item } = breadcrumbVariants({ size });

  const mergedCls = cn(item(), className);

  return (
    <li
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

BreadcrumbItem.displayName = 'BreadcrumbItem';

export default BreadcrumbItem;

import { forwardRef } from 'react';
import { Slot } from '@radix-ui/react-slot';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbLinkProps } from './types';

const BreadcrumbLink = forwardRef<HTMLAnchorElement, BreadcrumbLinkProps>((props, ref) => {
  const { asChild, className, ...rest } = props;

  const Comp = asChild ? Slot : 'a';

  const { link } = breadcrumbVariants();

  const mergedCls = cn(link(), className);

  return (
    <Comp
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

BreadcrumbLink.displayName = 'BreadcrumbLink';

export default BreadcrumbLink;

import { forwardRef } from 'react';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbListProps } from './types';

const BreadcrumbList = forwardRef<HTMLOListElement, BreadcrumbListProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { list } = breadcrumbVariants({ size });

  const mergedCls = cn(list(), className);

  return (
    <ol
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

BreadcrumbList.displayName = 'BreadcrumbList';

export default BreadcrumbList;

import { forwardRef } from 'react';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbPageProps } from './types';

const BreadcrumbPage = forwardRef<HTMLSpanElement, BreadcrumbPageProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { page } = breadcrumbVariants({ size });

  const mergedCls = cn(page(), className);

  return (
    <span
      aria-current="page"
      aria-disabled="true"
      className={mergedCls}
      ref={ref}
      role="link"
      {...rest}
    />
  );
});

BreadcrumbPage.displayName = 'BreadcrumbPage';

export default BreadcrumbPage;

import { forwardRef } from 'react';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbRootProps } from './types';

const BreadcrumbRoot = forwardRef<HTMLElement, BreadcrumbRootProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { root } = breadcrumbVariants({ size });

  const mergedCls = cn(root(), className);

  return (
    <nav
      aria-label="breadcrumb"
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

BreadcrumbRoot.displayName = 'BreadcrumbRoot';

export default BreadcrumbRoot;

import { forwardRef } from 'react';
import { ChevronRight } from 'lucide-react';
import { cn } from '@/lib/utils';
import { breadcrumbVariants } from './breadcrumb-variants';
import type { BreadcrumbSeparatorProps } from './types';

const BreadcrumbSeparator = forwardRef<HTMLLIElement, BreadcrumbSeparatorProps>((props, ref) => {
  const { children, className, ...rest } = props;

  const { separator } = breadcrumbVariants();

  const mergedCls = cn(separator(), className);

  return (
    <li
      className={mergedCls}
      ref={ref}
      {...rest}
    >
      {children ?? <ChevronRight />}
    </li>
  );
});

BreadcrumbSeparator.displayName = 'BreadcrumbSeparator';

export default BreadcrumbSeparator;

'use client';

import type { Ref } from 'react';
import { Fragment, forwardRef } from 'react';
import BreadcrumbEllipsis from './BreadcrumbEllipsis';
import BreadcrumbItemContent from './BreadcrumbItem';
import BreadcrumbLink from './BreadcrumbLink';
import BreadcrumbList from './BreadcrumbList';
import BreadcrumbPage from './BreadcrumbPage';
import BreadcrumbRoot from './BreadcrumbRoot';
import BreadcrumbSeparator from './BreadcrumbSeparator';
import type { BreadcrumbItem, BreadcrumbProps } from './types';

type EllipsisProps<T extends BreadcrumbItem> = Pick<
  BreadcrumbProps<T>,
  'className' | 'ellipsisIcon' | 'items' | 'renderEllipsis'
>;

const Ellipsis = <T extends BreadcrumbItem>({ className, ellipsisIcon, items, renderEllipsis }: EllipsisProps<T>) => {
  if (!renderEllipsis)
    return <BreadcrumbEllipsis className={className}>{ellipsisIcon}</BreadcrumbEllipsis>;

  return renderEllipsis(items);
};

function renderBreadcrumbContent<T extends BreadcrumbItem>(item: T, renderItem: BreadcrumbProps<T>['renderItem']) {
  if (renderItem)
    return renderItem(item);

  if (item.href)
    return <BreadcrumbLink {...item}>{item.label}</BreadcrumbLink>;

  return <BreadcrumbPage {...item}>{item.label}</BreadcrumbPage>;
}

const Breadcrumb = <T extends BreadcrumbItem>(props: BreadcrumbProps<T>, ref: Ref<HTMLElement>) => {
  const {
    className,
    classNames,
    ellipsis,
    ellipsisIcon,
    handleItemClick,
    items,
    renderEllipsis,
    renderItem,
    separator,
    size,
    ...rest
  } = props;

  const computedEllipsisRange = getEllipsisRange();

  const itemsFilterEllipsis = getItemsFilterEllipsis();

  const startEllipsisIndex = computedEllipsisRange?.[0];

  const ellipsisItems = computedEllipsisRange ? items.slice(computedEllipsisRange[0], computedEllipsisRange[1]) : [];

  function getItemsFilterEllipsis() {
    if (!computedEllipsisRange)
      return items;

    const [start, end] = computedEllipsisRange;

    return [...items.slice(0, start), ...items.slice(end)];
  }

  function getEllipsisRange() {
    /** when the item count is greater than 4, we will show ellipsis */
    const MIN_ITEM_COUNT_WITH_ELLIPSIS = 5;

    if (!ellipsis || items.length < MIN_ITEM_COUNT_WITH_ELLIPSIS)
      return null;

    if (ellipsis === true) {
      return [1, items.length - 2];
    }

    let [start, end] = ellipsis;

    if (start === 0) {
      start = 1;
    }

    if (end === items.length) {
      end = items.length - 1;
    }

    return [start, end];
  }

  return (
    <BreadcrumbRoot
      className={className || classNames?.root}
      size={size}
      {...rest}
      ref={ref}
    >
      <BreadcrumbList
        className={classNames?.list}
        size={size}
      >
        {itemsFilterEllipsis.map((item, index) => {
          const isEllipsis = startEllipsisIndex && startEllipsisIndex === index;

          const isShowSeparator = index < itemsFilterEllipsis.length - 1;

          return (
            <Fragment key={item.value}>
              {isEllipsis
                ? (
                  <>
                    <Ellipsis<T>
                      className={classNames?.ellipsis}
                      ellipsisIcon={ellipsisIcon}
                      items={ellipsisItems}
                      renderEllipsis={renderEllipsis}
                    />

                    {separator || <BreadcrumbSeparator className={classNames?.separator} />}
                  </>
                )
                : null}

              <BreadcrumbItemContent
                className={classNames?.item}
                size={size}
                onClick={() => handleItemClick?.(item)}
              >
                {item.leading}
                {renderBreadcrumbContent(item, renderItem)}
                {item.trailing}
              </BreadcrumbItemContent>

              {isShowSeparator ? separator || <BreadcrumbSeparator className={classNames?.separator} /> : null}
            </Fragment>
          );
        })}
      </BreadcrumbList>
    </BreadcrumbRoot>
  );
};

Breadcrumb.displayName = 'BreadcrumbUI';

const BreadcrumbUI = forwardRef(Breadcrumb) as <T extends BreadcrumbItem>(
  props: BreadcrumbProps<T> & { ref?: Ref<HTMLElement> }
) => React.ReactElement;

export default BreadcrumbUI;

import { tv } from 'tailwind-variants';

export const breadcrumbVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    ellipsis: 'flex items-center justify-center',
    item: 'inline-flex items-center list-none',
    link: 'hover:text-foreground transition-colors-200 rounded-sm focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-offset-background focus-visible:ring-primary',
    list: 'flex flex-wrap items-center my-0 break-words text-muted-foreground',
    page: 'font-normal text-foreground',
    root: '',
    separator: 'text-muted-foreground flex-shrink-0 list-none'
  },
  variants: {
    size: {
      '2xl': {
        item: 'gap-3.5',
        list: 'gap-4.5',
        root: 'text-xl'
      },
      'lg': {
        item: 'gap-2.5',
        list: 'gap-3.5',
        root: 'text-base'
      },
      'md': {
        item: 'gap-2',
        list: 'gap-3',
        root: 'text-sm'
      },
      'sm': {
        item: 'gap-1.75',
        list: 'gap-2.5',
        root: 'text-xs'
      },
      'xl': {
        item: 'gap-3',
        list: 'gap-4',
        root: 'text-lg'
      },
      'xs': {
        item: 'gap-1.5',
        list: 'gap-2',
        root: 'text-2xs'
      }
    }
  }
});

export type BreadcrumbSlots = keyof typeof breadcrumbVariants.slots;

export { default as Breadcrumb } from './Breadcrumb';
export { default as BreadcrumbEllipsis } from './BreadcrumbEllipsis';
export { default as BreadcrumbItemContent } from './BreadcrumbItem';
export { default as BreadcrumbLink } from './BreadcrumbLink';
export { default as BreadcrumbList } from './BreadcrumbList';
export { default as BreadcrumbPage } from './BreadcrumbPage';
export { default as BreadcrumbRoot } from './BreadcrumbRoot';
export { default as BreadcrumbSeparator } from './BreadcrumbSeparator';

export * from './types';

import type { HTMLComponentProps, ClassValue, SlotProps } from '@/types/shared';
import type { BreadcrumbSlots } from './breadcrumb-variants';

/**
 * Props for the breadcrumb ellipsis component.
 * Displays a visual indicator (usually "...") when breadcrumb items are hidden.
 */
export type BreadcrumbEllipsisProps = HTMLComponentProps<'span'>;

/**
 * Props for the breadcrumb item component.
 * Represents a single breadcrumb navigation item.
 */
export type BreadcrumbItemProps = HTMLComponentProps<'li'>;

/**
 * Props for the breadcrumb link component.
 * A clickable link in the breadcrumb trail.
 */
export type BreadcrumbLinkProps = HTMLComponentProps<'a'> & {
  /**
   * Whether to render as a child component using Radix UI's asChild pattern.
   */
  asChild?: boolean;
};

/**
 * Props for the breadcrumb list component.
 * Container for all breadcrumb items.
 */
export type BreadcrumbListProps = HTMLComponentProps<'ol'>;

/**
 * Props for the breadcrumb page component.
 * Displays the current/active page in the breadcrumb trail.
 */
export type BreadcrumbPageProps = HTMLComponentProps<'span'>;

/**
 * Props for the breadcrumb root component.
 * Main navigation element for breadcrumb navigation.
 */
export type BreadcrumbRootProps = HTMLComponentProps<'nav'>;

/**
 * Props for the breadcrumb separator component.
 * Displays a visual divider between breadcrumb items (e.g., "/", ">").
 */
export type BreadcrumbSeparatorProps = HTMLComponentProps<'li'>;

/**
 * Data structure for a single breadcrumb item.
 * Use this type to define items passed to the Breadcrumb component.
 */
export interface BreadcrumbItem extends BreadcrumbLinkProps, SlotProps {
  /**
   * Custom class names for styling this specific breadcrumb item.
   */
  className?: ClassValue;
  /**
   * Display text or content for the breadcrumb item.
   */
  label: React.ReactNode;
  /**
   * Unique identifier for the breadcrumb item.
   */
  value: string;
}

/**
 * Class names for different slots in the breadcrumb component.
 * Allows customizing styles for specific parts of the breadcrumb.
 */
export type BreadcrumbUi = Partial<Record<BreadcrumbSlots, ClassValue>>;

/**
 * Props for the main Breadcrumb component.
 * Manages rendering and navigation of breadcrumb items with optional ellipsis support.
 *
 * @template T - Type of breadcrumb item data, extends BreadcrumbItem
 *
 * @example
 * ```jsx
 * <Breadcrumb
 *   items={[
 *     { label: 'Home', value: 'home' },
 *     { label: 'Products', value: 'products' },
 *     { label: 'Electronics', value: 'electronics' }
 *   ]}
 *   ellipsis={[1, 2]}
 *   separator="/"
 *   handleItemClick={(item) => navigate(item.value)}
 * />
 * ```
 */
export type BreadcrumbProps<T extends BreadcrumbItem> = BreadcrumbRootProps & {
  /**
   * Class names for customizing breadcrumb slots (excluding link and page slots).
   */
  classNames?: Omit<BreadcrumbUi, 'link' | 'page'>;
  /**
   * Configuration for displaying ellipsis when items are hidden.
   * Can be true to auto-show ellipsis when items exceed 4, [start, end] for specific range, or null to disable.
   * When true, ellipsis appears when the item count is greater than 4.
   * [start, end] specifies the range indices where ellipsis should be displayed.
   */
  ellipsis?: true | [number, number] | null;
  /**
   * Custom icon or element to display for the ellipsis.
   */
  ellipsisIcon?: React.ReactNode;
  /**
   * Callback function invoked when a breadcrumb item is clicked.
   */
  handleItemClick?: (item: T) => void;
  /**
   * Array of breadcrumb items to render.
   */
  items: T[];
  /**
   * Custom render function for displaying the ellipsis element.
   */
  renderEllipsis?: (items: T[]) => React.ReactNode;
  /**
   * Custom render function for displaying individual breadcrumb items.
   */
  renderItem?: (item: T) => React.ReactNode;
  /**
   * Visual separator displayed between breadcrumb items (e.g., "/", ">", "|").
   */
  separator?: React.ReactNode;
};
