  const { className, size, ...rest } = props;

  const { footer } = cardVariants({ size });

  const mergedCls = cn(footer(), className);

  return (
    <div
      className={mergedCls}
      data-slot="card-footer"
      {...rest}
      ref={ref}
    />
  );
});

CardFooter.displayName = 'CardFooter';

