import React from 'react';
import { cn } from '@/lib/utils';
import { cardVariants } from './card-variants';
import type { CardTitleRootProps } from './types';

export const CardTitleRoot = React.forwardRef<HTMLDivElement, CardTitleRootProps>((props, ref) => {
  const { children, className, leading, size, trailing, ...rest } = props;

  const { titleRoot } = cardVariants({ size });

  const mergedCls = cn(titleRoot(), className);

  return (
    <div
      className={mergedCls}
      data-slot="card-title-root"
      {...rest}
      ref={ref}
    >
      {leading}
      {children}
      {trailing}
    </div>
  );
});

CardTitleRoot.displayName = 'CardTitleRoot';

import React, { isValidElement } from 'react';
import { If } from '../if';
import { CardContent } from './CardContent';
import { CardFooter } from './CardFooter';
import { CardHeader } from './CardHeader';
import { CardRoot } from './CardRoot';
import { CardTitle } from './CardTitle';
import { CardTitleRoot } from './CardTitleRoot';
import type { CardProps } from './types';

export const CardUI = React.forwardRef<HTMLDivElement, CardProps>((props, ref) => {
  const {
    children,
    className,
    classNames,
    extra,
    scrollable,
    footer,
    header,
    size,
    split,
    title,
    titleLeading,
    titleRoot,
    titleTrailing,
    headerProps,
    titleRootProps,
    titleProps,
    contentProps,
    footerProps,
    ...rest
  } = props;

  const showHeader = Boolean(header || title || extra);

  return (
    <CardRoot
      className={className || classNames?.root}
      size={size}
      split={split}
      {...rest}
      ref={ref}
    >
      <If condition={showHeader}>
        <CardHeader
          className={classNames?.header}
          size={size}
          {...headerProps}
        >
          <If
            condition={!header}
            fallback={header}
          >
            <If
              condition={!titleRoot}
              fallback={titleRoot}
            >
              <CardTitleRoot
                className={classNames?.titleRoot}
                size={size}
                {...titleRootProps}
              >
                {titleLeading}

                <If
                  condition={!isValidElement(title)}
                  fallback={title}
                >
                  <CardTitle
                    className={classNames?.title}
                    size={size}
                    {...titleProps}
                  >
                    {title}
                  </CardTitle>
                </If>

                {titleTrailing}
              </CardTitleRoot>
            </If>

            {extra}
          </If>
        </CardHeader>
      </If>

      <CardContent
        className={classNames?.content}
        scrollable={scrollable}
        size={size}
        {...contentProps}
      >
        {children}
      </CardContent>

      <If
        condition={!isValidElement(footer) && Boolean(footer)}
        fallback={footer}
      >
        <CardFooter
          className={classNames?.footer}
          size={size}
          {...footerProps}
        >
          {footer}
        </CardFooter>
      </If>
    </CardRoot>
  );
});

CardUI.displayName = 'CardUI';

import { tv } from 'tailwind-variants';

export const cardVariants = tv({
  defaultVariants: {
    scrollable: true,
    split: false,
    size: 'md'
  },
  slots: {
    content: 'flex-grow overflow-auto',
    footer: 'flex items-center justify-between',
    header: 'flex items-center justify-between',
    root: 'flex flex-col items-stretch rounded-md border bg-card text-card-foreground shadow-sm',
    title: 'font-semibold tracking-tight',
    titleRoot: 'flex items-center'
  },
  variants: {
    size: {
      '2xl': {
        content: 'px-7 py-6',
        footer: 'px-7 py-6',
        header: 'px-7 py-6',
        root: 'text-lg',
        title: 'text-xl',
        titleRoot: 'gap-3.5'
      },
      'lg': {
        content: 'px-5 py-4',
        footer: 'px-5 py-5',
        header: 'px-5 py-4',
        root: 'text-base',
        title: 'text-lg',
        titleRoot: 'gap-2.5'
      },
      'md': {
        content: 'px-4 py-3',
        footer: 'px-4 py-3',
        header: 'px-4 py-3',
        root: 'text-sm',
        title: 'text-base',
        titleRoot: 'gap-2'
      },
      'sm': {
        content: 'px-3 py-2',
        footer: 'px-3 py-2',
        header: 'px-3 py-2',
        root: 'text-xs',
        title: 'text-sm',
        titleRoot: 'gap-1.75'
      },
      'xl': {
        content: 'px-6 py-5',
        footer: 'px-6 py-6',
        header: 'px-6 py-5',
        root: 'text-base',
        title: 'text-lg',
        titleRoot: 'gap-3'
      },
      'xs': {
        content: 'px-2 py-1.5',
        footer: 'px-2 py-1.5',
        header: 'px-2 py-1.5',
        root: 'text-2xs',
        title: 'text-xs',
        titleRoot: 'gap-1.5'
      }
    },
    scrollable: {
      true: {
        content: 'overflow-auto'
      }
    },
    split: {
      true: {
        root: 'divide-y divide-border'
      }
    }
  }
});

export type CardSlots = keyof typeof cardVariants.slots;

export { default as Card } from './Card';
export * from './CardContent';
export * from './CardFooter';
export * from './CardHeader';
export * from './CardRoot';
export * from './CardTitle';
export * from './CardTitleRoot';

export * from './types';

import type { ReactNode } from 'react';
import type { ClassValue, WithClassName, ThemeSize } from '@/types/shared';
import type { CardSlots } from './card-variants';

/**
 * Props for the Card root component.
 * Main container for card content with size and layout options.
 */
export interface CardRootProps
  extends WithClassName,
  Omit<React.HTMLAttributes<HTMLDivElement>, 'className' | 'title'> {
  /**
   * The size variant of the card affecting padding and spacing.
   */
  size?: ThemeSize;
  /**
   * If true, splits the card layout into distinct sections visually.
   */
  split?: boolean;
}

/**
 * Props for the Card header component.
 * Container for the card's top section, typically containing title and extra content.
 */
export interface CardHeaderProps
  extends WithClassName,
  Omit<React.HTMLAttributes<HTMLDivElement>, 'className' | 'title'> {
  /**
   * The size variant of the header affecting padding and typography.
   */
  size?: ThemeSize;
}

/**
 * Props for the CardTitle root wrapper component.
 * Container for the title and optional leading/trailing icons or content.
 */
export interface CardTitleRootProps
  extends WithClassName,
  Omit<React.HTMLAttributes<HTMLDivElement>, 'className' | 'title'> {
  /**
   * Content or icon displayed before the title text.
   */
  leading?: React.ReactNode;
  /**
   * The size variant of the title container affecting spacing.
   */
  size?: ThemeSize;
  /**
   * Content or icon displayed after the title text.
   */
  trailing?: React.ReactNode;
}

/**
 * Props for the Card title component.
 * Displays the main heading or title text of the card.
 */
export interface CardTitleProps
  extends WithClassName,
  Omit<React.HTMLAttributes<HTMLDivElement>, 'className' | 'title'> {
  /**
   * The size variant of the title text.
   */
  size?: ThemeSize;
}

/**
 * Props for the Card footer component.
 * Container for the card's bottom section, typically containing action buttons.
 */
export interface CardFooterProps
  extends WithClassName,
  Omit<React.HTMLAttributes<HTMLDivElement>, 'className' | 'title'> {
  /**
   * The size variant of the footer affecting padding and spacing.
   */
  size?: ThemeSize;
}

/**
 * Props for the Card content component.
 * Main content area of the card.
 */
export interface CardContentProps
  extends WithClassName,
  Omit<React.HTMLAttributes<HTMLDivElement>, 'className' | 'title'> {
/**
 * If true, the card content will be scrollable.
 * When true, adds overflow-auto to the content wrapper.
 * @default true
 */
  scrollable?: boolean;
  /**
   * The size variant of the content affecting padding.
   */
  size?: ThemeSize;
}

/**
 * Class names for different slots in the card component.
 * Allows customizing styles for specific parts of the card.
 */
export type CardUi = Partial<Record<CardSlots, ClassValue>>;

/**
 * Props for the main Card component.
 * A flexible container component that combines header, title, content, and footer sections.
 *
 * @example
 * ```jsx
 * <Card
 *   size="md"
 *   split={false}
 *   flexHeight={true}
 *   title="Card Title"
 *   titleLeading={<IconComponent />}
 *   header={<CustomHeader />}
 *   footer={<Button>Action</Button>}
 * >
 *   Card content goes here
 * </Card>
 * ```
 */
export interface CardProps extends CardRootProps {
  /**
   * Class names for customizing different parts of the card.
   */
  classNames?: CardUi;
  /**
   * Extra content displayed in the header area, typically alongside the title.
   */
  extra?: ReactNode;
  /**
   * If true, the card content will be scrollable.
   * When true, adds overflow-auto to the content wrapper.
   * @default true
   */
  scrollable?: boolean;
  /**
   * Custom footer content rendered at the bottom of the card.
   */
  footer?: ReactNode;
  /**
   * Custom header content rendered at the top of the card.
   * Overrides default header if provided.
   */
  header?: ReactNode;
  /**
   * Main title text displayed in the card header.
   */
  title?: ReactNode;
  /**
   * Content displayed before the title in the title section.
   */
  titleLeading?: ReactNode;
  /**
   * Custom root wrapper for the title and optional leading/trailing content.
   */
  titleRoot?: ReactNode;
  /**
   * Content displayed after the title in the title section.
   */
  titleTrailing?: ReactNode;
  /**
   * Props for the card header component.
   */
  headerProps?: CardHeaderProps;
  /**
   * Props for the card title root component.
   */
  titleRootProps?: CardTitleRootProps;
  /**
   * Props for the card title component.
   */
  titleProps?: CardTitleProps;
  /**
   * Props for the card content component.
   */
  contentProps?: CardContentProps;
  /**
   * Props for the card footer component.
   */
  footerProps?: CardFooterProps;
}
