'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import CollapsibleUI from './CollapsibleUI';
import type { CollapsibleProps } from './types';

const Collapsible = forwardRef<ComponentRef<typeof CollapsibleUI>, CollapsibleProps>((props, ref) => {
  const config = useComponentConfig('collapsible');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <CollapsibleUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Collapsible.displayName = 'Collapsible';

export default Collapsible;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Content } from '@radix-ui/react-collapsible';
import { cn } from '@/lib/utils';
import { collapsibleVariants } from './collapsible-variants';
import type { CollapsibleContentProps } from './types';

const CollapsibleContent = forwardRef<ComponentRef<typeof Content>, CollapsibleContentProps>((props, ref) => {
  const { className, ...rest } = props;

  const { content } = collapsibleVariants();

  const mergedCls = cn(content(), className);

  return (
    <Content
      className={mergedCls}
      data-slot="collapsible-content"
      ref={ref}
      {...rest}
    />
  );
});

CollapsibleContent.displayName = 'CollapsibleContent';

export default CollapsibleContent;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root } from '@radix-ui/react-collapsible';
import { cn } from '@/lib/utils';
import { collapsibleVariants } from './collapsible-variants';
import type { CollapsibleRootProps } from './types';

const CollapsibleRoot = forwardRef<ComponentRef<typeof Root>, CollapsibleRootProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { root } = collapsibleVariants({ size });

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      data-slot="collapsible-root"
      ref={ref}
      {...rest}
    />
  );
});

CollapsibleRoot.displayName = 'CollapsibleRoot';

export default CollapsibleRoot;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import CollapsibleContent from './CollapsibleContent';
import CollapsibleRoot from './CollapsibleRoot';
import type { CollapsibleProps } from './types';

const CollapsibleUI = forwardRef<ComponentRef<typeof CollapsibleRoot>, CollapsibleProps>((props, ref) => {
  const { children, className, classNames, content, forceMountContent, ...rest } = props;

  return (
    <CollapsibleRoot
      className={className || classNames?.root}
      ref={ref}
      {...rest}
    >
      {children}

      <CollapsibleContent
        className={classNames?.content}
        forceMount={forceMountContent}
      >
        {content}
      </CollapsibleContent>
    </CollapsibleRoot>
  );
});

CollapsibleUI.displayName = 'CollapsibleUI';

export default CollapsibleUI;

import { tv } from 'tailwind-variants';

export const collapsibleVariants = tv({
  slots: {
    content: [
      'overflow-hidden transition will-change-auto',
      'data-[state=closed]:animate-collapsible-up data-[state=open]:animate-collapsible-down'
    ],
    root: '',
    trigger: ''
  },
  variants: {
    size: {
      '2xl': {
        root: 'text-xl'
      },
      'lg': {
        root: 'text-base'
      },
      'md': {
        root: 'text-sm'
      },
      'sm': {
        root: 'text-xs'
      },
      'xl': {
        root: 'text-lg'
      },
      'xs': {
        root: 'text-2xs'
      }
    }
  }
});

export type CollapsibleSlots = keyof typeof collapsibleVariants.slots;

export { CollapsibleTrigger } from '@radix-ui/react-collapsible';
export { default as Collapsible } from './Collapsible';
export { default as CollapsibleContent } from './CollapsibleContent';
export { default as CollapsibleRoot } from './CollapsibleRoot';
export * from './types';

import type {
  CollapsibleContentProps as _CollapsibleContentProps,
  CollapsibleProps as _CollapsibleRootProps
} from '@radix-ui/react-collapsible';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { CollapsibleSlots } from './collapsible-variants';

/**
 * Props for the collapsible root component.
 * Provides state management and interaction handling for the collapsible element.
 */
export type CollapsibleRootProps = StyledComponentProps<Omit<_CollapsibleRootProps, 'content'>>;

/**
 * Props for the collapsible content component.
 * The expandable/collapsible content area that shows or hides based on state.
 */
export type CollapsibleContentProps = StyledComponentProps<_CollapsibleContentProps>;

/**
 * Class names for different slots in the collapsible component.
 * Allows customizing styles for specific parts of the collapsible.
 */
export type CollapsibleClassNames = Partial<Record<CollapsibleSlots, ClassValue>>;

/**
 * Props for the main Collapsible component.
 * Controls the visibility of content with animation support.
 *
 * @example
 * ```tsx
 * <Collapsible
 *   content={<div>Hidden content that toggles</div>}
 *   forceMountContent
 *   defaultOpen={true}
 * />
 * ```
 */
export interface CollapsibleProps extends CollapsibleRootProps {
  /**
   * Custom class names for different collapsible UI slots.
   * Allows styling the root and content areas independently.
   */
  classNames?: CollapsibleClassNames;

  /**
   * The content to display when the collapsible is expanded.
   * Can be text, HTML elements, or any React node.
   */
  content?: React.ReactNode;

  /**
   * Forces the content to always be mounted in the DOM.
   * Enables animation control and prevents layout shifts.
   */
  forceMountContent?: true;
}
