'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import DividerUI from './DividerUI';
import type { DividerProps } from './types';

const Divider = forwardRef<ComponentRef<typeof DividerUI>, DividerProps>((props, ref) => {
  const config = useComponentConfig('divider');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <DividerUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Divider.displayName = 'Divider';

export default Divider;

import React from 'react';
import { cn } from '@/lib/utils';
import { dividerVariants } from './divider-variants';
import type { DividerLabelProps } from './types';

const DividerLabel = React.forwardRef<HTMLSpanElement, DividerLabelProps>((props, ref) => {
  const { align, className, orientation, size, ...rest } = props;

  const { label } = dividerVariants({ align, orientation, size });

  const mergedCls = cn(label(), className);

  return (
    <span
      className={mergedCls}
      {...rest}
      ref={ref}
    />
  );
});

DividerLabel.displayName = 'DividerLabel';

export default DividerLabel;

import React from 'react';
import { Root } from '@radix-ui/react-separator';
import { cn } from '@/lib/utils';
import { dividerVariants } from './divider-variants';
import type { DividerRootProps } from './types';

const DividerRoot = React.forwardRef<HTMLDivElement, DividerRootProps>((props, ref) => {
  const { border, className, orientation, ...rest } = props;

  const { root } = dividerVariants({ border, orientation });

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      {...rest}
      ref={ref}
    />
  );
});

DividerRoot.displayName = 'DividerRoot';

export default DividerRoot;

import React from 'react';
import DividerLabel from './DividerLabel';
import DividerRoot from './DividerRoot';
import type { DividerProps } from './types';

const DividerUI = React.forwardRef<HTMLDivElement, DividerProps>((props, ref) => {
  const { align, children, className, classNames, leading, orientation, size, trailing, ...rest } = props;

  return (
    <DividerRoot
      className={className || classNames?.root}
      orientation={orientation}
      {...rest}
      ref={ref}
    >
      {leading}

      {Boolean(children) && (
        <DividerLabel
          align={align}
          className={classNames?.label}
          orientation={orientation}
          size={size}
          {...rest}
        >
          {children}
        </DividerLabel>
      )}

      {trailing}
    </DividerRoot>
  );
});

DividerUI.displayName = 'DividerUI';

export default DividerUI;

import { tv } from 'tailwind-variants';
import type { VariantProps } from 'tailwind-variants';

export const dividerVariants = tv({
  compoundVariants: [
    {
      class: {
        label: 'px-0.75 py-1.5'
      },
      orientation: 'vertical',
      size: 'xs'
    },
    {
      class: {
        label: 'px-1.5 py-0.75'
      },
      orientation: 'horizontal',
      size: 'xs'
    },
    {
      class: {
        label: 'px-0.875 py-1.75'
      },
      orientation: 'vertical',
      size: 'sm'
    },
    {
      class: {
        label: 'px-1.75 py-0.875'
      },
      orientation: 'horizontal',
      size: 'sm'
    },
    {
      class: {
        label: 'px-1 py-2'
      },
      orientation: 'vertical',
      size: 'md'
    },
    {
      class: {
        label: 'px-2 py-1'
      },
      orientation: 'horizontal',
      size: 'md'
    },
    {
      class: {
        label: 'px-1.125 py-2.25'
      },
      orientation: 'vertical',
      size: 'lg'
    },
    {
      class: {
        label: 'px-2.25 py-1.125'
      },
      orientation: 'horizontal',
      size: 'lg'
    },
    {
      class: {
        label: 'px-1.25 py-2.5'
      },
      orientation: 'vertical',
      size: 'xl'
    },
    {
      class: {
        label: 'px-2.5 py-1.25'
      },
      orientation: 'horizontal',
      size: 'xl'
    },
    {
      class: {
        label: 'px-1.5 py-3'
      },
      orientation: 'vertical',
      size: '2xl'
    },
    {
      class: {
        label: 'px-3 py-1.5'
      },
      orientation: 'horizontal',
      size: '2xl'
    }
  ],
  defaultVariants: {
    align: 'center',
    border: 'solid',
    orientation: 'horizontal',
    size: 'md'
  },
  slots: {
    label: `absolute top-1/2 -translate-x-1/2 -translate-y-1/2 flex justify-center items-center text-muted-foreground bg-background`,
    root: `relative shrink-0 border-border`
  },
  variants: {
    align: {
      center: {
        label: 'left-1/2'
      },
      end: {
        label: 'left-15/16 sm:left-7/8 md:left-9/10 lg:left-11/12'
      },
      start: {
        label: 'left-1/16 sm:left-1/8 md:left-1/10 lg:left-1/12'
      }
    },
    border: {
      dashed: {
        root: 'border-dashed'
      },
      dotted: {
        root: 'border-dotted'
      },
      solid: {
        root: 'border-solid'
      }
    },
    orientation: {
      horizontal: {
        label: 'h-[1px]',
        root: 'w-full border-t'
      },
      vertical: {
        label: 'w-[1px]',
        root: 'h-full border-l'
      }
    },
    size: {
      '2xl': {
        label: 'text-2xl'
      },
      'lg': {
        label: 'text-base'
      },
      'md': {
        label: 'text-sm'
      },
      'sm': {
        label: 'text-xs'
      },
      'xl': {
        label: 'text-lg'
      },
      'xs': {
        label: 'text-2xs'
      }
    }
  }
});

type DividerVariants = VariantProps<typeof dividerVariants>;

export type DividerBorder = NonNullable<DividerVariants['border']>;

export type DividerSlots = keyof typeof dividerVariants.slots;

export { default as Divider } from './Divider';
export { default as DividerLabel } from './DividerLabel';
export { default as DividerRoot } from './DividerRoot';

export * from './types';

import type { SeparatorProps as _SeparatorProps } from '@radix-ui/react-separator';
import type { StyledComponentProps, ClassValue, SlotProps, ThemeAlign, ThemeOrientation } from '@/types/shared';
import type { DividerBorder, DividerSlots } from './divider-variants';

/**
 * Props for the divider root component.
 * Extends the base separator component from Radix UI with styling capabilities.
 */
export interface DividerRootProps extends StyledComponentProps<_SeparatorProps> {
  /**
   * Style variant for the divider border.
   * Controls the visual appearance and styling of the divider line.
   */
  border?: DividerBorder;
}

export type { DividerBorder };

/**
 * Props for the divider label component.
 * Used to create labeled dividers with text or content.
 */
export interface DividerLabelProps extends StyledComponentProps<React.ComponentProps<'span'>> {
  /**
   * Horizontal alignment of the label content.
   * Determines if the label is positioned left, center, or right.
   */
  align?: ThemeAlign;
  /**
   * Orientation of the divider (horizontal or vertical).
   * Controls the direction of the separator line.
   */
  orientation?: ThemeOrientation;
}

/**
 * Class names for different slots in the divider component.
 * Allows customizing styles for specific parts of the divider.
 */
export type DividerUi = Partial<Record<DividerSlots, ClassValue>>;

/**
 * Props for the main Divider component.
 * Combines root and label properties with slot customization.
 *
 * @example
 * ```tsx
 * <Divider
 *   border="solid"
 *   orientation="horizontal"
 *   align="center"
 *   classNames={{ root: 'my-4' }}
 * />
 * ```
 */
export interface DividerProps
  extends DividerRootProps,
  Pick<DividerLabelProps, 'align' | 'orientation' | 'size'>,
  SlotProps {
  /**
   * Class names for customizing different parts of the divider component.
   */
  classNames?: DividerUi;
}
