'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Content } from 'vaul';
import { useComponentConfig } from '../config-provider/context';
import DrawerUI from './DrawerUI';
import type { DrawerProps } from './types';

const Drawer = forwardRef<ComponentRef<typeof Content>, DrawerProps>((props, ref) => {
  const config = useComponentConfig('drawer');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <DrawerUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Drawer.displayName = 'Drawer';

export default Drawer;

import { Drawer } from 'vaul';
import { DialogClose } from '../dialog';
import type { DrawerCloseProps } from './types';

const DrawerClose = (props: DrawerCloseProps) => {
  return (
    <DialogClose
      component={Drawer.Close}
      {...props}
    />
  );
};

export default DrawerClose;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Content, Portal } from 'vaul';
import { cn } from '@/lib/utils';
import DrawerContentBody from './DrawerContentBody';
import DrawerKnob from './DrawerKnob';
import DrawerOverlay from './DrawerOverlay';
import { drawerVariants } from './drawer-variants';
import type { DrawerContentProps } from './types';

const DrawerContent = forwardRef<ComponentRef<typeof Content>, DrawerContentProps>((props, ref) => {
  const { children, className, classNames, size, ...rest } = props;

  const { content } = drawerVariants({ size });

  const mergedCls = cn(content(), className, classNames?.content);

  return (
    <Portal>
      <DrawerOverlay className={classNames?.overlay} />

      <Content
        className={mergedCls}
        data-slot="drawer-content"
        ref={ref}
        {...rest}
      >
        <DrawerContentBody className={classNames?.contentBody}>
          <DrawerKnob className={classNames?.knob} />
          {children}
        </DrawerContentBody>
      </Content>
    </Portal>
  );
});

DrawerContent.displayName = 'DrawerContent';

export default DrawerContent;

import { cn } from '@/lib/utils';
import { drawerVariants } from './drawer-variants';
import type { DrawerContentBodyProps } from './types';

const DrawerContentBody = (props: DrawerContentBodyProps) => {
  const { className, size, ...rest } = props;

  const { contentBody } = drawerVariants({ size });

  const mergedCls = cn(contentBody(), className);
  return (
    <div
      className={mergedCls}
      {...rest}
    />
  );
};

export default DrawerContentBody;

import { Drawer } from 'vaul';
import { DialogDescription } from '../dialog';
import type { DrawerDescriptionProps } from './types';

const DrawerDescription = (props: DrawerDescriptionProps) => {
  return (
    <DialogDescription
      component={Drawer.Description}
      {...props}
    />
  );
};

export default DrawerDescription;

import { cn } from '@/lib/utils';
import { drawerVariants } from './drawer-variants';
import type { DrawerKnobProps } from './types';

const DrawerKnob = (props: DrawerKnobProps) => {
  const { className, size, ...rest } = props;

  const { knob } = drawerVariants({ size });

  const mergedCls = cn(knob(), className);

  return (
    <div
      className={mergedCls}
      {...rest}
    />
  );
};

export default DrawerKnob;

import { Overlay as _Overlay } from 'vaul';
import { DialogOverlay } from '../dialog';
import type { DrawerOverlayProps } from './types';

const DrawerOverlay = (props: DrawerOverlayProps) => {
  return (
    <DialogOverlay
      component={_Overlay}
      {...props}
    />
  );
};

export default DrawerOverlay;

import { Drawer } from 'vaul';
import { DialogTitle } from '../dialog';
import type { DrawerTitleProps } from './types';

const DrawerTitle = (props: DrawerTitleProps) => {
  return (
    <DialogTitle
      component={Drawer.Title}
      {...props}
    />
  );
};

export default DrawerTitle;

import { type ComponentRef, forwardRef } from 'react';
import type { Content } from 'vaul';
import { Root as _Root } from 'vaul';
import { DialogFooter, DialogHeader, DialogTrigger } from '../dialog';
import DrawerClose from './DrawerClose';
import DrawerContent from './DrawerContent';
import DrawerDescription from './DrawerDescription';
import DrawerTitle from './DrawerTitle';
import type { DrawerProps } from './types';

const DrawerUI = forwardRef<ComponentRef<typeof Content>, DrawerProps>((props, ref) => {
  const {
    children,
    classNames,
    contentProps,
    description,
    footer,
    shouldScaleBackground = true,
    showClose,
    size,
    title,
    trigger
  } = props;

  return (
    <_Root
      shouldScaleBackground={shouldScaleBackground}
      {...props}
    >
      <DialogTrigger asChild>{trigger}</DialogTrigger>

      <DrawerContent
        classNames={classNames}
        ref={ref}
        {...contentProps}
      >
        <DialogHeader
          className={classNames?.header}
          size={size}
        >
          <DrawerTitle
            className={classNames?.title}
            size={size}
          >
            {title}
          </DrawerTitle>

          <DrawerDescription
            className={classNames?.description}
            size={size}
          >
            {description}
          </DrawerDescription>
        </DialogHeader>

        {showClose
          ? (
            <DrawerClose
              className={classNames?.close}
              size={size}
            />
          )
          : null}

        {children}

        {footer
          ? (
            <DialogFooter
              className={classNames?.footer || '!flex-col-reverse'}
              size={size}
            >
              {footer}
            </DialogFooter>
          )
          : null}
      </DrawerContent>
    </_Root>
  );
});

DrawerUI.displayName = 'DrawerUI';

export default DrawerUI;

import { tv } from 'tailwind-variants';
import { dialogVariants } from '../dialog/dialog-variants';

export const drawerVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    ...dialogVariants.slots,
    content: `fixed inset-x-0 bottom-0 z-50 border bg-background`,
    contentBody: `flex h-auto flex-col`,
    knob: `mx-auto shrink-0 cursor-grab active:cursor-grabbing rounded-full bg-muted`
  },
  variants: {
    size: {
      '2xl': {
        content: `mt-30 rounded-t-[16px]`,
        contentBody: `gap-y-6 max-w-3xl px-7 py-6 text-xl`,
        knob: 'mt-7 h-3.5 w-40'
      },
      'lg': {
        content: `mt-26 rounded-t-[12px]`,
        contentBody: `gap-y-4 max-w-xl px-5 py-4 text-base`,
        knob: 'mt-5 h-2.5 w-30'
      },
      'md': {
        content: `mt-24 rounded-t-[10px]`,
        contentBody: `gap-y-3 max-w-lg px-4 py-3 text-sm`,
        knob: 'mt-4 h-2 w-25'
      },
      'sm': {
        content: `mt-22 rounded-t-[8px]`,
        contentBody: `gap-y-2 max-w-md px-3 py-2 text-xs`,
        knob: 'mt-3.5 h-1.75 w-22'
      },
      'xl': {
        content: `mt-28 rounded-t-[14px]`,
        contentBody: `gap-y-5 max-w-2xl px-6 py-5 text-lg`,
        knob: 'mt-6 h-3 w-35'
      },
      'xs': {
        content: `mt-20 rounded-t-[6px]`,
        contentBody: `gap-y-1.5 max-w-md px-2 py-1.5 text-2xs`,
        knob: 'mt-3 h-1.5 w-20'
      }
    }
  }
});

export type DrawerSlots = keyof typeof drawerVariants.slots;

export { Root as DrawerRoot } from 'vaul';

export { DialogFooter as DrawerFooter, DialogHeader as DrawerHeader, DialogTrigger as DrawerTrigger } from '../dialog';

export { default as Drawer } from './Drawer';

export { default as DrawerClose } from './DrawerClose';
export { default as DrawerContent } from './DrawerContent';
export { default as DrawerContentBody } from './DrawerContentBody';
export { default as DrawerDescription } from './DrawerDescription';
export { default as DrawerKnob } from './DrawerKnob';
export { default as DrawerOverlay } from './DrawerOverlay';
export { default as DrawerTitle } from './DrawerTitle';

export * from './types';

import type { ContentProps, DialogProps } from 'vaul';
import type { HTMLComponentProps, StyledComponentProps, ClassValue, ThemeSize } from '@/types/shared';
import type {
  DialogCloseProps,
  DialogDescriptionProps,
  DialogHeaderProps,
  DialogOverlayProps,
  DialogTitleProps
} from '../dialog';
import type { DialogSlots } from '../dialog/dialog-variants';
import type { DrawerSlots } from './drawer-variants';

/**
 * Class names for different slots in the drawer component.
 * Allows customizing styles for drawer-specific and dialog-shared parts.
 */
export type DrawerClassNames = Partial<Record<DrawerSlots | DialogSlots, ClassValue>>;

/**
 * Props for the drawer content component.
 * Wraps the main content area of the drawer with styling and slot customization.
 *
 * @example
 * ```tsx
 * <Drawer.Content
 *   contentProps={{
 *     classNames: {
 *       content: 'custom-content-class',
 *       knob: 'custom-knob-class'
 *     }
 *   }}
 * >
 *   Content here
 * </Drawer.Content>
 * ```
 */
export type DrawerContentProps = StyledComponentProps<ContentProps> & {
  /**
   * Class names for customizing specific parts of the drawer content area.
   * Includes content, contentBody, knob, and overlay styling slots.
   */
  classNames?: Pick<DrawerClassNames, 'content' | 'contentBody' | 'knob' | 'overlay'>;
};

/**
 * Props for the drawer content body component.
 * Standard div element props for the inner content wrapper.
 */
export type DrawerContentBodyProps = HTMLComponentProps<'div'>;

/**
 * Props for the drawer footer component.
 * Standard div element props for the footer section of the drawer.
 */
export type DrawerFooterProps = HTMLComponentProps<'div'>;

/**
 * Props for the drawer knob component.
 * The draggable indicator typically shown at the top of mobile drawer implementations.
 */
export type DrawerKnobProps = HTMLComponentProps<'div'>;

/**
 * Props for the drawer overlay component.
 * The backdrop that appears behind the drawer when it's open.
 */
export type DrawerOverlayProps = Omit<DialogOverlayProps, 'component'>;

/**
 * Props for the drawer header component.
 * Container for the drawer's header content, typically including title and close button.
 */
export type DrawerHeaderProps = DialogHeaderProps;

/**
 * Props for the drawer description component.
 * Accessible description text for the drawer content.
 */
export type DrawerDescriptionProps = Omit<DialogDescriptionProps, 'component'>;

/**
 * Props for the drawer title component.
 * The main heading/title displayed in the drawer header.
 */
export type DrawerTitleProps = Omit<DialogTitleProps, 'component'>;

/**
 * Props for the drawer close button component.
 * Button trigger to dismiss and close the drawer.
 */
export type DrawerCloseProps = Omit<DialogCloseProps, 'component'>;

/**
 * Props for the main Drawer component.
 * A slide-out panel component with header, content, footer, and overlay.
 *
 * @example
 * ```tsx
 * <Drawer
 *   trigger={<button>Open Drawer</button>}
 *   title="Settings"
 *   description="Manage your preferences"
 *   footer={<button>Save</button>}
 *   showClose
 * >
 *   Drawer content goes here
 * </Drawer>
 * ```
 */
export type DrawerProps = DialogProps & {
  /**
   * Class names for customizing different parts of the drawer component.
   */
  classNames?: DrawerClassNames;
  /**
   * Props to customize the content area of the drawer.
   */
  contentProps?: DrawerContentProps;
  /**
   * Accessible description text for the drawer.
   * Useful for screen readers and accessibility.
   */
  description?: React.ReactNode;
  /**
   * Content to display in the drawer footer section.
   * Typically used for action buttons like Save/Cancel.
   */
  footer?: React.ReactNode;
  /**
   * Whether to show the close button in the drawer header.
   * Defaults to false.
   */
  showClose?: boolean;
  /**
   * Size variant for the drawer (sm, md, lg, etc).
   * Controls the width/dimensions of the drawer panel.
   */
  size?: ThemeSize;
  /**
   * Title text displayed in the drawer header.
   */
  title?: React.ReactNode;
  /**
   * Element that triggers the opening of the drawer.
   * Usually a button or interactive element.
   */
  trigger?: React.ReactNode;
};
