'use client';

import { useComponentConfig } from '../config-provider/context';
import DrawerUI from './DrawerUI';
import type { DrawerProps } from './types';

const Drawer = (props: DrawerProps) => {
  const config = useComponentConfig('drawer');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <DrawerUI
      {...mergedProps}
    />
  );
};

Drawer.displayName = 'Drawer';

export default Drawer;

import { Content } from '@radix-ui/react-dialog';
import { cn } from '@/lib/utils';
import { drawerVariants } from './drawer-variants';
import type { DrawerContentProps } from './types';

const DrawerContent = (props: DrawerContentProps) => {
  const { className, side, ...rest } = props;

  const { content } = drawerVariants({ side });

  const mergedCls = cn(content(), className);

  return (
    <Content
      className={mergedCls}
      data-side={side}
      data-slot="drawer-content"
      {...rest}
    />
  );
};

export default DrawerContent;

'use client';

import { cn } from '@/lib/utils';
import { Dialog } from '../dialog';
import DrawerContent from './DrawerContent';
import type { DrawerProps } from './types';

const DrawerUI = (props: DrawerProps) => {
  const { children, scrollable = true, ...rest } = props;

  return (
    <Dialog
      contentComponent={DrawerContent}
      {...rest}
    >
      <div className={cn('flex-grow', scrollable && ' overflow-auto')}>{children}</div>
    </Dialog>
  );
};

export default DrawerUI;

import { tv } from 'tailwind-variants';
import type { VariantProps } from 'tailwind-variants';

export const drawerVariants = tv({
  defaultVariants: {
    side: 'right',
    size: 'md'
  },
  slots: {
    content: [
      `fixed z-50 flex flex-col justify-between items-stretch border bg-background rounded-md transition ease-in-out`,
      `data-[state=open]:animate-in data-[state=open]:duration-500`,
      `data-[state=closed]:animate-out data-[state=closed]:duration-300`
    ]
  },
  variants: {
    side: {
      bottom: {
        content: `inset-x-0 bottom-0 border-t rounded-b-0 data-[state=open]:slide-in-from-bottom data-[state=closed]:slide-out-to-bottom`
      },
      left: {
        content: `inset-y-0 left-0 h-full w-3/4 border-r rounded-l-0 data-[state=open]:slide-in-from-left data-[state=closed]:slide-out-to-left sm:max-w-sm`
      },
      right: {
        content: `inset-y-0 right-0 h-full w-3/4 border-l rounded-r-0 data-[state=open]:slide-in-from-right data-[state=closed]:slide-out-to-right sm:max-w-sm`
      },
      top: {
        content: `inset-x-0 top-0 border-b rounded-t-0 data-[state=open]:slide-in-from-top data-[state=closed]:slide-out-to-top`
      }
    },
    size: {
      '2xl': {
        content: `gap-y-6 px-7 py-6 text-xl`
      },
      'lg': {
        content: `gap-y-4 px-5 py-4 text-base`
      },
      'md': {
        content: `gap-y-3 px-4 py-3 text-sm`
      },
      'sm': {
        content: `gap-y-2 px-3 py-2 text-xs`
      },
      'xl': {
        content: `gap-y-5 px-6 py-5 text-lg`
      },
      'xs': {
        content: `gap-y-1.5 px-2 py-1.5 text-2xs`
      }
    }
  }
});

type DrawerVariants = VariantProps<typeof drawerVariants>;

export type DrawerSide = NonNullable<DrawerVariants['side']>;

export { default as Drawer } from './Drawer';
export { default as DrawerContent } from './DrawerContent';

export * from './types';

import type { DialogContentProps } from '@radix-ui/react-dialog';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { DialogSlots as DrawerSlots } from '../dialog/dialog-variants';
import type { DialogProps } from '../dialog/types';
import type { DrawerSide } from './drawer-variants';

/**
 * Props for the drawer content component.
 * The main container that slides in from a specified side of the screen.
 *
 * @example
 * ```tsx
 * <DrawerContent side="right">
 *   <DrawerHeader>
 *     <DrawerTitle>Drawer Title</DrawerTitle>
 *   </DrawerHeader>
 *   <div>Drawer content goes here</div>
 * </DrawerContent>
 * ```
 */
export interface DrawerContentProps extends StyledComponentProps<DialogContentProps> {
  /**
   * The side of the screen from which the drawer slides in.
   * Can be "top", "right", "bottom", or "left".
   */
  side?: DrawerSide;
}

/**
 * Class names for different slots in the drawer component.
 * Allows customizing styles for the overlay, content, and other parts.
 */
export type DrawerClassNames = Partial<Record<DrawerSlots, ClassValue>>;

/**
 * Props for the main Drawer component.
 * A side panel modal that slides in from the edge of the screen.
 *
 * @example
 * ```tsx
 * <Drawer scrollable>
 *   <DrawerTrigger>Open Drawer</DrawerTrigger>
 *   <DrawerContent side="left">
 *     <DrawerHeader>
 *       <DrawerTitle>Navigation</DrawerTitle>
 *     </DrawerHeader>
 *     <DrawerBody>
 *       <nav>Navigation items here</nav>
 *     </DrawerBody>
 *   </DrawerContent>
 * </Drawer>
 * ```
 */
export interface DrawerProps extends Omit<DialogProps<DrawerContentProps>, 'contentComponent'> {
  /**
   * Whether the drawer content should be scrollable.
   * When true, adds overflow-auto to the content wrapper.
   * @default true
   */
  scrollable?: boolean;
}

export { DrawerSide };
