'use client';

import { ComputedField } from '@skyroc/form';
import FormField from './FormField';
import type { FormComputedFieldProps } from './types';

const FormComputedField = <Values = any,>(props: FormComputedFieldProps<Values>) => {
  return (
    <FormField
      component={ComputedField}
      {...props}
    />
  );
};

export default FormComputedField;

import { cn } from '@/lib/utils';
import { formVariants } from './form-variants';
import type { FormDescriptionProps } from './types';

const FormDescription = (props: FormDescriptionProps) => {
  const { className, ...rest } = props;

  const { description } = formVariants();

  const mergedClass = cn(description(), className);

  return (
    <p
      className={mergedClass}
      data-slot="form-description"
      {...rest}
    />
  );
};

export default FormDescription;

'use client';

import { useComponentConfig } from '../config-provider/context';
import FormFieldUI from './FormFieldUI';
import type { FormFieldProps } from './types';

const FormField = <Values = any,>(props: FormFieldProps<Values>) => {
  const config = useComponentConfig('formField');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <FormFieldUI
      {...mergedProps}
    />
  );
};

FormField.displayName = 'FormField';

export default FormField;

'use client';

import { useId } from 'react';
import type { AllPathsKeys } from '@skyroc/form';
import { Field, useFieldError } from '@skyroc/form';
import FormDescription from './FormDescription';
import FormItem from './FormItem';
import FormLabel from './FormLable';
import FormMessage from './FormMessage';
import type { FormFieldProps } from './types';

const FormFieldUI = <Values = any,>(props: FormFieldProps<Values>) => {
  const {
    children,
    className,
    classNames,
    component: Component = Field,
    description,
    label,
    name,
    size,
    ...rest
  } = props;

  const id = useId();

  const errors = useFieldError<Values, AllPathsKeys<Values>>(name);

  const hasError = errors.length > 0;

  const formItemId = `${id}-form-item`;
  const formDescriptionId = `${id}-form-item-description`;
  const formMessageId = `${id}-form-item-message`;

  return (
    <FormItem
      className={className}
      size={size}
    >
      <FormLabel
        className={classNames?.label}
        error={hasError}
        htmlFor={formItemId}
        size={size}
      >
        {label}
      </FormLabel>

      <Component
        {...rest}
        aria-describedby={!hasError ? `${formDescriptionId}` : `${formDescriptionId} ${formMessageId}`}
        aria-invalid={hasError}
        id={id}
        name={name}
      >
        {children}
      </Component>

      {description
        ? (
          <FormDescription
            className={classNames?.description}
            id={formDescriptionId}
          >
            {description}
          </FormDescription>
        )
        : null}

      {hasError
        ? (
          <FormMessage
            className={classNames?.message}
            id={formMessageId}
          >
            {errors[0]}
          </FormMessage>
        )
        : null}
    </FormItem>
  );
};

FormFieldUI.displayName = 'FormFieldUI';

export default FormFieldUI;

import { cn } from '@/lib/utils';
import { formVariants } from './form-variants';
import type { FormItemProps } from './types';

const FormItem = (props: FormItemProps) => {
  const { className, size, ...rest } = props;

  const { item } = formVariants({ size });

  const mergedCls = cn(item(), className);

  return (
    <div
      className={mergedCls}
      data-slot="form-item"
      {...rest}
    />
  );
};

export default FormItem;

import { cn } from '@/lib/utils';
import Label from '../label/Label';
import { formVariants } from './form-variants';
import type { FormLabelProps } from './types';

const FormLabel = (props: FormLabelProps) => {
  const { className, error, size, ...rest } = props;

  const { label } = formVariants({ error, size });

  const mergedCls = cn(label(), className);

  return (
    <Label
      className={mergedCls}
      data-error={error}
      data-slot="form-label"
      size={size}
      {...rest}
    />
  );
};

export default FormLabel;

import { cn } from '@/lib/utils';
import { formVariants } from './form-variants';
import type { FormMessageProps } from './types';

const FormMessage = (props: FormMessageProps) => {
  const { className, error, ...rest } = props;

  const { message } = formVariants();

  const mergedCls = cn(message(), className);

  const body = error?.length ? error[0] : props.children;

  if (!body)
    return null;

  return (
    <p
      className={mergedCls}
      data-slot="form-message"
      {...rest}
    >
      {body}
    </p>
  );
};

export default FormMessage;

import { tv } from 'tailwind-variants';

export const formVariants = tv({
  defaultVariants: {
    error: false,
    size: 'md'
  },
  slots: {
    description: `text-muted-foreground`,
    item: `form-item`,
    label: 'flex items-center data-[error=true]:text-destructive',
    message: `font-medium text-destructive`
  },
  variants: {
    error: {
      true: {
        message: `text-destructive`
      }
    },
    size: {
      '2xl': {
        item: 'text-xl space-y-3.5',
        label: 'gap-3.5'
      },
      'lg': {
        item: 'text-base space-y-2.5',
        label: 'gap-2.5'
      },
      'md': {
        item: 'text-sm space-y-2',
        label: 'gap-2'
      },
      'sm': {
        item: 'text-xs space-y-1.75',
        label: 'gap-1.75'
      },
      'xl': {
        item: 'text-lg space-y-3',
        label: 'gap-3'
      },
      'xs': {
        item: 'text-2xs space-y-1.5',
        label: 'gap-1.5'
      }
    }
  }
});

export type FormSlots = keyof typeof formVariants.slots;

'use client';

export {
  Form,
  List as FormList,
  useArrayField,
  useEffectField,
  useFieldError,
  useFieldState,
  useForm,
  useSelector,
  useUndoRedo,
  useWatch
} from '@skyroc/form';

export type {
  Action as FormAction,
  AllPathsKeys,
  ComputedFieldProps,
  FieldElement,
  FormInstance,
  FormProps,
  Meta,
  Rule,
  SubscribeMaskOptions,
  ValidateMessages
} from '@skyroc/form';

export { default as FormComputedField } from './FormComputedField';
export { default as FormField } from './FormField';

export type { FormComputedFieldProps, FormFieldProps } from './types';

import type { ComponentProps, ElementType, ReactNode } from 'react';
import type { ComputedFieldProps, FieldProps } from '@skyroc/form';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { LabelProps } from '../label/types';
import type { FormSlots } from './form-variants';

/**
 * Class names for different slots in the form component.
 * Allows customizing styles for specific parts of form layout.
 */
type FormClassNames = Partial<Record<FormSlots, ClassValue>>;

/**
 * Props for the form description component.
 * Used to display helper text or additional information about a form field.
 */
export interface FormDescriptionProps extends StyledComponentProps<ComponentProps<'p'>> {}

/**
 * Props for the form item wrapper component.
 * Container that groups label, input, description, and error message.
 */
export interface FormItemProps extends StyledComponentProps<ComponentProps<'div'>> {}

/**
 * Props for the form label component.
 * Label text associated with form fields, with error state styling.
 */
export interface FormLabelProps extends LabelProps {
  /**
   * Whether the label should display error styling.
   * Applied when the associated field has validation errors.
   */
  error?: boolean;
}

/**
 * Props for the form message component.
 * Displays validation error messages or informational text.
 */
export interface FormMessageProps extends StyledComponentProps<ComponentProps<'p'>> {
  /**
   * Array of error messages to display.
   * When present, the message component renders in error state.
   */
  error?: string[];
}

/**
 * Shared props for form field components.
 * Common configuration options for both regular and computed fields.
 */
type FormSharedProps = StyledComponentProps<{
  /**
   * Class names for customizing different parts of the form field.
   */
  classNames?: FormClassNames;
  /**
   * Helper text displayed below the form field input.
   * Used to provide additional context or usage information.
   */
  description?: string;
  /**
   * Error message displayed when field validation fails.
   */
  error?: string;
  /**
   * Label text displayed above the form field input.
   */
  label?: ReactNode;
}>;

/**
 * Props for a regular form field component.
 * Used for standard form inputs with validation and labeling.
 *
 * @template Values - The shape of form values object
 *
 * @example
 * ```tsx
 * <FormField<{ email: string }>
 *   name="email"
 *   label="Email Address"
 *   description="Enter your email"
 *   error={errors.email}
 *   component={Input}
 * />
 * ```
 */
export type FormFieldProps<Values = any> = FieldProps<Values>
  & FormSharedProps & {
    /**
     * The React component or HTML element to render as the form input.
     * Examples: Input, Select, Textarea, etc.
     */
    component?: ElementType;
  };

/**
 * Props for a computed form field component.
 * Used for fields that derive their value from other form fields.
 *
 * @template Values - The shape of form values object
 *
 * @example
 * ```tsx
 * <FormComputedField<{ firstName: string; lastName: string; fullName: string }>
 *   name="fullName"
 *   compute={(values) => `${values.firstName} ${values.lastName}`}
 *   label="Full Name"
 * />
 * ```
 */
export type FormComputedFieldProps<Values = any> = ComputedFieldProps<Values> & FormSharedProps;
