'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import HoverCardUI from './HoverCardUI';
import type { HoverCardProps } from './types';

const HoverCard = forwardRef<ComponentRef<typeof HoverCardUI>, HoverCardProps>((props, ref) => {
  const config = useComponentConfig('hoverCard');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <HoverCardUI
      {...mergedProps}
      ref={ref}
    />
  );
});

HoverCard.displayName = 'HoverCard';

export default HoverCard;

import { Arrow } from '@radix-ui/react-hover-card';
import { cn } from '@/lib/utils';
import { hoverCardVariants } from './hover-card-variants';
import type { HoverCardArrowProps } from './types';

const HoverCardArrow = (props: HoverCardArrowProps) => {
  const { className, size, ...rest } = props;

  const { arrow } = hoverCardVariants({ size });

  const mergedClass = cn(arrow(), className);

  return (
    <Arrow
      {...rest}
      className={mergedClass}
      data-slot="hover-card-arrow"
    />
  );
};

export default HoverCardArrow;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Content, Portal } from '@radix-ui/react-hover-card';
import { cn } from '@/lib/utils';
import { hoverCardVariants } from './hover-card-variants';
import type { HoverCardContentProps } from './types';

const HoverCardContent = forwardRef<ComponentRef<typeof Content>, HoverCardContentProps>((props, ref) => {
  const { align = 'center', className, sideOffset = 8, ...rest } = props;

  const { content } = hoverCardVariants();

  const mergedCls = cn(content(), className);

  return (
    <Portal data-slot="hover-card-portal">
      <Content
        align={align}
        className={mergedCls}
        data-slot="hover-card-content"
        ref={ref}
        sideOffset={sideOffset}
        {...rest}
      />
    </Portal>
  );
});

HoverCardContent.displayName = 'HoverCardContent';

export default HoverCardContent;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root, Trigger } from '@radix-ui/react-hover-card';
import HoverCardArrow from './HoverCardArrow';
import HoverCardContent from './HoverCardContent';
import type { HoverCardProps } from './types';

const HoverCardUI = forwardRef<ComponentRef<typeof HoverCardContent>, HoverCardProps>((props, ref) => {
  const { arrowProps, children, className, classNames, showArrow, trigger, ...rest } = props;

  return (
    <Root
      data-slot="hover-card-root"
      {...rest}
    >
      <Trigger
        asChild
        data-slot="hover-card-trigger"
      >
        {trigger}
      </Trigger>

      <HoverCardContent
        className={className || classNames?.content}
        ref={ref}
      >
        {children}

        {showArrow
          ? (
            <HoverCardArrow
              {...arrowProps}
              className={classNames?.arrow}
            />
          )
          : null}
      </HoverCardContent>
    </Root>
  );
});

HoverCardUI.displayName = 'HoverCardUI';

export default HoverCardUI;

import { tv } from 'tailwind-variants';

export const hoverCardVariants = tv({
  slots: {
    arrow: 'fill-popover stroke-border',
    content: [
      `w-auto p-4 rounded-md border bg-popover  text-popover-foreground shadow-md outline-none z-50 will-change-transform`,
      `data-[state=open]:zoom-in-95 data-[state=closed]:zoom-out-95 data-[state=open]:fade-in-0  data-[state=open]:animate-in`,
      `data-[state=closed]:zoom-out-95 data-[state=closed]:fade-out-0  data-[state=closed]:animate-out`,
      `data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2`
    ]
  }
});

export type HoverCardSlots = keyof typeof hoverCardVariants.slots;

export { Root as HoverCardRoot, Trigger as HoverCardTrigger } from '@radix-ui/react-hover-card';

export { default as HoverCard } from './HoverCard';
export { default as HoverCardArrow } from './HoverCardArrow';
export { default as HoverCardContent } from './HoverCardContent';

export * from './types';

import type {
  HoverCardArrowProps as _HoverCardArrowProps,
  HoverCardContentProps as _HoverCardContentProps,
  HoverCardProps as _HoverCardProps
} from '@radix-ui/react-hover-card';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { HoverCardSlots } from './hover-card-variants';

/**
 * Class names for different slots in the hover card component.
 * Allows customizing styles for specific parts of the hover card.
 */
export type HoverCardClassNames = Partial<Record<HoverCardSlots, ClassValue>>;

/**
 * Props for the main Hover Card component.
 * A floating card that appears when hovering over a trigger element.
 *
 * @example
 * ```tsx
 * <HoverCard
 *   trigger={<span>Hover me</span>}
 *   showArrow
 *   contentProps={{ side: 'right' }}
 * >
 *   <p>This content appears on hover</p>
 * </HoverCard>
 * ```
 */
export type HoverCardProps = StyledComponentProps<_HoverCardProps> & {
  /**
   * Props to customize the arrow indicator of the hover card.
   * Only applied when showArrow is true.
   */
  arrowProps?: HoverCardArrowProps;
  /**
   * Class names for customizing different parts of the hover card component.
   */
  classNames?: HoverCardClassNames;
  /**
   * Props to customize the content container of the hover card.
   * Includes positioning, delay, and other content behaviors.
   */
  contentProps?: Omit<HoverCardContentProps, 'children' | 'className'>;
  /**
   * Whether to display the arrow indicator pointing to the trigger element.
   * Defaults to false.
   */
  showArrow?: boolean;
  /**
   * Element that triggers the hover card to appear.
   * Can be any React element or component.
   */
  trigger?: React.ReactNode;
};

/**
 * Props for the hover card arrow component.
 * The small arrow/pointer that visually connects the trigger to the card.
 */
export type HoverCardArrowProps = StyledComponentProps<_HoverCardArrowProps>;

/**
 * Props for the hover card content component.
 * Container for the content displayed in the floating hover card.
 */
export type HoverCardContentProps = StyledComponentProps<_HoverCardContentProps>;
