'use client';

import { useComponentConfig } from '../config-provider/context';
import KeyboardKeyUI from './KeyboardKeyUI';
import type { KeyboardKeyProps } from './types';

const KeyboardKey = (props: KeyboardKeyProps) => {
  const config = useComponentConfig('keyboardKey');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <KeyboardKeyUI
      {...mergedProps}
    />
  );
};

KeyboardKey.displayName = 'KeyboardKey';

export default KeyboardKey;

import { Fragment } from 'react';
import { cn } from '@/lib/utils';
import KeyboardKey from './KeyboardKey';
import { keyboardKeyVariants } from './keyboard-key-variants';
import type { KeyboardKeyGroupProps } from './types';

const KeyboardKeyGroup = (props: KeyboardKeyGroupProps) => {
  const { className, classNames, separator = '+', size, values, variant, ...rest } = props;

  const { group, separator: separatorCls } = keyboardKeyVariants({ size, variant });

  const mergedCls = cn(group(), className || classNames?.group);

  const separatorMergedCls = cn(separatorCls(), classNames?.separator);

  return (
    <div
      {...rest}
      className={mergedCls}
      data-size={size}
      data-slot="keyboard-key-group"
      data-variant={variant}
    >
      {values?.map((value, index) => (
        <Fragment key={String(index)}>
          <KeyboardKey
            className={classNames?.item}
            size={size}
            value={value}
            variant={variant}
          />

          {separator && index !== values.length - 1 ? <span className={separatorMergedCls}>{separator}</span> : null}
        </Fragment>
      ))}
    </div>
  );
};

export default KeyboardKeyGroup;

'use client';

import { cn } from '@/lib';
import { keyboardKeyVariants } from './keyboard-key-variants';
import type { KeyboardKeyProps } from './types';
import { useKeyboardKey } from './use-keyboard-key';

export const KeyboardKeyUI = (props: KeyboardKeyProps) => {
  const { children, className, size, value, variant, ...rest } = props;

  const { item } = keyboardKeyVariants({ size, variant });

  const mergedCls = cn(item(), className);

  const { getKeyboardKey } = useKeyboardKey();

  const getValues = () => {
    if (!value) {
      return [];
    }

    if (Array.isArray(value)) {
      return value.map(v => getKeyboardKey(v)).filter(Boolean);
    }

    return [getKeyboardKey(value)!];
  };

  const values = getValues();

  return (
    <div
      className={mergedCls}
      data-size={size}
      data-slot="keyboard-key"
      data-variant={variant}
      {...rest}
    >
      {children ? children(values) : values.map(v => <span key={v}>{v}</span>)}
    </div>
  );
};

KeyboardKeyUI.displayName = 'KeyboardKeyUI';

export default KeyboardKeyUI;

export { default as KeyboardKey } from './KeyboardKey';
export { default as KeyboardKeyGroup } from './KeyboardKeyGroup';

export * from './types';

export { useKeyboardKey } from './use-keyboard-key';

import { tv } from 'tailwind-variants';
import type { VariantProps } from 'tailwind-variants';

export const keyboardKeyVariants = tv({
  defaultVariants: {
    size: 'md',
    variant: 'outline'
  },
  slots: {
    group: 'flex items-center',
    item: `inline-flex items-center justify-center w-fit font-medium rounded-sm`,
    separator: 'font-medium text-muted-foreground'
  },
  variants: {
    size: {
      '2xl': {
        group: 'gap-2',
        item: 'gap-1.5 h-8 min-w-8 px-1.5 text-lg'
      },
      'lg': {
        group: 'gap-1.5',
        item: 'gap-1 h-6 min-w-6 px-1 text-sm'
      },
      'md': {
        group: 'gap-1.25',
        item: 'gap-0.75 h-5 min-w-5 px-0.75 text-xs'
      },
      'sm': {
        group: 'gap-1',
        item: 'gap-0.625 h-4.5 min-w-4.5 px-0.625 text-2xs'
      },
      'xl': {
        group: 'gap-1.75',
        item: 'gap-1.25 h-7 min-w-7 px-1.25 text-base'
      },
      'xs': {
        group: 'gap-0.75',
        item: 'gap-0.5 h-4 min-w-4 px-0.5 text-3xs'
      }
    },
    variant: {
      ghost: {
        item: 'border-border bg-muted text-muted-foreground border'
      },
      outline: {
        item: 'border-border bg-background text-muted-foreground border'
      },
      solid: {
        item: 'bg-muted-foreground text-muted'
      }
    }
  }
});

type KeyboardKeyVariants = VariantProps<typeof keyboardKeyVariants>;

export type KeyboardKeyVariant = NonNullable<KeyboardKeyVariants['variant']>;

export type KeyboardKeySlots = keyof typeof keyboardKeyVariants.slots;

import type { ReactNode } from 'react';
import type { HTMLComponentProps, ClassValue } from '@/types/shared';
import type { KeyboardKeySlots, KeyboardKeyVariant } from './keyboard-key-variants';

/**
 * Predefined keyboard keys that have built-in styling and special handling.
 * These keys are commonly used in keyboard shortcuts and key combination displays.
 */
export type BuiltinKeyboardKey
  = | 'alt'
    | 'arrowdown'
    | 'arrowleft'
    | 'arrowright'
    | 'arrowup'
    | 'backspace'
    | 'capslock'
    | 'command'
    | 'ctrl'
    | 'delete'
    | 'end'
    | 'enter'
    | 'escape'
    | 'home'
    | 'meta'
    | 'option'
    | 'pagedown'
    | 'pageup'
    | 'shift'
    | 'tab'
    | 'win';

/**
 * Platform-specific mappings for modifier keys.
 * Allows different visual representations based on the operating system.
 * For example, "cmd" on Mac vs "ctrl" on Windows.
 */
export interface SpecificKeyboardKeyMap {
  /** Custom display text for the alt key */
  alt: string;
  /** Custom display text for the ctrl key */
  ctrl: string;
  /** Custom display text for the meta key */
  meta: string;
}

/**
 * Union type that accepts either built-in keyboard keys or custom key strings.
 * Use built-in keys for standard keyboard keys, or custom strings for any other key identifier.
 */
export type KeyboardKeyValue = BuiltinKeyboardKey | (string & {});

/**
 * Props for the KeyboardKey component.
 * Renders a visual representation of one or more keyboard keys.
 *
 * @template T - Type of the keyboard key value(s), defaults to KeyboardKeyValue
 *
 * @example
 * // Single key
 * <KeyboardKey value="enter" />
 *
 * @example
 * // Key combination (multiple keys)
 * <KeyboardKey value={['ctrl', 'c']} />
 *
 * @example
 * // Custom rendering of key values
 * <KeyboardKey value={['cmd', 'shift', 'p']}>
 *   {(values) => values.join(' + ')}
 * </KeyboardKey>
 */
export interface KeyboardKeyProps<T extends KeyboardKeyValue | KeyboardKeyValue[] = KeyboardKeyValue>
  extends Omit<HTMLComponentProps<'div'>, 'children'> {
  /**
   * Render function that receives an array of key display values.
   * Used to customize how the keys are rendered and displayed.
   */
  children?: (values: string[]) => React.ReactNode;
  /**
   * The keyboard key(s) to display.
   * Can be a single key, an array of keys, or a keyboard key value.
   */
  value?: T | string[];
  /**
   * Visual variant style for the keyboard key(s).
   */
  variant?: KeyboardKeyVariant;
}

/**
 * Class names for different slots in the keyboard key component.
 * Allows customizing styles for specific parts of the keyboard key.
 */
export type KeyboardKeyClassNames = Partial<Record<KeyboardKeySlots, ClassValue>>;

/**
 * Props for the KeyboardKeyGroup component.
 * Renders multiple keyboard keys with a separator between them.
 *
 * @template T - Type of the keyboard key value(s), defaults to KeyboardKeyValue
 *
 * @example
 * // Keyboard shortcut with default separator
 * <KeyboardKeyGroup values={['ctrl', 'shift', 'k']} />
 *
 * @example
 * // Custom separator and rendering
 * <KeyboardKeyGroup
 *   values={['cmd', 'option', 'j']}
 *   separator=" + "
 *   render={(key) => <span key={key}>{key}</span>}
 * />
 */
export interface KeyboardKeyGroupProps<T extends KeyboardKeyValue | KeyboardKeyValue[] = KeyboardKeyValue>
  extends Omit<KeyboardKeyProps, 'children' | 'value'> {
  /**
   * Class names for customizing different parts of the keyboard key group.
   */
  classNames?: KeyboardKeyClassNames;
  /**
   * Custom render function for individual keys in the group.
   * Each key value will be passed to this function for rendering.
   */
  render?: (value: T) => React.ReactNode;
  /**
   * Content to display between keyboard keys (separator).
   * Defaults to a visual separator if not provided.
   */
  separator?: ReactNode;
  /**
   * Array of keyboard key values to display in the group.
   * Each value will be rendered as a separate key component.
   */
  values: T[];
}

export type { KeyboardKeyVariant };

'use client';

import { useEffect, useState } from 'react';

export type BuiltinKeyboardKey
  = | 'alt'
    | 'arrowdown'
    | 'arrowleft'
    | 'arrowright'
    | 'arrowup'
    | 'backspace'
    | 'capslock'
    | 'command'
    | 'ctrl'
    | 'delete'
    | 'end'
    | 'enter'
    | 'escape'
    | 'home'
    | 'meta'
    | 'option'
    | 'pagedown'
    | 'pageup'
    | 'shift'
    | 'tab'
    | 'win';

export interface KeyboardKeyProps {
  value: string;
}

interface SpecificKeyboardKeyMap {
  alt: string;
  ctrl: string;
  meta: string;
}

export const builtinKeyboardKeyMap: Record<BuiltinKeyboardKey, string> = {
  alt: '',
  arrowdown: '↓',
  arrowleft: '←',
  arrowright: '→',
  arrowup: '↑',
  backspace: '⌫',
  capslock: '⇪',
  command: '⌘',
  ctrl: '',
  delete: '⌦',
  end: '↘',
  enter: '↵',
  escape: '⎋',
  home: '↖',
  meta: '',
  option: '⌥',
  pagedown: '⇟',
  pageup: '⇞',
  shift: '⇧',
  tab: '⇥',
  win: '⊞'
};

/* ------------------------------------------------------------------ */
/*                               Hook                                 */
/* ------------------------------------------------------------------ */
export function useKeyboardKey() {
  const [isMacOS, setIsMacOS] = useState(false);

  const specificMapRef: SpecificKeyboardKeyMap = {
    alt: isMacOS ? builtinKeyboardKeyMap.option : 'alt',
    ctrl: isMacOS ? '⌃' : 'ctrl',
    meta: isMacOS ? builtinKeyboardKeyMap.command : builtinKeyboardKeyMap.win
  };

  const getKeyboardKey = (value?: KeyboardKeyProps['value']) => {
    if (!value)
      return '';

    if (value === 'meta' || value === 'alt' || value === 'ctrl') {
      return specificMapRef[value as keyof SpecificKeyboardKeyMap];
    }

    return builtinKeyboardKeyMap[value as BuiltinKeyboardKey] || value.toUpperCase();
  };

  useEffect(() => {
    setIsMacOS(/Macintosh;/.test(navigator.userAgent));
  }, []);

  return {
    getKeyboardKey,
    isMacOS
  };
}
