'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Root } from '@radix-ui/react-label';
import { useComponentConfig } from '../config-provider/context';
import LabelUI from './LabelUI';
import type { LabelProps } from './types';

const Label = forwardRef<ComponentRef<typeof Root>, LabelProps>((props, ref) => {
  const config = useComponentConfig('label');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <LabelUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Label.displayName = 'Label';

export default Label;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root } from '@radix-ui/react-label';
import { cn } from '@/lib/utils';
import { labelVariants } from './label-variants';
import type { LabelProps } from './types';

const LabelUI = forwardRef<ComponentRef<typeof Root>, LabelProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const mergedCls = cn(labelVariants({ size }), className);

  return (
    <Root
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

LabelUI.displayName = 'LabelUI';

export default LabelUI;

import { tv } from 'tailwind-variants';

export const labelVariants = tv({
  base: 'font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70',
  defaultVariants: {
    size: 'md'
  },
  variants: {
    size: {
      '2xl': 'text-xl',
      'lg': 'text-base',
      'md': 'text-sm',
      'sm': 'text-xs',
      'xl': 'text-lg',
      'xs': 'text-2xs'
    }
  }
});
