'use client';

import { useComponentConfig } from '../config-provider/context';
import LayoutUI from './LayoutUI';
import type { LayoutProps } from './types';

const Layout = (props: LayoutProps) => {
  const config = useComponentConfig('layout');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <LayoutUI
      {...mergedProps}
    />
  );
};

Layout.displayName = 'Layout';

export default Layout;

import { cn } from '@/lib/utils';
import { layoutVariants } from './layout-variants';
import type { LayoutFooterProps } from './types';

const LayoutFooter = (props: LayoutFooterProps) => {
  const { className, ...rest } = props;

  const { footer } = layoutVariants();

  const mergedClass = cn(footer(), className);

  return (
    <footer
      className={mergedClass}
      {...rest}
    />
  );
};

export default LayoutFooter;

import { cn } from '@/lib/utils';
import { layoutVariants } from './layout-variants';
import type { LayoutHeaderProps } from './types';

const LayoutHeader = (props: LayoutHeaderProps) => {
  const { className, ...rest } = props;

  const { header } = layoutVariants();

  const mergedClass = cn(header(), className);

  return (
    <header
      className={mergedClass}
      {...rest}
    />
  );
};

export default LayoutHeader;

import { cn } from '@/lib/utils';
import { layoutVariants } from './layout-variants';
import type { LayoutMainProps } from './types';

const LayoutMain = (props: LayoutMainProps) => {
  const { className, collapsible, variant, ...rest } = props;

  const { main } = layoutVariants({ collapsible, variant });

  const mergedClass = cn(main(), className);

  return (
    <main
      className={mergedClass}
      {...rest}
    />
  );
};

export default LayoutMain;

import type { CSSProperties } from 'react';
import { cn } from '@/lib/utils';
import { DialogOverlay, DialogPortal, DialogRoot } from '../dialog';
import { DrawerContent } from '../drawer';
import { useLayoutContext } from './context';
import { layoutVariants } from './layout-variants';
import type { LayoutMobileProps } from './types';

const LayoutMobile = (props: LayoutMobileProps) => {
  const { children, className, rootClassName, side } = props;
  const { mobile, mobileRoot } = layoutVariants();
  const mergedCls = {
    cls: cn(mobile(), className),
    root: cn(mobileRoot(), rootClassName)
  };
  const { onOpenMobileChange, openMobile } = useLayoutContext();

  return (
    <DialogRoot
      open={openMobile}
      onOpenChange={onOpenMobileChange}
    >
      <DialogPortal>
        <DialogOverlay />

        <DrawerContent
          className={mergedCls.root}
          side={side}
          style={{ '--skyroc-sidebar-width': '18rem' } as CSSProperties}
        >
          <div
            className={mergedCls.cls}
            data-mobile="true"
            data-sidebar="sidebar"
          >
            {children}
          </div>
        </DrawerContent>
      </DialogPortal>
    </DialogRoot>
  );
};

export default LayoutMobile;

import { cn } from '@/lib/utils';
import { useLayoutContext } from './context';
import { layoutVariants } from './layout-variants';
import type { LayoutRailProps } from './types';

const LayoutRail = (props: LayoutRailProps) => {
  const { className, collapsible, side, variant } = props;
  const { rail } = layoutVariants({ collapsible, side, variant });
  const mergedCls = cn(rail(), className);
  const { toggleSidebar } = useLayoutContext();
  return (
    <div
      className={mergedCls}
      title="Toggle sidebar"
      onClick={toggleSidebar}
    />
  );
};

export default LayoutRail;

'use client';

import type { CSSProperties } from 'react';
import { useMemo, useState } from 'react';
import { useControllableState } from '@radix-ui/react-use-controllable-state';
import { themeSizeRatio } from '@/constants/theme';
import { useMediaQuery } from '@/hooks/use-media-query';
import { cn } from '@/lib/utils';
import { LayoutContext } from './context';
import { layoutVariants } from './layout-variants';
import type { LayoutRootProps } from './types';

const LayoutRoot = ({
  children,
  collapsedSidebarWidth = 50,
  collapsible,
  defaultOpen = false,
  onOpenChange,
  open: controlledOpen,
  side,
  sidebarWidth = 240,
  size = 'md',
  variant,
  ...props
}: LayoutRootProps) => {
  const isMobile = useMediaQuery({ maxWidth: 768 });
  const [openMobile, setOpenMobile] = useState(false);
  // const [internalOpen, setInternalOpen] = useState(defaultOpen);
  const [open, setOpen] = useControllableState({
    caller: 'layout',
    defaultProp: defaultOpen,
    onChange: onOpenChange,
    prop: controlledOpen
  });

  // Support controlled and uncontrolled mode
  // const open = controlledOpen !== undefined ? controlledOpen : internalOpen;

  const handleOpenChange = (value: boolean) => {
    if (controlledOpen === undefined) {
      setOpen(value);
    }
    onOpenChange?.(value);
  };

  const state = useMemo(() => {
    return open ? 'expanded' : 'collapsed';
  }, [open]);

  const { root } = layoutVariants({ size, variant });
  const mergedCls = cn(root(), props.className);

  const style = useMemo(() => {
    const sidebarWidthValue = (sidebarWidth * themeSizeRatio[size]) / 16;
    const collapsedSidebarWidthValue = (collapsedSidebarWidth * themeSizeRatio[size]) / 16;
    return {
      '--skyroc-sidebar-width': `${sidebarWidthValue}rem`,
      '--skyroc-collapsed-sidebar-width': `${collapsedSidebarWidthValue}rem`
    } as CSSProperties;
  }, [sidebarWidth, collapsedSidebarWidth, size]);

  const dataCollapsible = useMemo(() => {
    return state === 'collapsed' ? collapsible : '';
  }, [collapsible, state]);

  const renderChildren = typeof children === 'function' ? children({ open }) : children;

  return (
    <LayoutContext.Provider
      value={{
        collapsedSidebarWidth,
        isMobile,
        onOpenChange: handleOpenChange,
        onOpenMobileChange: (value: boolean) => {
          setOpenMobile(value);
        },
        open,
        openMobile,
        sidebarWidth,
        state,
        toggleSidebar: () => {
          if (isMobile) {
            setOpenMobile(!openMobile);
          }
          else {
            handleOpenChange(!open);
          }
        }
      }}
    >
      <div
        className={mergedCls}
        data-collapsible={dataCollapsible}
        data-side={side}
        data-state={state}
        data-variant={variant}
        style={style}
      >
        {renderChildren}
      </div>
    </LayoutContext.Provider>
  );
};

export default LayoutRoot;

import { cn } from '@/lib/utils';
import LayoutMobile from './LayoutMobile';
import { useLayoutContext } from './context';
import { layoutVariants } from './layout-variants';
import type { LayoutSidebarProps } from './types';

const LayoutSidebar = ({
  children,
  collapsible = 'offcanvas',
  side = 'left',
  size,
  variant = 'sidebar',
  ...rest
}: LayoutSidebarProps) => {
  const { className, ui } = rest;
  const { collapsedSidebarWidth, isMobile, open } = useLayoutContext();
  const { sidebar, sidebarGapHandler, sidebarRoot, sidebarWrapper } = layoutVariants({ collapsible, side, variant });
  const mergedCls = {
    cls: cn(sidebar(), className || ui?.sidebar),
    gapHandler: cn(sidebarGapHandler(), ui?.sidebarGapHandler),
    root: cn(sidebarRoot(), ui?.sidebarRoot),
    wrapper: cn(sidebarWrapper(), ui?.sidebarWrapper)
  };

  const childrenProps = {
    collapsed: !open,
    collapsedWidth: collapsedSidebarWidth,
    size
  };

  const renderChildren = typeof children === 'function' ? children(childrenProps) : children;

  return isMobile
    ? (
      <LayoutMobile
        className={className}
        rootClassName={ui?.mobileRoot}
        side={side}
      >
        {renderChildren}
      </LayoutMobile>
    )
    : (
      <div className={mergedCls.root}>
        <div className={mergedCls.gapHandler} />

        <div className={mergedCls.wrapper}>
          <div
            className={mergedCls.cls}
            data-sidebar="sidebar"
          >
            {renderChildren}
          </div>
        </div>
      </div>
    );
};

export default LayoutSidebar;

import { cn } from '@/lib/utils';
import { layoutVariants } from './layout-variants';
import type { LayoutTabProps } from './types';

const LayoutTab = (props: LayoutTabProps) => {
  const { className, ...rest } = props;

  const { tab } = layoutVariants();

  const mergedClass = cn(tab(), className);

  return (
    <div
      className={mergedClass}
      {...rest}
    />
  );
};

export default LayoutTab;

import { PanelLeft } from 'lucide-react';
import { cn } from '@/lib/utils';
import { ButtonIcon } from '../button';
import { useLayoutContext } from './context';
import { layoutVariants } from './layout-variants';
import type { LayoutTriggerProps } from './types';

const LayoutTrigger = (props: LayoutTriggerProps) => {
  const { className, ...rest } = props;
  const { trigger } = layoutVariants();
  const mergedCls = cn(trigger(), className);
  const { toggleSidebar } = useLayoutContext();
  return (
    <ButtonIcon
      className={mergedCls}
      {...rest}
      onClick={toggleSidebar}
    >
      <PanelLeft />
    </ButtonIcon>
  );
};

export default LayoutTrigger;

import LayoutFooter from './LayoutFooter';
import LayoutHeader from './LayoutHeader';
import LayoutMain from './LayoutMain';
import LayoutRail from './LayoutRail';
import LayoutRoot from './LayoutRoot';
import LayoutSidebar from './LayoutSidebar';
import LayoutTab from './LayoutTab';
import type { LayoutProps } from './types';

const LayoutUI = ({
  children,
  collapsedSidebarWidth,
  collapsible = 'offcanvas',
  defaultOpen,
  footer,
  header,
  onOpenChange,
  open,
  side = 'left',
  sidebar,
  sidebarWidth,
  size = 'md',
  tab,
  ui,
  variant = 'sidebar'
}: LayoutProps) => {
  return (
    <LayoutRoot
      className={ui?.root}
      collapsedSidebarWidth={collapsedSidebarWidth}
      collapsible={collapsible}
      defaultOpen={defaultOpen}
      open={open}
      side={side}
      sidebarWidth={sidebarWidth}
      size={size}
      variant={variant}
      onOpenChange={onOpenChange}
    >
      <LayoutSidebar
        collapsible={collapsible}
        side={side}
        size={size}
        ui={ui}
        variant={variant}
      >
        {typeof sidebar === 'function'
          ? props => (
            <>
              {sidebar(props)}

              <LayoutRail
                className={ui?.rail}
                collapsible={collapsible}
                side={side}
                variant={variant}
              />
            </>
          )
          : (
            <>
              {sidebar}

              <LayoutRail
                className={ui?.rail}
                collapsible={collapsible}
                side={side}
                variant={variant}
              />
            </>
          )}
      </LayoutSidebar>

      <LayoutMain
        className={ui?.main}
        collapsible={collapsible}
        variant={variant}
      >
        <LayoutHeader className={ui?.header}>{header}</LayoutHeader>
        {tab ? <LayoutTab className={ui?.tab}>{tab}</LayoutTab> : null}
        {children}
        {footer ? <LayoutFooter className={ui?.footer}>{footer}</LayoutFooter> : null}
      </LayoutMain>
    </LayoutRoot>
  );
};

LayoutUI.displayName = 'LayoutUI';

export default LayoutUI;

'use client';

import { createContext, useContext } from 'react';
import type { LayoutContextType } from './types';

export const LayoutContext = createContext<LayoutContextType | null>(null);

export const useLayoutContext = () => {
  const context = useContext(LayoutContext);
  if (!context) {
    throw new Error('useLayoutContext must be used within a <LayoutProvider />');
  }
  return context;
};

export { default as Layout } from './Layout';
export { default as LayoutFooter } from './LayoutFooter';
export { default as LayoutHeader } from './LayoutHeader';
export { default as LayoutMain } from './LayoutMain';
export { default as LayoutMobile } from './LayoutMobile';
export { default as LayoutRail } from './LayoutRail';
export { default as LayoutRoot } from './LayoutRoot';
export { default as LayoutSidebar } from './LayoutSidebar';
export { default as LayoutTab } from './LayoutTab';
export { default as LayoutTrigger } from './LayoutTrigger';

export * from './types';
export * from './layout-variants';

// @unocss-include
import { tv } from 'tailwind-variants';
import type { VariantProps } from 'tailwind-variants';

export const layoutVariants = tv({
  slots: {
    root: 'group relative flex w-full min-h-full data-[side=right]:flex-row-reverse',
    sidebarRoot: 'hidden md:block',
    sidebarWrapper: `absolute inset-y-0 z-10 hidden h-full w-[var(--skyroc-sidebar-width)] transition-[left,right,width,opacity] duration-200 ease-linear md:flex`,
    sidebar: [
      `flex flex-col size-full bg-sidebar-background`,
      `group-data-[variant=floating]:rounded-lg group-data-[variant=floating]:border group-data-[variant=floating]:border-border group-data-[variant=floating]:border-solid group-data-[variant=floating]:shadow`
    ],
    sidebarGapHandler: [
      `relative h-full w-[var(--skyroc-sidebar-width)] bg-transparent transition-width duration-200 ease-linear`,
      `group-data-[collapsible=offcanvas]:w-0`,
      `group-data-[side=right]:rotate-180`
    ],
    mobileRoot: 'w-[var(--skyroc-sidebar-width)] bg-sidebar-background p-0 [&>button]:hidden',
    mobileOverlay: [
      `fixed inset-0 z-50 bg-black/80`,
      `data-[state=open]:animate-in data-[state=open]:fade-in-0`,
      `data-[state=closed]:animate-out data-[state=closed]:fade-out-0`
    ],
    mobile: 'flex flex-col size-full',
    rail: [
      'absolute inset-y-0 z-20 hidden w-[var(--skyroc-layout-spacing)] -translate-x-1/2 transition-all ease-linear sm:flex',
      `after:absolute after:inset-y-0 after:left-1/2 after:content-[''] after:w-[calc(var(--skyroc-layout-spacing)/8)] hover:after:bg-sidebar-border`
    ],
    trigger: '',
    main: 'relative flex flex-1 flex-col items-stretch min-h-full bg-background',
    header: 'relative flex items-center shrink-0',
    tab: 'shrink-0',
    footer: 'shrink-0'
  },
  variants: {
    size: {
      'xs': {
        root: 'text-2xs [--skyroc-layout-spacing:0.75rem]'
      },
      'sm': {
        root: 'text-xs [--skyroc-layout-spacing:0.875rem]'
      },
      'md': {
        root: 'text-sm [--skyroc-layout-spacing:1rem]'
      },
      'lg': {
        root: 'text-base [--skyroc-layout-spacing:1.25rem]'
      },
      'xl': {
        root: 'text-lg [--skyroc-layout-spacing:1.5rem]'
      },
      '2xl': {
        root: 'text-xl [--skyroc-layout-spacing:1.75rem]'
      }
    },
    variant: {
