'use client';

import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import { ListUI } from './ListUI';
import type { ListProps } from './types';

const List = forwardRef<HTMLUListElement, ListProps>((props, ref) => {
  const config = useComponentConfig('list');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <ListUI
      {...mergedProps}
      ref={ref}
    />
  );
});

List.displayName = 'List';

export default List;

import { cn } from '@/lib/utils';
import { listVariants } from './list-variants';
import type { ListContentProps } from './types';

export const ListContent = (props: ListContentProps) => {
  const { children, className, size, ...rest } = props;

  const { content } = listVariants({ size });

  return (
    <div
      className={cn(content(), className)}
      data-slot="list-content"
      {...rest}
    >
      {children}
    </div>
  );
}
;

import { cn } from '@/lib/utils';
import { listVariants } from './list-variants';
import type { ListDescriptionProps } from './types';

export const ListDescription = (props: ListDescriptionProps) => {
  const { children, className, size, ...rest } = props;

  const { description } = listVariants({ size });

  return (
    <p
      className={cn(description(), className)}
      data-slot="list-description"
      {...rest}
    >
      {children}
    </p>
  );
}
;

ListDescription.displayName = 'ListDescription';

import React from 'react';
import { cn } from '@/lib/utils';
import { listVariants } from './list-variants';
import type { ListItemProps } from './types';

export const ListItem = (props: ListItemProps) => {
  const { children, className, size, ...rest } = props;

  const { item } = listVariants({ size });

  return (
    <li
      className={cn(item(), className)}
      data-slot="list-item"
      {...rest}
    >
      {children}
    </li>
  );
}
;

ListItem.displayName = 'ListItem';

import { isValidElement } from 'react';
import { cn } from '@/lib/utils';
import { Divider } from '../divider';
import { ListContent } from './ListContent';
import { ListDescription } from './ListDescription';
import { ListItem } from './ListItem';
import { ListTitle } from './ListTitle';
import { listVariants } from './list-variants';
import type { ListItemUIProps } from './types';

export const ListItemUI = (props: ListItemUIProps) => {
  const {
    className,
    classNames,
    content,
    contentProps,
    description,
    descriptionProps,
    divider,
    leading,
    size,
    title,
    titleProps,
    trailing,
    ...rest
  } = props;

  const { item } = listVariants({ size });

  return (
    <>
      <ListItem
        className={cn(item(), className, classNames?.item)}
        size={size}
        {...rest}
      >
        {leading}

        <ListContent
          className={classNames?.content}
          size={size}
          {...contentProps}
        >

          {title
            ? (
              <ListTitle
                className={classNames?.title}
                size={size}
                {...titleProps}
              >
                {title}
              </ListTitle>
            )
            : null}

          {description
            ? (
              <ListDescription
                className={classNames?.description}
                size={size}
                {...descriptionProps}
              >
                {description}
              </ListDescription>
            )
            : null}

          {content}
        </ListContent>

        {trailing}

      </ListItem>

      {isValidElement(divider) ? divider : divider ? <Divider /> : null}
    </>
  );
};

import React from 'react';
import { cn } from '@/lib/utils';
import { listVariants } from './list-variants';
import type { ListRootProps } from './types';

export const ListRoot = React.forwardRef<HTMLUListElement, ListRootProps>(
  (props, ref) => {
    const { children, className, size, ...rest } = props;

    const { root } = listVariants({ size });

    return (
      <ul
        className={cn(root(), className)}
        data-slot="list-root"
        ref={ref}
        {...rest}
      >
        {children}
      </ul>
    );
  }
);

ListRoot.displayName = 'ListRoot';

import React from 'react';
import { cn } from '@/lib/utils';
import { listVariants } from './list-variants';
import type { ListTitleProps } from './types';

export const ListTitle = (props: ListTitleProps) => {
  const { children, className, size, ...rest } = props;

  const { title } = listVariants({ size });

  return (
    <h3
      className={cn(title(), className)}
      data-slot="list-title"
      {...rest}
    >
      {children}
    </h3>
  );
};

import React from 'react';
import { ListRoot } from './ListRoot';
import type { ListProps } from './types';
import { ListItemUI } from './ListItemUI';

export const ListUI = React.forwardRef<HTMLUListElement, ListProps>(
  (props, ref) => {
    const { children, className, classNames, leading, trailing, items, size, contentProps, titleProps, descriptionProps, ...rest } = props;

    const length = items.length;

    return (
      <ListRoot
        className={className || classNames?.root}
        ref={ref}
        size={size}
        {...rest}
      >
        {items.map((item, index) => (
          <ListItemUI
            classNames={classNames}
            contentProps={contentProps}
            descriptionProps={descriptionProps}
            divider={index !== length - 1}
            key={String(index)}
            leading={leading}
            size={size}
            titleProps={titleProps}
            trailing={trailing}
            {...item}
          />
        ))}
      </ListRoot>
    );
  }
);

ListUI.displayName = 'ListUI';

export { default as List } from './List';
export * from './ListContent';
export * from './ListDescription';
export * from './ListItem';
export * from './ListItemUI';
export * from './ListRoot';
export * from './ListTitle';
export * from './ListUI';

export * from './types';

import { tv } from 'tailwind-variants';

export const listVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    content: 'flex flex-col grow',
    description: '[&_p]:leading-relaxed text-muted-foreground',
    item: 'flex list-none',
    root: 'flex flex-col cursor-pointer',
    title: 'font-medium tracking-tight'
  },
  variants: {
    size: {
      '2xl': {
        content: 'gap-2',
        item: 'gap-7',
        root: 'gap-7 text-xl',
        title: 'text-2xl leading-[calc(var(--spacing)*6.25)]'
      },
      'lg': {
        content: 'gap-1.25',
        item: 'gap-5',
        root: 'gap-5 text-base',
        title: 'text-lg leading-[calc(var(--spacing)*5)]'
      },
      'md': {
        content: 'gap-1',
        item: 'gap-4',
        root: 'gap-4 text-sm',
        title: 'text-base leading-[calc(var(--spacing)*4.375)]'
      },
      'sm': {
        content: 'gap-1',
        item: 'gap-3',
        root: 'gap-3 text-xs',
        title: 'text-sm leading-[calc(var(--spacing)*3.75)]'
      },
      'xl': {
        content: 'gap-1.5',
        item: 'gap-6',
        root: 'gap-6 text-lg',
        title: 'text-xl leading-[calc(var(--spacing)*5.625)]'
      },
      'xs': {
        content: 'gap-0.75',
        item: 'gap-2',
        root: 'gap-2 text-2xs',
        title: 'text-xs leading-[calc(var(--spacing)*3.125)]'
      }
    }
  }
});

export type ListSlots = keyof typeof listVariants.slots;

import type { ComponentPropsWithRef, ReactNode } from 'react';
import type { ClassValue, SlotProps, StyledComponentProps, ThemeSize } from '@/types/shared';
import type { ListSlots } from './list-variants';

/**
 * Props for the List root component.
 * Main container for list items.
 */
export interface ListRootProps extends StyledComponentProps<ComponentPropsWithRef<'ul'>> {
  /**
   * The size variant of the list affecting spacing and typography.
   */
  size?: ThemeSize;
}

/**
 * Props for the List item component.
 * Individual list item container.
 */
export interface ListItemProps extends StyledComponentProps<ComponentPropsWithRef<'li'>> {
  /**
   * The size variant of the list item affecting spacing.
   */
  size?: ThemeSize;
}

/**
 * Props for the List content component.
 * Container for the main content area within a list item.
 */
export interface ListContentProps extends StyledComponentProps<ComponentPropsWithRef<'div'>> {
  /**
   * The size variant of the content affecting spacing.
   */
  size?: ThemeSize;
}

/**
 * Props for the List title component.
 * Displays the title text within a list item.
 */
export interface ListTitleProps extends StyledComponentProps<ComponentPropsWithRef<'h3'>> {
  /**
   * The size variant of the title text.
   */
  size?: ThemeSize;
}

/**
 * Props for the List description component.
 * Displays the description text within a list item.
 */
export interface ListDescriptionProps extends StyledComponentProps<ComponentPropsWithRef<'p'>> {
  /**
   * The size variant of the description text.
   */
  size?: ThemeSize;
}

/**
 * Class names for different slots in the list component.
 * Allows customizing styles for specific parts of the list.
 */
export type ListUi = Partial<Record<ListSlots, ClassValue>>;

/**
 * Props for the main List component.
 * A flexible list component that combines root, item, content, title, and description sections.
 *
 * @example
 * ```jsx
 * <List size="md">
 *   <ListItem
 *     title="Item Title"
 *     description="Item description"
 *     leading={<Icon />}
 *     trailing={<ChevronIcon />}
 *   >
 *     Additional content
 *   </ListItem>
 * </List>
 * ```
 */
export interface ListProps extends ListRootProps, Pick<ListItemUIProps, 'contentProps' | 'titleProps' | 'descriptionProps'>, SlotProps {
  /**
   * Class names for customizing different parts of the list.
   */
  classNames?: ListUi;

  /**
   * Array of list items to render.
   */
  items: ListItemUIProps[];
}

/**
 * Props for the ListItemUI component.
 * A composite list item with leading, content, and trailing areas.
 */
export interface ListItemUIProps extends Omit<ListItemProps, 'title' | 'content' | 'children'> {
  /**
   * Class names for customizing different parts of the list item.
   */
  classNames?: ListUi;
  /**
   * Content displayed at the start of the list item (e.g., avatar, thumbnail).
   */
  leading?: ReactNode;
  /**
   * Content displayed at the end of the list item (e.g., action buttons, badge).
   */
  trailing?: ReactNode;
  /**
   * Title text for the list item.
   */
  title?: ReactNode;
  /**
   * Description text for the list item.
   */
  description?: ReactNode;
  /**
   * Main content area for the list item.
   * Displayed after title and description.
   */
  content?: ReactNode;
  /**
   * Custom divider element to display between list items.
   * If not provided, a default divider will be used when divider is true.
   */
  divider?: ReactNode | boolean;
  /**
   * Props for the list content component.
   */
  contentProps?: ListContentProps;
  /**
   * Props for the list title component.
   */
  titleProps?: ListTitleProps;
  /**
   * Props for the list description component.
   */
  descriptionProps?: ListDescriptionProps;
}
