import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Arrow as _Arrow } from '@radix-ui/react-menu';
import { cn } from '@/lib/utils';
import { menuVariants } from './menu-variants';
import type { MenuArrowProps } from './types';

const MenuArrow = forwardRef<ComponentRef<typeof _Arrow>, MenuArrowProps>((props, ref) => {
  const { className, component: Arrow = _Arrow, ...rest } = props;

  const { arrow } = menuVariants();

  const mergedCls = cn(arrow(), className);

  return (
    <Arrow
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

MenuArrow.displayName = 'MenuArrow';

export default MenuArrow;

'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Group as _Group } from '@radix-ui/react-menu';
import { cn } from '@/lib';
import _MenuCheckboxItem from './MenuCheckboxItem';
import MenuLabel from './MenuLabel';
import MenuSeparator from './MenuSeparator';
import { isLabel, isSeparator } from './shared';
import type { MenuCheckboxGroupProps } from './types';

const MenuCheckboxGroup = forwardRef<ComponentRef<typeof _Group>, MenuCheckboxGroupProps>((props, ref) => {
  const {
    checks,
    className,
    classNames,
    component: MenuCheckboxItem = _MenuCheckboxItem,
    groupComponent: Group = _Group,
    items,
    labelComponent: Label = MenuLabel,
    onChecksChange,
    separatorComponent: Separator = MenuSeparator,
    size,
    ...rest
  } = props;

  return (
    <Group
      className={cn(className || classNames?.group)}
      ref={ref}
      {...rest}
    >
      {items.map((item, index) => {
        if (isLabel(item)) {
          return (
            <Label
              classNames={classNames}
              key={String(index)}
              size={size}
              {...item}
            >
              {item.label}
            </Label>
          );
        }

        if (isSeparator(item)) {
          return (
            <Separator
              className={classNames?.separator}
              key={String(index)}
              size={size}
            />
          );
        }

        return (
          <MenuCheckboxItem
            key={String(index)}
            {...item}
            checked={item.checked || checks?.includes(item?.value || '')}
            classNames={classNames}
            size={size}
            onCheckedChange={(checked) => {
              item.onCheckedChange?.(checked);

              if (checked) {
                onChecksChange?.([...(checks || []), item?.value || '']);
              }
              else {
                onChecksChange?.([...(checks || []).filter(check => check !== item?.value)]);
              }
            }}
          >
            {item.label}
          </MenuCheckboxItem>
        );
      })}
    </Group>
  );
});

MenuCheckboxGroup.displayName = 'MenuCheckboxGroup';

export default MenuCheckboxGroup;

import type { ComponentRef } from 'react';
import { forwardRef, isValidElement } from 'react';
import { CheckboxItem as _CheckboxItem } from '@radix-ui/react-menu';
import { Check } from 'lucide-react';
import { withClassName } from '@/lib/compose-props';
import { cn } from '@/lib/utils';
import MenuShortcut from '../menu/MenuShortcut';
import MenuItemIndicator from './MenuItemIndicator';
import { menuVariants } from './menu-variants';
import type { MenuCheckboxItemProps } from './types';

const MenuCheckboxItem = forwardRef<ComponentRef<typeof _CheckboxItem>, MenuCheckboxItemProps>((props, ref) => {
  const {
    children,
    className,
    classNames,
    component: CheckboxItem = _CheckboxItem,
    indicatorComponent: IndicatorComponent = MenuItemIndicator,
    indicatorIcon,
    leading,
    shortcut,
    size,
    trailing,
    ...rest
  } = props;

  const { checkboxItem, itemIcon } = menuVariants({ size });

  const mergedCls = cn(checkboxItem(), className || classNames?.item);

  return (
    <CheckboxItem
      className={mergedCls}
      ref={ref}
      {...rest}
    >
      <IndicatorComponent
        className={classNames?.itemIndicator}
        size={size}
      >
        {indicatorIcon || <Check />}
      </IndicatorComponent>

      {isValidElement(leading) ? withClassName(leading, itemIcon()) : leading}
      {children}
      {trailing}

      {shortcut
        ? (
          <MenuShortcut
            className={classNames?.shortcut}
            size={size}
            value={shortcut}
          />
        )
        : null}
    </CheckboxItem>
  );
});

