import { Item } from '@radix-ui/react-menubar';

import MenuItem from '../menu/MenuItem';

import type { MenubarItemProps } from './types';

const MenubarItem = (props: MenubarItemProps) => {
  return (
    <MenuItem
      {...props}
      component={Item as typeof MenuItem}
    />
  );
};

export default MenubarItem;

import { ItemIndicator } from '@radix-ui/react-menubar';

import MenuItemIndicator from '../menu/MenuItemIndicator';

import type { MenubarItemIndicatorProps } from './types';

const MenubarItemIndicator = (props: MenubarItemIndicatorProps) => {
  return (
    <MenuItemIndicator
      component={ItemIndicator as typeof MenuItemIndicator}
      {...props}
    />
  );
};

export default MenubarItemIndicator;

import { Label } from '@radix-ui/react-menubar';

import MenuLabel from '../menu/MenuLabel';

import type { MenubarLabelProps } from './types';

const MenubarLabel = (props: MenubarLabelProps) => {
  return (
    <MenuLabel
      component={Label as typeof MenuLabel}
      {...props}
    />
  );
};

export default MenubarLabel;

import { Content, Menu, Portal } from '@radix-ui/react-menubar';

import { cn } from '@/lib/utils';

import { menuVariants } from '../menu/menu-variants';

import MenubarTrigger from './MenubarTrigger';
import type { MenubarMenuProps } from './types';

const MenubarMenu = (props: MenubarMenuProps) => {
  const { children, className, classNames, size, trigger, value, ...rest } = props;

  const { content } = menuVariants({ size });

  const mergedCls = cn(content(), className, classNames?.content);

  return (
    <Menu value={value}>
      <MenubarTrigger
        {...rest}
        classNames={classNames}
      >
        {trigger}
      </MenubarTrigger>

      <Portal>
        <Content className={mergedCls}>{children}</Content>
      </Portal>
    </Menu>
  );
};

export default MenubarMenu;

import { Sub } from '@radix-ui/react-menubar';

import MenuOption from '../menu/MenuOption';

import MenubarItem from './MenubarItem';
import MenubarLabel from './MenubarLabel';
import MenubarSeparator from './MenubarSeparator';
import MenubarSubContent from './MenubarSubContent';
import MenubarSubTrigger from './MenubarSubTrigger';
import type { MenubarOptionProps } from './types';

const MenubarOption = (props: MenubarOptionProps) => {
  return (
    <MenuOption
      {...props}
      component={MenubarItem}
      labelComponent={MenubarLabel}
      separatorComponent={MenubarSeparator}
      subComponent={Sub}
      subContentComponent={MenubarSubContent}
      subTriggerComponent={MenubarSubTrigger}
    />
  );
};

MenubarOption.displayName = 'MenubarOption';

export default MenubarOption;

import { Root } from '@radix-ui/react-menubar';
import type { ComponentRef } from 'react';
import { forwardRef } from 'react';

import { cn } from '@/lib/utils';

import { menubarVariants } from './menubar-variansts';
import type { MenubarRootProps } from './types';

const MenubarRoot = forwardRef<ComponentRef<typeof Root>, MenubarRootProps>((props, ref) => {
  const { className, size, ...rest } = props;

  const { root } = menubarVariants({ size });

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      ref={ref}
      {...rest}
    />
  );
});

MenubarRoot.displayName = 'MenubarRoot';

export default MenubarRoot;

import { Separator } from '@radix-ui/react-menubar';

import MenuSeparator from '../menu/MenuSeparator';

import type { MenubarSeparatorProps } from './types';

const MenubarSeparator = (props: MenubarSeparatorProps) => {
  return (
    <MenuSeparator
      component={Separator as typeof MenuSeparator}
      {...props}
