};

export default MenubarSubContent;

import { SubTrigger } from '@radix-ui/react-menubar';

import MenuSubTrigger from '../menu/MenuSubTrigger';

import type { MenubarSubTriggerProps } from './types';

const MenubarSubTrigger = (props: MenubarSubTriggerProps) => {
  return (
    <MenuSubTrigger
      component={SubTrigger as typeof MenuSubTrigger}
      {...props}
    />
  );
};

export default MenubarSubTrigger;

import { Trigger } from '@radix-ui/react-menubar';
import type { ComponentRef } from 'react';
import { forwardRef, isValidElement } from 'react';

import { withClassName } from '@/lib/compose-props';
import { cn } from '@/lib/utils';

import MenuShortcut from '../menu/MenuShortcut';
import { menuVariants } from '../menu/menu-variants';

import type { MenubarTriggerProps } from './types';

const MenubarTrigger = forwardRef<ComponentRef<typeof Trigger>, MenubarTriggerProps>((props, ref) => {
  const { children, className, classNames, leading, shortcut, size, trailing, ...rest } = props;

  const { item, itemIcon } = menuVariants({ size });

  const mergedCls = cn(item(), className || classNames?.trigger);

  return (
    <Trigger
      className={mergedCls}
      ref={ref}
      {...rest}
    >
      {isValidElement(leading) ? withClassName(leading, itemIcon()) : leading}

      {children}

      {trailing}

      {shortcut && (
        <MenuShortcut
          className={classNames?.shortcut}
          size={size}
          value={shortcut}
        />
      )}
    </Trigger>
  );
});

MenubarTrigger.displayName = 'MenubarTrigger';

export default MenubarTrigger;

import MenubarMenu from './MenubarMenu';
import MenubarOption from './MenubarOption';
import MenubarRoot from './MenubarRoot';
import type { MenubarProps } from './types';

const MenubarUI = (props: MenubarProps) => {
  const { classNames, items, size, ...rest } = props;

  return (
    <MenubarRoot {...rest}>
      {items.map((item, index) => {
        const { children, label, ...restItem } = item;

        return (
          <MenubarMenu
            classNames={classNames}
            key={String(index)}
            size={size}
            {...restItem}
            trigger={label}
          >
            {children &&
              children.length > 0 &&
              children.map((child, childIndex) => {
                return (
                  <MenubarOption
                    classNames={classNames}
                    item={child}
                    key={String(childIndex)}
                    size={size}
                  />
                );
              })}
          </MenubarMenu>
        );
      })}
    </MenubarRoot>
  );
};

MenubarUI.displayName = 'MenubarUI';

export default MenubarUI;

// export { default as Menubar } from './Menubar';

export { default as MenubarItem } from './MenubarItem';
export { default as MenubarLabel } from './MenubarLabel';

export { default as MenubarSeparator } from './MenubarSeparator';
export { default as MenubarSubContent } from './MenubarSubContent';
export { default as MenubarSubTrigger } from './MenubarSubTrigger';
export { default as MenubarTrigger } from './MenubarTrigger';

import { tv } from 'tailwind-variants';

export const menubarVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    root: `flex items-center rounded-md border bg-background shadow-sm`
  },
  variants: {
    size: {
      '2xl': {
        root: 'gap-2.5 p-1.25'
      },
      lg: {
        root: 'gap-1.75 p-0.875'
      },
      md: {
        root: 'gap-1.5 p-0.75'
      },
      sm: {
        root: 'gap-1.25 p-0.625 text-xs'
      },
      xl: {
        root: 'gap-2 p-1'
      },
      xs: {
        root: 'gap-1 p-0.5 text-2xs'
      }
    }
  }
});

export type MenubarSlots = keyof typeof menubarVariants.slots;

import type {
  MenubarMenuProps as _MenubarMenuProps,
  MenubarProps as _MenubarProps,
  MenubarTriggerProps as _MenubarTriggerProps
} from '@radix-ui/react-menubar';
import type { ReactNode } from 'react';

import type { StyledComponentProps, ClassValue, ThemeSize } from '@/types/shared';

import type {
  MenuClassNames,
  MenuCommonProps,
  MenuItemIndicatorProps,
  MenuItemProps,
  MenuLabelProps,
  MenuOptionProps,
  MenuSeparatorProps,
  MenuSubContentProps,
  MenuSubTriggerProps
} from '../menu/types';

/**
 * Props for a menu in the menubar.
 * Represents a top-level menu that opens when triggered.
 *
 * @example
 * <MenubarMenu trigger="File" size="md">
 *   <MenuItem>New</MenuItem>
 *   <MenuItem>Open</MenuItem>
 *   <MenuSeparator />
 *   <MenuItem>Exit</MenuItem>
 * </MenubarMenu>
 */
export interface MenubarMenuProps extends Omit<MenubarTriggerProps, 'children' | 'classNames'> {
  /** Menu content (items, labels, separators, etc.) */
  children: ReactNode;
  /** Class names for customizing menubar menu slots */
  classNames?: Pick<MenuClassNames, 'content' | 'shortcut'> & {
    trigger?: ClassValue;
  };
  /** Size variant for the menu */
  size?: ThemeSize;
  /** Content to display in the trigger button */
  trigger: ReactNode;
  /** Unique identifier for the menu */
  value?: string;
}

/**
 * Props for a menubar item.
 * A clickable item within a menubar menu.
 */
export type MenubarItemProps = Omit<MenuItemProps, 'component'>;

/**
 * Props for a menubar label.
 * A non-interactive label within a menubar menu.
 */
export type MenubarLabelProps = Omit<MenuLabelProps, 'component'>;

/**
 * Props for rendering a menubar option from data.
 * Used internally to render menu items dynamically.
 */
export type MenubarOptionProps = Omit<
  MenuOptionProps,
  'component' | 'labelComponent' | 'separatorComponent' | 'subComponent' | 'subContentComponent' | 'subTriggerComponent'
>;

/**
 * Props for a menubar item indicator.
 * Renders an indicator (checkbox, radio, etc.) within a menubar item.
 */
export type MenubarItemIndicatorProps = Omit<MenuItemIndicatorProps, 'component'>;

/**
 * Props for a menubar separator.
 * A visual divider within a menubar menu.
 */
export type MenubarSeparatorProps = Omit<MenuSeparatorProps, 'component'>;

/**
 * Props for menubar submenu content.
 * Container for nested menu items.
 */
export type MenubarSubContentProps = Omit<MenuSubContentProps, 'component' | 'groupComponent' | 'portalComponent'>;

/**
 * Props for a menubar submenu trigger.
 * A menubar item that opens a nested submenu.
 */
export type MenubarSubTriggerProps = Omit<MenuSubTriggerProps, 'component'>;

/**
 * Props for a menubar trigger button.
 * The clickable button that opens a menubar menu.
 *
 * @example
 * <MenubarTrigger shortcut="Cmd+N">
 *   New File
 * </MenubarTrigger>
 */
export interface MenubarTriggerProps extends StyledComponentProps<_MenubarTriggerProps> {
  /** Class names for customizing trigger slots */
  classNames?: Pick<MenuClassNames, 'shortcut'> & {
    trigger?: ClassValue;
  };
  /** Content to display before the trigger text */
  leading?: ReactNode;
  /** Keyboard shortcut to display */
  shortcut?: string | string[];
  /** Content to display after the trigger text */
  trailing?: ReactNode;
}

/**
 * Data structure for a menubar menu option.
 * Represents a single top-level menu in the menubar.
 */
type MenubarOption = Omit<MenubarMenuProps, 'children' | 'classNames' | 'trigger'> & {
  /** Menu items to render */
  children: MenubarOptionProps['item'][];
  /** Display text for the menu trigger */
  label: ReactNode;
};

/**
 * Props for the menubar root component.
 * Main container for the menubar.
 */
export interface MenubarRootProps extends StyledComponentProps<_MenubarProps> {}

/**
 * Props for the main Menubar component.
 * A horizontal menu bar containing multiple menus.
 * Typically used for application navigation and actions.
 *
 * @example
 * <Menubar
 *   items={[
 *     {
 *       label: 'File',
 *       children: [
 *         { type: 'item', label: 'New' },
 *         { type: 'item', label: 'Open' },
 *         { type: 'separator' },
 *         { type: 'item', label: 'Exit' }
 *       ]
 *     },
 *     {
 *       label: 'Edit',
 *       children: [
 *         { type: 'item', label: 'Undo' },
 *         { type: 'item', label: 'Redo' }
 *       ]
 *     }
 *   ]}
 * />
 */
export interface MenubarProps extends Omit<_MenubarProps, 'children'>, Omit<MenuCommonProps, 'classNames'> {
  /** Class names for customizing menubar slots */
  classNames?: MenuCommonProps['classNames'] & {
    trigger?: ClassValue;
  };
  /** Array of menu definitions to render */
  items: MenubarOption[];
}
