'use client';

import NavigationMenuUI from './NavigationMenuUI';
import type { NavigationMenuProps } from './types';

const NavigationMenu = (props: NavigationMenuProps) => {
  return (
    <NavigationMenuUI
      {...props}
    />
  );
};

NavigationMenu.displayName = 'NavigationMenu';

export default NavigationMenu;

import { isValidElement } from 'react';
import { NavigationMenuLink } from '@radix-ui/react-navigation-menu';
import { ArrowUpRight } from 'lucide-react';
import { withClassName } from '@/lib/compose-props';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuChildLinkProps } from './types';

const NavigationMenuChildLink = (props: NavigationMenuChildLinkProps) => {
  const { children, disabled, className, classNames, component: Component = 'a', description, leading, size, trailing, ...rest } = props;

  const { subLink, subLinkContent, subLinkDescription, itemIcon, linkIcon, subLinkLabel } = navigationMenuVariants({
    size
  });

  const mergedCls = {
    cls: cn(subLink(), className || classNames?.subLink),
    content: cn(subLinkContent(), classNames?.subLinkContent),
    description: cn(subLinkDescription(), classNames?.subLinkDescription),
    icon: cn(itemIcon(), classNames?.itemIcon),
    linkIcon: cn(linkIcon(), classNames?.linkIcon),
    label: cn(subLinkLabel(), classNames?.subLinkLabel)
  };

  return (
    <NavigationMenuLink
      asChild
      className={mergedCls.cls}
      data-disabled={disabled ? '' : undefined}
      data-slot="navigation-menu-child-link"
      {...rest}
    >
      <Component>
        {isValidElement(leading) ? withClassName(leading, mergedCls.icon) : leading}

        <div className={mergedCls.content}>
          <span className={mergedCls.label}>{children}</span>
          {description ? <p className={mergedCls.description}>{description}</p> : null}
        </div>

        {trailing || <ArrowUpRight className={mergedCls.icon} />}
      </Component>
    </NavigationMenuLink>
  );
};

export default NavigationMenuChildLink;

import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuChildListProps } from './types';

const NavigationMenuChildList = (props: NavigationMenuChildListProps) => {
  const { className, size, ...rest } = props;

  const { subList } = navigationMenuVariants({ size });

  const mergedCls = cn(subList(), className);

  return (
    <ul
      className={mergedCls}
      data-slot="navigation-menu-child-list"
      {...rest}
    />
  );
};

export default NavigationMenuChildList;

import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuChildListItemProps } from './types';

const NavigationMenuChildListItem = (props: NavigationMenuChildListItemProps) => {
  const { className, ...rest } = props;

  const { subItem } = navigationMenuVariants();

  const mergedCls = cn(subItem(), className);

  return (
    <li
      className={mergedCls}
      data-slot="navigation-menu-child-list-item"
      {...rest}
    />
  );
};

export default NavigationMenuChildListItem;

import { Content } from '@radix-ui/react-navigation-menu';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuContentProps } from './types';

const NavigationMenuContent = (props: NavigationMenuContentProps) => {
  const { className, ...rest } = props;

  const { content } = navigationMenuVariants();

  const mergedCls = cn(content(), className);

  return (
    <Content
      className={mergedCls}
      data-slot="navigation-menu-content"
      {...rest}
    />
  );
};

export default NavigationMenuContent;

import { Indicator } from '@radix-ui/react-navigation-menu';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuIndicatorProps } from './types';

const NavigationMenuIndicator = (props: NavigationMenuIndicatorProps) => {
  const { className, classNames, size, ...rest } = props;

  const { arrow, indicator } = navigationMenuVariants({ size });

  const mergedCls = cn(indicator(), className);

  const mergedArrowCls = cn(arrow(), classNames?.arrow);

  return (
    <Indicator
      className={mergedCls}
      data-slot="navigation-menu-indicator"
      {...rest}
    >
      <div
        className={mergedArrowCls}
        data-slot="navigation-menu-indicator-arrow"
      />
    </Indicator>
  );
};

export default NavigationMenuIndicator;

import { Item } from '@radix-ui/react-navigation-menu';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuItemProps } from './types';

const NavigationMenuItem = (props: NavigationMenuItemProps) => {
  const { className, ...rest } = props;

  const { item } = navigationMenuVariants();

  const mergedCls = cn(item(), className);

  return (
    <Item
      className={mergedCls}
      data-slot="navigation-menu-item"
      {...rest}
    />
  );
};

export default NavigationMenuItem;

import { isValidElement } from 'react';
import { Link } from '@radix-ui/react-navigation-menu';
import { ArrowUpRight } from 'lucide-react';
import { withClassName } from '@/lib/compose-props';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuLinkProps } from './types';

const NavigationMenuLink = (props: NavigationMenuLinkProps) => {
  const { children, disabled, className, classNames, component: Component = 'a', leading, size, trailing, ...rest } = props;

  const { itemIcon, link, linkIcon } = navigationMenuVariants({ size });

  const mergedCls = {
    cls: cn(link(), className || classNames?.link),
    icon: cn(itemIcon(), classNames?.itemIcon),
    linkIcon: cn(linkIcon(), classNames?.linkIcon)
  };

  return (
    <Link
      asChild
      className={mergedCls.cls}
      data-disabled={disabled ? '' : undefined}
      data-slot="navigation-menu-link"
      {...rest}
    >
      <Component>
        {isValidElement(leading) ? withClassName(leading, mergedCls.icon) : leading}

        <span
          data-slot="navigation-menu-link-label"
        >
          {children}
        </span>

        {trailing || <ArrowUpRight className={mergedCls.linkIcon} />}
      </Component>
    </Link>
  );
};

export default NavigationMenuLink;

import { List } from '@radix-ui/react-navigation-menu';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuListProps } from './types';

const NavigationMenuList = (props: NavigationMenuListProps) => {
  const { className, size, ...rest } = props;

  const { list } = navigationMenuVariants({ size });

  const mergedCls = cn(list(), className);

  return (
    <List
      {...rest}
      className={mergedCls}
      data-size={size}
      data-slot="navigation-menu-list"
