export default NavigationMenuList;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root } from '@radix-ui/react-navigation-menu';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuRootProps } from './types';

const NavigationMenuRoot = forwardRef<ComponentRef<typeof Root>, NavigationMenuRootProps>((props, ref) => {
  const { className, ...rest } = props;

  const { root } = navigationMenuVariants();

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      data-slot="navigation-menu-root"
      ref={ref}
      {...rest}
    />
  );
});

NavigationMenuRoot.displayName = 'NavigationMenuRoot';

export default NavigationMenuRoot;

import { Trigger } from '@radix-ui/react-navigation-menu';
import { ChevronDown } from 'lucide-react';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuTriggerProps } from './types';

const NavigationMenuTrigger = (props: NavigationMenuTriggerProps) => {
  const { children, className, size, classNames, leading, trailing, ...rest } = props;

  const { trigger, triggerIcon } = navigationMenuVariants({ size });

  const mergedCls = {
    cls: cn(trigger(), className || classNames?.trigger),
    triggerIcon: cn(triggerIcon(), classNames?.triggerIcon)
  };

  return (
    <Trigger
      {...rest}
      className={mergedCls.cls}
      data-slot="navigation-menu-trigger"
    >
      {leading}
      <span data-slot="navigation-menu-trigger-labe">{children}</span>

      {trailing || (
        <ChevronDown
          aria-hidden="true"
          className={mergedCls.triggerIcon}
        />
      )}
    </Trigger>
  );
};

export default NavigationMenuTrigger;

import NavigationMenuChildLink from './NavigationMenuChildLink';
import NavigationMenuChildList from './NavigationMenuChildList';
import NavigationMenuChildListItem from './NavigationMenuChildListItem';
import NavigationMenuContent from './NavigationMenuContent';
import NavigationMenuIndicator from './NavigationMenuIndicator';
import NavigationMenuItem from './NavigationMenuItem';
import NavigationMenuLink from './NavigationMenuLink';
import NavigationMenuList from './NavigationMenuList';
import NavigationMenuRoot from './NavigationMenuRoot';
import NavigationMenuTrigger from './NavigationMenuTrigger';
import NavigationMenuViewport from './NavigationMenuViewport';
import type { NavigationMenuItemChildOption, NavigationMenuItemOption, NavigationMenuLinkBaseOption, NavigationMenuProps, NavigationMenuTriggerProps } from './types';

function isLink(item: NavigationMenuItemOption): item is NavigationMenuLinkBaseOption {
  return item.type === 'link' || !item.children;
}

function getItem(itemOption: NavigationMenuItemOption): [NavigationMenuItemOption, NavigationMenuItemChildOption[]] {
  if (isLink(itemOption)) {
    return [itemOption, []];
  }

  const { children = [], ...item } = itemOption;

  return [item, children];
}

const NavigationMenuUI = (props: NavigationMenuProps) => {
  const { classNames, items, showArrow, itemProps, linkProps, listProps, triggerProps, contentProps, childListProps, childListItemProps, childLinkProps, size, ...rest } = props;

  return (
    <NavigationMenuRoot
      {...rest}
    >
      <NavigationMenuList
        className={classNames?.list}
        size={size}
        {...listProps}
      >
        {items.map((itemOption, index) => {
          const itemKey = String(index);

          const [item, children] = getItem(itemOption);

          return (
            <NavigationMenuItem
              className={classNames?.item}
              key={itemKey}
              {...itemProps}
            >
              {isLink(itemOption)
                ? (
                  <NavigationMenuLink
                    classNames={classNames}
                    size={size}
                    {...linkProps}
                    {...item as Omit<NavigationMenuLinkBaseOption, 'children'>}
                  >
                    {item.label}
                  </NavigationMenuLink>
                )
                : (
                  <>
                    <NavigationMenuTrigger
                      classNames={classNames}
                      size={size}
                      {...triggerProps}
                      {...item as Omit<NavigationMenuTriggerProps, 'children'>}
                    >
                      {item.label}
                    </NavigationMenuTrigger>

                    <NavigationMenuContent
                      className={classNames?.content}
                      {...contentProps}
                    >
                      <NavigationMenuChildList
                        className={classNames?.subList}
                        size={size}
                        {...childListProps}
                      >
                        {children && children.length > 0
                          ? children.map((child, childIndex) => {
                            // Child items should be link type
                            const childItem = child;

                            return (
                              <NavigationMenuChildListItem
                                className={classNames?.subItem}
                                key={String(childIndex)}
                                {...childListItemProps}
                              >
                                <NavigationMenuChildLink
                                  classNames={classNames}
                                  size={size}
                                  {...childItem}
                                >
                                  {childItem.label}
                                </NavigationMenuChildLink>
                              </NavigationMenuChildListItem>
                            );
                          })
                          : null}
                      </NavigationMenuChildList>
                    </NavigationMenuContent>
                  </>
                )}
            </NavigationMenuItem>
          );
        })}
      </NavigationMenuList>

      <NavigationMenuIndicator
        classNames={classNames}
        size={size}
      />

      <NavigationMenuViewport
        classNames={classNames}
        size={size}
      />

    </NavigationMenuRoot>
  );
};

NavigationMenuUI.displayName = 'NavigationMenuUI';

export default NavigationMenuUI;

import { Viewport } from '@radix-ui/react-navigation-menu';
import { cn } from '@/lib/utils';
import { navigationMenuVariants } from './navigation-menu';
import type { NavigationMenuViewportProps } from './types';

const NavigationMenuViewport = (props: NavigationMenuViewportProps) => {
  const { className, classNames, size, ...rest } = props;

  const { viewport, viewportRoot } = navigationMenuVariants({ size });

  const mergedCls = {
    cls: cn(viewport(), className || classNames?.viewport),
    root: cn(viewportRoot(), classNames?.viewportRoot)
  };

  return (
    <div
      className={mergedCls.root}
      data-slot="navigation-menu-viewport-root"
    >

      <Viewport
        className={mergedCls.cls}
        data-slot="navigation-menu-viewport"
        {...rest}
      />
    </div>
  );
};

export default NavigationMenuViewport;

export { default as NavigationMenu } from './NavigationMenu';

