'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import PaginationUI from './PaginationUI';
import type { PaginationProps } from './types';

const Pagination = forwardRef<ComponentRef<'nav'>, PaginationProps>((props, ref) => {
  const config = useComponentConfig('pagination');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <PaginationUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Pagination.displayName = 'Pagination';

export default Pagination;

import { cn } from '@/lib/utils';
import { paginationVariants } from './pagination-variants';
import type { PaginationContentProps } from './types';

const PaginationContent = (props: PaginationContentProps) => {
  const { className, children, size, ...rest } = props;

  const { list } = paginationVariants({ size });

  const mergedCls = cn(list(), className);

  return (
    <div
      className={mergedCls}
      data-slot="pagination-content"
      {...rest}
    >
      {children}
    </div>
  );
};

export default PaginationContent;

import { EllipsisIcon } from 'lucide-react';
import { cn } from '@/lib/utils';
import { paginationVariants } from './pagination-variants';
import type { PaginationEllipsisProps } from './types';

const PaginationEllipsis = (props: PaginationEllipsisProps) => {
  const { className, children, icon, size, ...rest } = props;

  const { ellipsis } = paginationVariants({ size });

  const mergedCls = cn(ellipsis(), className);

  return (
    <div
      aria-hidden
      className={mergedCls}
      data-slot="pagination-ellipsis"
      {...rest}
    >
      {children ?? icon ?? <EllipsisIcon />}
      <span className="sr-only">More pages</span>
    </div>
  );
};

export default PaginationEllipsis;

