'use client';

import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import PasswordUI from './PasswordUI';
import type { PasswordProps } from './types';

const Password = forwardRef<HTMLInputElement, PasswordProps>((props, ref) => {
  const config = useComponentConfig('password');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <PasswordUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Password.displayName = 'Password';

export default Password;

'use client';

import { forwardRef } from 'react';
import { Eye, EyeOff } from 'lucide-react';
import { useControllableState } from '@radix-ui/react-use-controllable-state';
import Input from '../input/InputUI';
import type { PasswordProps } from './types';

const PasswordUI = forwardRef<HTMLInputElement, PasswordProps>((props, ref) => {
  const {
    trailing,
    visible: visibleProp,
    defaultVisible = false,
    onVisibleChange,
    visibleIcon,
    hiddenIcon,
    ...rest
  } = props;

  const [visible, setVisible] = useControllableState({
    defaultProp: defaultVisible,
    onChange: onVisibleChange,
    prop: visibleProp
  });

  function toggleVisible() {
    setVisible(!visible);
  }

  const VisibleIcon = visible
    ? (visibleIcon || <Eye className="cursor-pointer" />)
    : (hiddenIcon || <EyeOff className="cursor-pointer" />);

  return (
    <Input
      aria-roledescription="Password"
      autoComplete="off"
      data-slot="password"
      ref={ref}
      type={visible ? 'text' : 'password'}
      {...rest}

      // eslint-disable-next-line react/jsx-props-no-multi-spaces
      trailing={(
        <>
          {trailing}

          <span
            data-slot="password-visible"
            onClick={toggleVisible}
          >
            {VisibleIcon}
          </span>
        </>
      )}
    />
  );
});

PasswordUI.displayName = 'PasswordUI';

export default PasswordUI;

import type { ReactNode } from 'react';
import type { InputProps } from '../input';

/**
 * Props for the Password component.
 * A styled password input element with visibility toggle and optional clear button.
 *
 * @example
 * ```tsx
 * // Basic password input
 * <Password placeholder="Enter password" />
 *
 * // Controlled password with visibility
 * <Password
 *   value={password}
 *   onChange={e => setPassword(e.target.value)}
 *   visible={visible}
 *   onVisibleChange={setVisible}
 * />
 *
 * // Password with clearable button
 * <Password clearable placeholder="Enter password" />
 *
 * // Password with custom visibility icons
 * <Password
 *   visibleIcon={<LockOpen />}
 *   hiddenIcon={<Lock />}
 * />
 *
 * // Disabled password
 * <Password disabled placeholder="Disabled" />
 * ```
 */
export interface PasswordProps extends InputProps {

  /**
   * Controlled visibility state of the password.
   */
  visible?: boolean;
  /**
   * Default visibility state when uncontrolled.
   * @default false
   */
  defaultVisible?: boolean;
  /**
   * Callback when visibility state changes.
   */
  onVisibleChange?: (visible: boolean) => void;
  /**
   * Custom icon to show when password is visible.
   * @default <Eye />
   */
  visibleIcon?: ReactNode;
  /**
   * Custom icon to show when password is hidden.
   * @default <EyeOff />
   */
  hiddenIcon?: ReactNode;
}
