'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Content } from '@radix-ui/react-popover';
import { useComponentConfig } from '../config-provider/context';
import PopoverUI from './PopoverUI';
import type { PopoverProps } from './types';

const Popover = forwardRef<ComponentRef<typeof Content>, PopoverProps>((props, ref) => {
  const config = useComponentConfig('popover');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <PopoverUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Popover.displayName = 'Popover';

export default Popover;

import { Anchor } from '@radix-ui/react-popover';
import { cn } from '@/lib/utils';
import { popoverVariants } from './popover-varianst';
import type { PopoverAnchorProps } from './types';

const PopoverAnchor = (props: PopoverAnchorProps) => {
  const { className, ...rest } = props;

  const { anchor } = popoverVariants();

  const mergedCls = cn(anchor(), className);

  return (
    <Anchor
      className={mergedCls}
      data-slot="popover-anchor"
      {...rest}
    />
  );
};

export default PopoverAnchor;

import { Arrow } from '@radix-ui/react-popover';
import { cn } from '@/lib/utils';
import { popoverVariants } from './popover-varianst';
import type { PopoverArrowProps } from './types';

const PopoverArrow = (props: PopoverArrowProps) => {
  const { className, size, ...rest } = props;

  const { arrow } = popoverVariants({ size });

  const mergedCls = cn(arrow(), className);

  return (
    <Arrow
      className={mergedCls}
      data-slot="popover-arrow"
      {...rest}
    />
  );
};

export default PopoverArrow;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Content } from '@radix-ui/react-popover';
import { cn } from '@/lib/utils';
import { popoverVariants } from './popover-varianst';
import type { PopoverContentProps } from './types';

const PopoverContent = forwardRef<ComponentRef<typeof Content>, PopoverContentProps>((props, ref) => {
  const { avoidCollisions = true, className, sideOffset = 5, size, ...rest } = props;

  const { content } = popoverVariants({ size });

  const mergedCls = cn(content(), className);

  return (
    <Content
      avoidCollisions={avoidCollisions}
      className={mergedCls}
      data-slot="popover-content"
      sideOffset={sideOffset}
      {...rest}
      ref={ref}
    />
  );
});

PopoverContent.displayName = 'PopoverContent';

export default PopoverContent;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Content } from '@radix-ui/react-popover';
import { Close, Portal, Root, Trigger } from '@radix-ui/react-popover';
import PopoverAnchor from './PopoverAnchor';
import PopoverArrow from './PopoverArrow';
import PopoverContent from './PopoverContent';
import type { PopoverProps } from './types';

const PopoverUI = forwardRef<ComponentRef<typeof Content>, PopoverProps>((props, ref) => {
  const {
    arrowHeight,
    arrowWidth,
    children,
    className,
    classNames,
    closeIcon,
    container,
    defaultOpen,
    forceMountPortal,
    modal,
    onOpenChange,
    open,
    showArrow,
    size,
    trigger,
    anchorProps,
    contentProps,
    arrowProps,
    ...rest
  } = props;

  return (
    <Root
      defaultOpen={defaultOpen}
      modal={modal}
      open={open}
      onOpenChange={onOpenChange}
    >
      <PopoverAnchor {...anchorProps}>
        <Trigger asChild>{trigger}</Trigger>
      </PopoverAnchor>

      <Portal
        container={container}
        data-slot="popover-portal"
        forceMount={forceMountPortal}
      >
        <PopoverContent
          {...rest}
          {...contentProps}
          className={className || classNames?.content}
          ref={ref}
          size={size}
        >
          {children}
          {closeIcon ? <Close asChild>{closeIcon}</Close> : null}

          {showArrow
            ? (
              <PopoverArrow
                className={classNames?.arrow}
                height={arrowHeight}
                size={size}
                width={arrowWidth}
                {...arrowProps}
              />
            )
            : null}
        </PopoverContent>
      </Portal>
    </Root>
  );
});

PopoverUI.displayName = 'PopoverUI';

export default PopoverUI;

export { Popover as PopoverRoot, PopoverPortal, PopoverTrigger } from '@radix-ui/react-popover';

export { default as PopoverAnchor } from './PopoverAnchor';

export { default as PopoverArrow } from './PopoverArrow';

export { default as PopoverContent } from './PopoverContent';

export { default as Popover } from './Popover';

export { default as PopoverUI } from './PopoverUI';

export * from './types';

import { tv } from 'tailwind-variants';

export const popoverVariants = tv({
  defaultVariants: {
    size: 'md'
  },
  slots: {
    anchor: 'w-fit',
    arrow: 'w-1em h-0.5em fill-popover stroke-border',
    content: [
      `w-auto rounded-md border bg-popover  text-popover-foreground shadow-md outline-none z-50 will-change-transform`,
      `data-[state=open]:animate-in data-[state=open]:fade-in-0 data-[state=open]:zoom-in-95`,
      `data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=closed]:zoom-out-95`,
      `data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2`
    ]
  },
  variants: {
    size: {
      '2xl': {
        arrow: 'text-lg',
        content: 'p-5.5 text-xl'
      },
      'lg': {
        arrow: 'text-sm',
        content: 'p-4.5 text-base'
      },
      'md': {
        arrow: 'text-xs',
        content: 'p-4 text-sm'
      },
      'sm': {
        arrow: 'text-2xs',
        content: 'p-3.5 text-xs'
      },
      'xl': {
        arrow: 'text-base',
        content: 'p-5 text-lg'
      },
      'xs': {
        arrow: 'text-3xs',
        content: 'p-3 text-2xs'
      }
    }
  }
});

export type PopoverSlots = keyof typeof popoverVariants.slots;

import type {
  PopoverPortalProps,
  PopoverAnchorProps as _PopoverAnchorProps,
  PopoverArrowProps as _PopoverArrowProps,
  PopoverContentProps as _PopoverContentProps,
  PopoverProps as _PopoverProps
} from '@radix-ui/react-popover';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { PopoverSlots } from './popover-varianst';

/**
 * Class names for different slots in the popover component.
 * Allows customizing styles for specific parts of the popover.
 */
export type PopoverClassNames = Partial<Record<PopoverSlots, ClassValue>>;

/**
 * Props for the PopoverArrow component.
 * Renders a decorative arrow pointing to the popover trigger.
 *
 * @example
 * <PopoverArrow />
 */
export type PopoverArrowProps = StyledComponentProps<_PopoverArrowProps>;

/**
 * Props for the PopoverContent component.
 * Container for the popover body content.
 *
 * @example
 * <PopoverContent>
 *   <p>Popover content goes here</p>
 * </PopoverContent>
 */
export type PopoverContentProps = StyledComponentProps<_PopoverContentProps>;

/**
 * Props for the main Popover component.
 * A component that displays content in a floating panel relative to a trigger element.
 * Combines trigger and content functionality with optional arrow and close button.
 *
 * @example
 * // Basic popover
 * <Popover trigger={<button>Click me</button>} showArrow>
 *   <p>This is the popover content</p>
 * </Popover>
 *
 * @example
 * // Popover with close button
 * <Popover
 *   trigger={<button>Options</button>}
 *   closeIcon={<XIcon />}
 *   side="bottom"
 *   sideOffset={10}
 * >
 *   <div className="p-4">Settings</div>
 * </Popover>
 *
 * @example
 * // Customized arrow size
 * <Popover
 *   trigger={<button>Help</button>}
 *   showArrow
 *   arrowWidth={20}
 *   arrowHeight={10}
 * >
 *   <p>Help content</p>
 * </Popover>
 */
export type PopoverProps = _PopoverProps
  & Pick<PopoverPortalProps, 'container' | 'forceMount'>
  & Omit<PopoverContentProps, 'forceMount'> & {
    /** Height of the arrow element in pixels */
    arrowHeight?: number;
    /** Width of the arrow element in pixels */
    arrowWidth?: number;
    /** Class names for customizing different parts of the popover */
    classNames?: PopoverClassNames;
    /** Icon or element to display as the close button */
    closeIcon?: React.ReactNode;
    /** Whether to disable the portal container */
    disabledPortal?: boolean;
    /** Force mount the popover portal */
    forceMountPortal?: true;
    /** Whether to display an arrow pointing to the trigger */
    showArrow?: boolean;
    /** Content to display in the popover trigger button */
    trigger?: React.ReactNode;
    /** Props for the popover anchor component */
    anchorProps?: PopoverAnchorProps;
    /** Props for the popover content component */
    contentProps?: PopoverContentProps;
    /** Props for the popover arrow component */
    arrowProps?: PopoverArrowProps;
  };

/**
 * Props for the PopoverAnchor component.
 * An alternative trigger element that anchors the popover without opening it.
 * Useful when you want the popover to appear relative to a specific element.
 *
 * @example
 * <Popover>
 *   <PopoverAnchor asChild>
 *     <div ref={anchorRef} />
 *   </PopoverAnchor>
 *   <PopoverContent>Content</PopoverContent>
 * </Popover>
 */
export type PopoverAnchorProps = StyledComponentProps<_PopoverAnchorProps>;
