'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Root } from '@radix-ui/react-progress';
import { useComponentConfig } from '../config-provider/context';
import ProgressUI from './ProgressUI';
import type { ProgressProps } from './types';

const Progress = forwardRef<ComponentRef<typeof Root>, ProgressProps>((props, ref) => {
  const config = useComponentConfig('progress');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <ProgressUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Progress.displayName = 'Progress';

export default Progress;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Indicator, Root } from '@radix-ui/react-progress';
import { cn } from '@/lib/utils';
import { progressVariants } from './progress-variants';
import type { ProgressProps } from './types';

const Progress = forwardRef<ComponentRef<typeof Root>, ProgressProps>((props, ref) => {
  const { className, classNames, color, size, value, ...rest } = props;

  const { indicator, root } = progressVariants({ color, size });

  const mergedCls = {
    indicator: cn(indicator(), classNames?.indicator),
    root: cn(root(), className, classNames?.root)
  };

  return (
    <Root
      className={mergedCls.root}
      data-slot="progress-root"
      ref={ref}
      value={value}
      {...rest}
    >
      <Indicator
        className={mergedCls.indicator}
        data-slot="progress-indicator"
        style={{ transform: `translateX(-${100 - (value || 0)}%)` }}
      />
    </Root>
  );
});

Progress.displayName = 'ProgressUI';

export default Progress;

import { tv } from 'tailwind-variants';

export const progressVariants = tv({
  defaultVariants: {
    color: 'primary',
    size: 'md'
  },
  slots: {
    indicator: 'flex-1 size-full transition-all duration-200',
    root: 'relative w-full overflow-hidden rounded-full'
  },
  variants: {
    color: {
      accent: {
        indicator: 'bg-accent-foreground',
        root: 'bg-accent-foreground/20'
      },
      carbon: {
        indicator: 'bg-carbon',
        root: 'bg-carbon/20'
      },
      destructive: {
        indicator: 'bg-destructive',
        root: 'bg-destructive/20'
      },
      info: {
        indicator: 'bg-info',
        root: 'bg-info/20'
      },
      primary: {
        indicator: 'bg-primary',
        root: 'bg-primary/20'
      },
      secondary: {
        indicator: 'bg-secondary-foreground',
        root: 'bg-secondary-foreground/20'
      },
      success: {
        indicator: 'bg-success',
        root: 'bg-success/20'
      },
      warning: {
        indicator: 'bg-warning',
        root: 'bg-warning/20'
      }
    },
    size: {
      '2xl': {
        root: 'h-4'
      },
      'lg': {
        root: 'h-3'
      },
      'md': {
        root: 'h-2.5'
      },
      'sm': {
        root: 'h-2'
      },
      'xl': {
        root: 'h-3.5'
      },
      'xs': {
        root: 'h-1.75'
      }
    }
  }
});

export type ProgressSlots = keyof typeof progressVariants.slots;

import type { ProgressProps as _ProgressProps } from '@radix-ui/react-progress';
import type { StyledComponentProps, ClassValue, ThemeColor } from '@/types/shared';
import type { ProgressSlots } from './progress-variants';

/**
 * Class names for different slots in the progress component.
 * Allows customizing styles for specific parts of the progress bar.
 */
export type ProgressClassNames = Partial<Record<ProgressSlots, ClassValue>>;

/**
 * Props for the Progress component.
 * A visual indicator showing the progress of an operation or task.
 *
 * @example
 * // Basic progress bar at 60%
 * <Progress value={60} max={100} />
 *
 * @example
 * // Progress bar with custom color
 * <Progress
 *   value={75}
 *   max={100}
 *   color="success"
 *   classNames={{
 *     root: 'h-2',
 *     indicator: 'bg-green-500'
 *   }}
 * />
 *
 * @example
 * // Indeterminate progress (loading)
 * <Progress className="animate-pulse" />
 *
 * @example
 * // Progress with label
 * <div className="space-y-2">
 *   <div className="flex justify-between text-sm">
 *     <span>Loading...</span>
 *     <span>45%</span>
 *   </div>
 *   <Progress value={45} max={100} />
 * </div>
 */
export interface ProgressProps extends StyledComponentProps<Omit<_ProgressProps, 'children'>> {
  /** Class names for customizing different parts of the progress component */
  classNames?: ProgressClassNames;
  /** Color variant for the progress bar */
  color?: ThemeColor;
}
