'use client';

import { useComponentConfig } from '../config-provider/context';
import RadioUI from './RadioUI';
import type { RadioProps } from './types';

const Radio = (props: RadioProps) => {
  const config = useComponentConfig('radio');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <RadioUI
      {...mergedProps}
    />
  );
};

Radio.displayName = 'Radio';

export default Radio;

'use client';

import { forwardRef, useId } from 'react';
import { Item } from '@radix-ui/react-radio-group';
import { Slot } from '@radix-ui/react-slot';
import { cn } from '@/lib/utils';
import RadioLabel from '../label/Label';
import { radioVariants } from './radio-variants';
import RadioIndicator from './RadioIndicator';
import type { RadioCardProps } from './types';

const RadioCard = forwardRef<HTMLButtonElement, RadioCardProps>((props, ref) => {
  const {
    checked,
    radioPosition = 'right',
    className,
    classNames,
    color,
    description,
    disabled,
    icon,
    id: controlId,
    label,
    size,
    value,
    variant,
    ...rest
  } = props;

  const id = useId();

  const {
    card,
    cardContent,
    cardLabel,
    cardDescription,
    cardTextContent,
    control,
    indicator
  } = radioVariants({ color, size, variant });

  const mergedCls = cn(
    card(),
    disabled && 'cursor-not-allowed opacity-50',
    className || classNames?.card
  );

  const labelCls = cn(
    cardLabel(),
    classNames?.cardLabel
  );

  const descriptionCls = cn(
    cardDescription(),
    classNames?.cardDescription
  );

  const contentCls = cn(
    cardContent(),
    classNames?.cardContent
  );

  const textContentCls = cn(
    cardTextContent(),
    classNames?.cardTextContent
  );

  const controlCls = cn(
    control(),
    classNames?.control
  );

  const indicatorCls = cn(
    indicator(),
    classNames?.indicator
  );

  const radioElement = (
    <Item
      className={controlCls}
      disabled={disabled}
      id={controlId || id}
      ref={ref}
      value={value}
      {...rest}
    >
      <RadioIndicator
        className={indicatorCls}
        color={color}
        variant={variant}
      />
    </Item>
  );

  const contentElement = (
    <div className={contentCls}>
      {icon ? <Slot className="shrink-0 text-lg">{icon}</Slot> : null}

      <div className={textContentCls}>
        {label
          ? (
            <span className={labelCls}>
              {label}
            </span>
          )
          : null}

        {description
          ? (
            <p className={descriptionCls}>
              {description}
            </p>
          )
          : null}
      </div>
    </div>
  );

  return (
    <RadioLabel
      className={mergedCls}
      data-slot="radio-card"
      data-state={checked ? 'checked' : 'unchecked'}
      htmlFor={controlId || id}
    >
      {radioPosition === 'left' && radioElement}
      {contentElement}
      {radioPosition === 'right' && radioElement}
    </RadioLabel>
  );
});

RadioCard.displayName = 'RadioCard';

export default RadioCard;

'use client';

import { Root } from '@radix-ui/react-radio-group';
import { cn } from '@/lib/utils';
import { radioVariants } from './radio-variants';
import RadioCard from './RadioCard';
import type { RadioCardGroupProps } from './types';

const RadioCardGroup = (props: RadioCardGroupProps) => {
  const {
    className,
    classNames,
    color,
    disabled,
    items,
    orientation = 'horizontal',
    radioPosition = 'right',
    size,
    value,
    variant,
    ...rest
  } = props;

  const { group } = radioVariants({ orientation, size });

  const mergedCls = cn(group(), className || classNames?.group);

  return (
    <Root
      className={mergedCls}
      disabled={disabled}
      value={value}
      {...rest}
    >
      {items.map((item) => {
        const isChecked = value === item.value;
        const isDisabled = disabled || item.disabled;

        return (
          <RadioCard
            checked={isChecked}
            classNames={classNames}
            color={color}
            disabled={isDisabled}
            key={item.value}
            radioPosition={radioPosition}
            size={size}
            variant={variant}
            {...item}
          />
        );
      })}
    </Root>
  );
};

RadioCardGroup.displayName = 'RadioCardGroup';

export default RadioCardGroup;

import { Root } from '@radix-ui/react-radio-group';
import { cn } from '@/lib/utils';
import Radio from './Radio';
import { radioVariants } from './radio-variants';
import type { RadioGroupProps } from './types';

const RadioGroup = (props: RadioGroupProps) => {
  const { className, classNames, color, items, orientation, size, variant, ...rest } = props;

  const { group } = radioVariants({ orientation, size });

  const mergedCls = cn(group(), className || classNames?.group);

  return (
    <Root
      className={mergedCls}
      {...rest}
    >
      {items.map(item => (
        <Radio
          classNames={classNames}
          color={color}
          key={item.value}
          size={size}
          variant={variant}
          {...item}
        />
      ))}
    </Root>
  );
};

export default RadioGroup;

import { Item } from '@radix-ui/react-radio-group';
import { cn } from '@/lib/utils';
import { radioVariants } from './radio-variants';
import type { RadioGroupItemProps } from './types';

const RadioGroupItem = (props: RadioGroupItemProps) => {
  const { className, color, size, variant, ...rest } = props;

  const { control } = radioVariants({ color, size, variant });

  const mergedCls = cn(control(), className);

  return (
    <Item
      className={mergedCls}
      data-color={color}
      data-size={size}
      data-slot="radio-group-item"
      {...rest}
    />
  );
};

export default RadioGroupItem;

import { Indicator } from '@radix-ui/react-radio-group';
import { cn } from '@/lib/utils';
import { radioVariants } from './radio-variants';
import type { RadioIndicatorProps } from './types';

const RadioIndicator = (props: RadioIndicatorProps) => {
  const { className, color, variant, ...rest } = props;

  const { indicator } = radioVariants({ color, variant });

  const mergedCls = cn(indicator(), className);

  return (
    <Indicator
      className={mergedCls}
      data-color={color}
      data-slot="radio-indicator"
      {...rest}
    />
  );
};

export default RadioIndicator;

import { cn } from '@/lib/utils';
import { radioVariants } from './radio-variants';
import type { RadioRootProps } from './types';

const RadioRoot = (props: RadioRootProps) => {
  const { className, size, ...rest } = props;

  const { root } = radioVariants({ size });

  const mergedCls = cn(root(), className);

  return (
    <div
      className={mergedCls}
      data-size={size}
      data-slot="radio-root"
      {...rest}
    />
  );
};

export default RadioRoot;

'use client';

import { useId } from 'react';
import RadioLabel from '../label/Label';
import RadioGroupItem from './RadioGroupItem';
import RadioIndicator from './RadioIndicator';
import RadioRoot from './RadioRoot';
import type { RadioProps } from './types';

const Radio = (props: RadioProps) => {
  const { className, classNames, color, id, label, size, value, variant, rootProps, itemProps, indicatorProps, ...rest } = props;

  const internalId = useId();

  const controlId = id || internalId;

  return (
    <RadioRoot
      className={className || classNames?.root}
      size={size}
      {...rootProps}
    >
      <RadioGroupItem
        {...rest}
        {...itemProps}
        color={color}
        id={controlId}
        size={size}
        value={value}
        variant={variant}
      >
        <RadioIndicator
          className={classNames?.indicator}
          color={color}
          variant={variant}
          {...indicatorProps}
        />
      </RadioGroupItem>

      {label
        ? (
          <RadioLabel
            className={classNames?.label}
            htmlFor={controlId}
            size={size}
          >
            {label}
          </RadioLabel>
        )
        : null}
    </RadioRoot>
  );
};

Radio.displayName = 'RadioUI';

export default Radio;

export { default as Radio } from './Radio';
export { default as RadioCard } from './RadioCard';
export { default as RadioCardGroup } from './RadioCardGroup';
export { default as RadioGroup } from './RadioGroup';
export { default as RadioGroupItem } from './RadioGroupItem';
export { default as RadioIndicator } from './RadioIndicator';
export { default as RadioRoot } from './RadioRoot';

export * from './types';

