'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import ScrollAreaUI from './ScrollAreaUI';
import type ScrollAreaRoot from './ScrollAreaRoot';
import type { ScrollAreaProps } from './types';

const ScrollArea = forwardRef<ComponentRef<typeof ScrollAreaRoot>, ScrollAreaProps>((props, ref) => {
  const config = useComponentConfig('scrollArea');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <ScrollAreaUI
      {...mergedProps}
      ref={ref}
    />
  );
});

ScrollArea.displayName = 'ScrollArea';

export default ScrollArea;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root } from '@radix-ui/react-scroll-area';
import { cn } from '@/lib/utils';
import { scrollAreaVariants } from './scroll-area-variants';
import type { ScrollAreaRootProps } from './types';

const ScrollAreaRoot = forwardRef<ComponentRef<typeof Root>, ScrollAreaRootProps>((props, ref) => {
  const { className, ...rest } = props;

  const { root } = scrollAreaVariants();

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      data-slot="scroll-area-root"
      {...rest}
      ref={ref}
    />
  );
});

ScrollAreaRoot.displayName = 'ScrollAreaRoot';

export default ScrollAreaRoot;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Scrollbar } from '@radix-ui/react-scroll-area';
import { cn } from '@/lib/utils';
import { scrollAreaVariants } from './scroll-area-variants';
import type { ScrollAreaScrollbarProps } from './types';

const ScrollAreaScrollbar = forwardRef<ComponentRef<typeof Scrollbar>, ScrollAreaScrollbarProps>((props, ref) => {
  const { className, orientation, size, ...rest } = props;

  const { scrollbar } = scrollAreaVariants({ orientation, size });

  const mergedCls = cn(scrollbar(), className);

  return (
    <Scrollbar
      className={mergedCls}
      data-slot="scroll-area-scrollbar"
      orientation={orientation}
      {...rest}
      ref={ref}
    />
  );
});

ScrollAreaScrollbar.displayName = 'ScrollAreaScrollbar';

export default ScrollAreaScrollbar;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Thumb } from '@radix-ui/react-scroll-area';
import { cn } from '@/lib/utils';
import { scrollAreaVariants } from './scroll-area-variants';
import type { ScrollAreaThumbProps } from './types';

const ScrollAreaScrollbar = forwardRef<ComponentRef<typeof Thumb>, ScrollAreaThumbProps>((props, ref) => {
  const { className, ...rest } = props;

  const { thumb } = scrollAreaVariants();

  const mergedCls = cn(thumb(), className);

  return (
    <Thumb
      className={mergedCls}
      data-slot="scroll-area-thumb"
      {...rest}
      ref={ref}
    />
  );
});

ScrollAreaScrollbar.displayName = 'ScrollAreaScrollbar';

export default ScrollAreaScrollbar;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { ScrollAreaCorner } from '@radix-ui/react-scroll-area';
import { cn } from '@/lib/utils';
import ScrollAreaRoot from './ScrollAreaRoot';
import ScrollAreaScrollbar from './ScrollAreaScrollbar';
import ScrollAreaThumb from './ScrollAreaThumb';
import ScrollAreaViewport from './ScrollAreaViewport';
import type { ScrollAreaProps } from './types';

const ScrollArea = forwardRef<ComponentRef<typeof ScrollAreaRoot>, ScrollAreaProps>((props, ref) => {
  const { children, className, classNames, forceMount, nonce, orientation, size, viewportProps, scrollbarProps, thumbProps, ...rest } = props;

  return (
    <ScrollAreaRoot
      className={className}
      {...rest}
      ref={ref}
    >
      <ScrollAreaViewport
        className={classNames?.viewport}
        nonce={nonce}
        {...viewportProps}
      >
        {children}
      </ScrollAreaViewport>

      <ScrollAreaScrollbar
        className={classNames?.scrollbar}
        forceMount={forceMount}
        orientation={orientation}
        size={size}
        {...scrollbarProps}
      >
        <ScrollAreaThumb
          className={classNames?.thumb}
          {...thumbProps}
        />
      </ScrollAreaScrollbar>

      <ScrollAreaCorner
        className={cn(classNames?.corner)}
        data-slot="scroll-area-corner"
      />
    </ScrollAreaRoot>
  );
});

ScrollArea.displayName = 'ScrollAreaUI';

export default ScrollArea;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Viewport } from '@radix-ui/react-scroll-area';
import { cn } from '@/lib/utils';
import { scrollAreaVariants } from './scroll-area-variants';
import type { ScrollAreaViewportProps } from './types';

const ScrollAreaViewport = forwardRef<ComponentRef<typeof Viewport>, ScrollAreaViewportProps>((props, ref) => {
  const { className, ...rest } = props;

  const { viewport } = scrollAreaVariants();

  const mergedCls = cn(viewport(), className);

  return (
    <Viewport
      className={mergedCls}
      data-slot="scroll-area-viewport"
      {...rest}
      ref={ref}
    />
  );
});

ScrollAreaViewport.displayName = 'ScrollAreaViewport';

export default ScrollAreaViewport;

export { default as ScrollArea } from './ScrollArea';
export { default as ScrollAreaRoot } from './ScrollAreaRoot';
export { default as ScrollAreaScrollbar } from './ScrollAreaScrollbar';
export { default as ScrollAreaThumb } from './ScrollAreaThumb';
export { default as ScrollAreaViewport } from './ScrollAreaViewport';

export * from './types';

import { tv } from 'tailwind-variants';

export const scrollAreaVariants = tv({
  compoundVariants: [
    {
      class: {
        scrollbar: 'h-1.5'
      },
      orientation: 'horizontal',
      size: 'xs'
    },
    {
      class: {
        scrollbar: 'w-1.5'
      },
      orientation: 'vertical',
      size: 'xs'
    },
    {
      class: {
        scrollbar: 'h-2'
      },
      orientation: 'horizontal',
      size: 'sm'
    },
    {
      class: {
        scrollbar: 'w-2'
      },
      orientation: 'vertical',
      size: 'sm'
    },
    {
      class: {
        scrollbar: 'h-2.5'
      },
      orientation: 'horizontal',
      size: 'md'
    },
    {
      class: {
        scrollbar: 'w-2.5'
      },
      orientation: 'vertical',
      size: 'md'
    },
    {
      class: {
        scrollbar: 'h-3'
      },
      orientation: 'horizontal',
      size: 'lg'
    },
    {
      class: {
        scrollbar: 'w-3'
      },
      orientation: 'vertical',
      size: 'lg'
    },
    {
      class: {
        scrollbar: 'h-3.5'
      },
      orientation: 'horizontal',
      size: 'xl'
    },
    {
      class: {
        scrollbar: 'w-3.5'
      },
      orientation: 'vertical',
      size: 'xl'
    },
    {
      class: {
        scrollbar: 'h-4'
      },
      orientation: 'horizontal',
      size: '2xl'
    },
    {
      class: {
        scrollbar: 'w-4'
      },
      orientation: 'vertical',
      size: '2xl'
    }
  ],
  defaultVariants: {
    orientation: 'vertical',
    size: 'md'
  },
  slots: {
    corner: '',
    root: 'relative overflow-hidden',
    scrollbar: 'flex touch-none select-none transition-colors-200',
    thumb: 'relative flex-1 rounded-full bg-border',
    viewport: 'size-full rounded-inherit'
  },
  variants: {
    orientation: {
      horizontal: {
        scrollbar: 'flex-col p-px border-t border-t-transparent'
      },
      vertical: {
        scrollbar: 'h-full p-px border-l border-l-transparent'
      }
    },
    size: {
      '2xl': {},
      'lg': {},
      'md': {},
      'sm': {},
      'xl': {},
      'xs': {}
    }
  }
});

export type ScrollAreaSlots = keyof typeof scrollAreaVariants.slots;

import type {
  ScrollAreaProps as _ScrollAreaProps,
  ScrollAreaScrollbarProps as _ScrollAreaScrollbarProps,
  ScrollAreaThumbProps as _ScrollAreaThumbProps,
  ScrollAreaViewportProps as _ScrollAreaViewportProps
} from '@radix-ui/react-scroll-area';
import type { StyledComponentProps, ClassValue } from '@/types/shared';
import type { ScrollAreaSlots } from './scroll-area-variants';

/**
 * Class names for different slots in the scroll area component.
 * Allows customizing styles for the root, scrollbar, thumb, and viewport elements.
 */
export type ScrollAreaUi = Partial<Record<ScrollAreaSlots, ClassValue>>;

/**
 * Props for the scroll area root component.
 * Provides the container for scrollable content with custom scrollbar styling.
 */
export interface ScrollAreaRootProps extends StyledComponentProps<_ScrollAreaProps> {}

/**
 * Props for the scroll area scrollbar component.
 * Represents the track element that contains the thumb for vertical or horizontal scrolling.
 */
export interface ScrollAreaScrollbarProps extends StyledComponentProps<_ScrollAreaScrollbarProps> {}

/**
 * Props for the scroll area thumb component.
 * The draggable indicator that shows the scroll position and can be dragged to scroll.
 */
export interface ScrollAreaThumbProps extends StyledComponentProps<_ScrollAreaThumbProps> {}

/**
 * Props for the scroll area viewport component.
 * The container that holds the actual scrollable content.
 */
export interface ScrollAreaViewportProps extends StyledComponentProps<_ScrollAreaViewportProps> {}

/**
 * Props for the main ScrollArea component.
 * Combines root, scrollbar, and viewport configurations for a complete scrollable region.
 *
 * @example
 * ```tsx
 * <ScrollArea classNames={{ viewport: "max-h-96" }}>
 *   <div>Scrollable content here</div>
 * </ScrollArea>
 * ```
 */
export interface ScrollAreaProps
  extends ScrollAreaRootProps,
  Omit<ScrollAreaScrollbarProps, 'dir'>,
  Omit<ScrollAreaViewportProps, 'dir'> {
  /**
   * Class names for customizing different parts of the scroll area component.
   */
  classNames?: ScrollAreaUi;
  /**
   * Props for the scroll area viewport component.
   */
  viewportProps?: ScrollAreaViewportProps;
  /**
   * Props for the scroll area scrollbar component.
   */
  scrollbarProps?: ScrollAreaScrollbarProps;
  /**
   * Props for the scroll area thumb component.
   */
  thumbProps?: ScrollAreaThumbProps;
}
