'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Root } from '@radix-ui/react-tabs';
import { useComponentConfig } from '../config-provider/context';
import SegmentUI from './SegmentUI';
import type { SegmentOptionData, SegmentProps } from './types';

const Segment = forwardRef<ComponentRef<typeof Root>, SegmentProps<SegmentOptionData>>((props, ref) => {
  const config = useComponentConfig('segment');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <SegmentUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Segment.displayName = 'Segment';

export default Segment;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import type { Root } from '@radix-ui/react-tabs';
import SegmentList from '../tabs/TabsList';
import SegmentRoot from '../tabs/TabsRoot';
import SegmentTrigger from '../tabs/TabsTrigger';
import type { SegmentOptionData, SegmentProps } from './types';

const Segment = forwardRef<ComponentRef<typeof Root>, SegmentProps<SegmentOptionData>>((props, ref) => {
  const {
    className,
    classNames,
    dir,
    enableIndicator = true,
    items,
    loop,
    orientation = 'horizontal',
    size,
    value,
    ...rest
  } = props;

  return (
    <SegmentRoot
      className={[className, classNames?.root]}
      dir={dir}
      ref={ref}
      size={size}
      value={value}
      {...rest}
    >
      <SegmentList
        className={classNames?.list}
        dir={dir}
        enableIndicator={enableIndicator}
        loop={loop}
        orientation={orientation}
        size={size}
        value={value}
        classNames={{
          indicator: classNames?.indicator,
          indicatorRoot: classNames?.indicatorRoot
        }}
      >
        {items.map(item => (
          <SegmentTrigger
            className={classNames?.trigger}
            dir={dir}
            disabled={item.disabled}
            enableIndicator={enableIndicator}
            key={item.value}
            size={size}
            value={item.value}
          >
            {item.label}
          </SegmentTrigger>
        ))}
      </SegmentList>
    </SegmentRoot>
  );
});

Segment.displayName = 'SegmentUI';

export default Segment;

import type { TabsOptionData, TabsProps } from '../tabs/types';

/**
 * Data structure for a single segment option in the segment control.
 * Extends TabsOptionData but removes the children property since segments
 * typically do not display content panels like tabs do.
 */
export type SegmentOptionData = Omit<TabsOptionData, 'children'>;

/**
 * Props for the Segment Control component.
 * A segmented control (also known as segmented button group) for selecting one option
 * from multiple mutually exclusive choices.
 *
 * @template T - Type of segment option data, defaults to SegmentOptionData
 *
 * @example
 * ```tsx
 * <Segment
 *   items={[
 *     { value: "day", label: "Day" },
 *     { value: "week", label: "Week" },
 *     { value: "month", label: "Month" }
 *   ]}
 *   defaultValue="day"
 * />
 * ```
 */
export type SegmentProps<T extends SegmentOptionData> = Omit<TabsProps<TabsOptionData>, 'items'> & {
  /**
   * Array of segment options to display.
   * Each option represents a selectable segment button.
   */
  items: T[];
};
