'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { useComponentConfig } from '../config-provider/context';
import type TabsRoot from './TabsRoot';
import TabsUI from './TabsUI';
import type { TabsOptionData, TabsProps } from './types';

const Tabs = forwardRef<ComponentRef<typeof TabsRoot>, TabsProps<TabsOptionData>>((props, ref) => {
  const config = useComponentConfig('tabs');

  const mergedProps = {
    ...config,
    ...props
  };

  return (
    <TabsUI
      {...mergedProps}
      ref={ref}
    />
  );
});

Tabs.displayName = 'Tabs';

export default Tabs;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { TabsContent as Content } from '@radix-ui/react-tabs';
import { cn } from '@/lib/utils';
import { tabsVariants } from './tabs-variants';
import type { TabsContentProps } from './types';

const TabsContent = forwardRef<ComponentRef<typeof Content>, TabsContentProps>((props, ref) => {
  const { className, orientation, size, ...rest } = props;

  const { content } = tabsVariants({ orientation, size });

  const mergedCls = cn(content(), className);

  return (
    <Content
      className={mergedCls}
      {...rest}
      ref={ref}
    />
  );
});

TabsContent.displayName = 'TabsContent';

export default TabsContent;

'use client';
import type { ComponentRef, CSSProperties } from 'react';
import { forwardRef, useEffect, useRef, useState } from 'react';
import { useComposedRefs } from '@radix-ui/react-compose-refs';
import { List } from '@radix-ui/react-tabs';
import { cn } from '@/lib/utils';
import { If } from '../if';
import { tabsVariants } from './tabs-variants';
import type { IndicatorStyle, TabsListProps } from './types';

const TabsList = forwardRef<ComponentRef<typeof List>, TabsListProps>((props, ref) => {
  const { children, className, classNames, dir, enableIndicator, orientation, size, value, ...rest } = props;

  const tabsListRef = useRef<HTMLDivElement>(null);

  const [indicatorStyle, setIndicatorStyle] = useState<IndicatorStyle>({
    position: null,
    size: null
  });

  const mergedRef = useComposedRefs(ref, tabsListRef);

  const { indicator, indicatorRoot, list } = tabsVariants({ orientation, size });

  const mergedCls = cn(list(), className);

  const mergedRootCls = cn(indicatorRoot(), classNames?.indicatorRoot);

  const mergedIndicatorCls = cn(indicator(), classNames?.indicator);

  function updateIndicatorStyle() {
    const activeTab = tabsListRef.current?.querySelector<HTMLButtonElement>('[role="tab"][data-state="active"]');

    if (!activeTab)
      return;

    if (orientation === 'horizontal') {
      setIndicatorStyle({
        position: activeTab.offsetLeft,
        size: activeTab.offsetWidth
      });
    }
    else {
      setIndicatorStyle({
        position: activeTab.offsetTop,
        size: activeTab.offsetHeight
      });
    }
  }

  useEffect(() => {
    updateIndicatorStyle();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value, dir]);
  return (
    <List
      className={mergedCls}
      dir={dir}
      {...rest}
      ref={mergedRef}
    >
      {children}

      <If condition={Boolean(enableIndicator)}>
        <div
          className={mergedRootCls}
          style={
            {
              '--soybean-tabs-indicator-position': `${indicatorStyle.position}px`,
              '--soybean-tabs-indicator-size': `${indicatorStyle.size}px`
            } as CSSProperties
          }
        >
          <div className={mergedIndicatorCls} />
        </div>
      </If>
    </List>
  );
});

TabsList.displayName = 'TabsList';

export default TabsList;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Root } from '@radix-ui/react-tabs';
import { cn } from '@/lib/utils';
import { tabsVariants } from './tabs-variants';
import type { TabsRootProps } from './types';

const TabRoot = forwardRef<ComponentRef<typeof Root>, TabsRootProps>((props, ref) => {
  const { className, fill, orientation, size, ...rest } = props;

  const { root } = tabsVariants({ fill, orientation, size });

  const mergedCls = cn(root(), className);

  return (
    <Root
      className={mergedCls}
      {...rest}
      ref={ref}
    />
  );
});

TabRoot.displayName = 'TabRoot';

export default TabRoot;

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import { Trigger } from '@radix-ui/react-tabs';
import { cn } from '@/lib/utils';
import { tabsVariants } from './tabs-variants';
import type { TabsTriggerProps } from './types';

const TabsTrigger = forwardRef<ComponentRef<typeof Trigger>, TabsTriggerProps>((props, ref) => {
  const { className, enableIndicator = true, size, ...rest } = props;

  const { trigger } = tabsVariants({ enableIndicator, size });

  const mergedCls = cn(trigger(), className);

  return (
    <Trigger
      className={mergedCls}
      {...rest}
      ref={ref}
    />
  );
});

TabsTrigger.displayName = 'TabsTrigger';

export default TabsTrigger;

'use client';

import type { ComponentRef } from 'react';
import { forwardRef } from 'react';
import TabsContent from './TabsContent';
import TabsList from './TabsList';
import Root from './TabsRoot';
import TabsTrigger from './TabsTrigger';
import type { TabsOptionData, TabsProps } from './types';

const TabsUI = forwardRef<ComponentRef<typeof Root>, TabsProps<TabsOptionData>>((props, ref) => {
  const {
    className,
    classNames,
    dir,
    enableIndicator = true,
    forceMountContent,
    items,
    loop,
    orientation = 'horizontal',
    size,
    value,
    listProps,
    triggerProps,
    contentProps,
    ...rest
  } = props;

  return (
    <Root
      className={[className, classNames?.root]}
      dir={dir}
      ref={ref}
      size={size}
      value={value}
      {...rest}
    >
      <TabsList
        className={classNames?.list}
        dir={dir}
        enableIndicator={enableIndicator}
        loop={loop}
        orientation={orientation}
        size={size}
        value={value}
        classNames={{
          indicator: classNames?.indicator,
          indicatorRoot: classNames?.indicatorRoot
        }}
        {...listProps}
      >
        {items.map(item => (
          <TabsTrigger
            className={classNames?.trigger}
            dir={dir}
            disabled={item.disabled}
            enableIndicator={enableIndicator}
            key={item.value}
            size={size}
            value={item.value}
            {...triggerProps}
          >
            {item.label}
          </TabsTrigger>
        ))}
      </TabsList>

      {items.map(item => (
        <TabsContent
          className={classNames?.content}
          dir={dir}
          forceMount={forceMountContent}
          key={item.value}
          orientation={orientation}
          size={size}
          value={item.value}
          {...contentProps}
        >
          {typeof item.children === 'function' ? item.children({ active: item.value === value, item }) : item.children}
        </TabsContent>
      ))}
    </Root>
  );
});

TabsUI.displayName = 'TabsUI';

export default TabsUI;

export { default as Tabs } from './Tabs';
export { default as TabsContent } from './TabsContent';
export { default as TabsList } from './TabsList';
export { default as TabsRoot } from './TabsRoot';
export { default as TabsTrigger } from './TabsTrigger';

export * from './types';

import { tv } from 'tailwind-variants';
import type { VariantProps } from 'tailwind-variants';

export const tabsVariants = tv({
  compoundVariants: [
    { class: { content: 'mt-1.5', indicatorRoot: 'py-0.75' }, orientation: 'horizontal', size: 'xs' },
    { class: { content: 'ml-1.5', indicatorRoot: 'px-0.75' }, orientation: 'vertical', size: 'xs' },
    { class: { content: 'mt-1.75', indicatorRoot: 'py-1' }, orientation: 'horizontal', size: 'sm' },
    { class: { content: 'ml-1.75', indicatorRoot: 'px-1' }, orientation: 'vertical', size: 'sm' },
    { class: { content: 'mt-2', indicatorRoot: 'py-1' }, orientation: 'horizontal', size: 'md' },
    { class: { content: 'ml-2', indicatorRoot: 'px-1' }, orientation: 'vertical', size: 'md' },
    { class: { content: 'mt-2.5', indicatorRoot: 'py-1.125' }, orientation: 'horizontal', size: 'lg' },
    { class: { content: 'ml-2.5', indicatorRoot: 'px-1.125' }, orientation: 'vertical', size: 'lg' },
    { class: { content: 'mt-3', indicatorRoot: 'py-1.25' }, orientation: 'horizontal', size: 'xl' },
    { class: { content: 'ml-3', indicatorRoot: 'px-1.25' }, orientation: 'vertical', size: 'xl' },
    { class: { content: 'mt-3.5', indicatorRoot: 'py-1.5' }, orientation: 'horizontal', size: '2xl' },
    { class: { content: 'ml-3.5', indicatorRoot: 'px-1.5' }, orientation: 'vertical', size: '2xl' }
  ],
  defaultVariants: {
    size: 'md',
    orientation: 'horizontal',
    shape: 'square',
    fill: 'auto',
    enableIndicator: true
  },
  slots: {
    content: `flex-grow self-stretch overflow-hidden focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-offset-background focus-visible:ring-primary`,
    indicator: `size-full bg-background shadow`,
    indicatorRoot: `absolute top-0 left-0 z-2 transition-all duration-300`,
    list: 'relative inline-flex justify-center items-center bg-muted text-muted-foreground',
    root: `flex`,
    trigger: [
      `relative z-3 inline-flex items-center justify-center flex-1 whitespace-nowrap rounded-md font-medium ease-in transition-all duration-200`,
      `focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-offset-background focus-visible:ring-primary`,
      `disabled:pointer-events-none disabled:opacity-50`
    ]
  },
  variants: {
    enableIndicator: {
      false: {
        trigger: `data-[state=active]:bg-background data-[state=active]:text-foreground data-[state=active]:shadow`
      }
    },
    fill: {
      auto: {
        root: `items-start`
      },
      full: {
        root: `items-stretch`
      }
    },
    shape: {
      square: {
        list: 'rounded-md',
        indicator: 'rounded-md'
      },
      rounded: {
        list: 'rounded-full',
        indicator: 'rounded-full'
      }
    },
    orientation: {
      horizontal: {
        indicatorRoot: `h-full w-(var(--radix-tabs-indicator-size)) translate-x-(var(--radix-tabs-indicator-position))`,
        root: `flex-col`
      },
      vertical: {
        indicatorRoot: `w-full h-(var(--radix-tabs-indicator-size)) translate-y-(var(--radix-tabs-indicator-position))`,
        list: `flex-col`
      }
    },
    size: {
      '2xl': {
        list: `p-1.5`,
        root: 'text-xl',
        trigger: `gap-4 px-6 py-1.5`
      },
      'lg': {
        list: `p-1.125`,
        root: 'text-base',
        trigger: `gap-2.5 px-4 py-1.125`
      },
      'md': {
        list: `p-1`,
        root: 'text-sm',
        trigger: `gap-2 px-3 py-1`
      },
      'sm': {
        list: `p-0.875`,
        root: 'text-xs',
        trigger: `gap-1.5 px-2 py-1`
      },
      'xl': {
        list: `p-1.25`,
        root: 'text-lg',
        trigger: `gap-3 px-5 py-1.25`
      },
      'xs': {
        list: `p-0.75`,
        root: 'text-2xs',
        trigger: `gap-1 px-1.5 py-0.75`
      }
    }
  }
});

export type TabsSlots = keyof typeof tabsVariants.slots;
export type TabsProps = VariantProps<typeof tabsVariants>;
export type TabsFill = NonNullable<TabsProps['fill']>;

import type {
  TabsContentProps as _TabsContentProps,
  TabsListProps as _TabsListProps,
  TabsProps as _TabsRootProps,
  TabsTriggerProps as _TabsTriggerProps
} from '@radix-ui/react-tabs';
import type { StyledComponentProps, ClassValue, ThemeOrientation } from '@/types/shared';
import type { TabsFill, TabsSlots } from './tabs-variants';

/**
 * Represents the visual style properties of the indicator.
 * Used to position and size the active tab indicator element.
 *
 * @example
 * ```tsx
 * const indicator: IndicatorStyle = {
 *   position: 0,      // Pixel offset from the left
 *   size: 100         // Width of the indicator in pixels
 * }
 * ```
 */
export interface IndicatorStyle {
  /**
   * The horizontal position (in pixels) where the indicator should be placed.
   * Null when no indicator is visible.
   */
  position: number | null;
  /**
   * The width (in pixels) of the indicator element.
   * Null when no indicator is visible.
   */
  size: number | null;
}

/**
 * Class names for different slots in the tabs component.
 * Allows customizing styles for specific parts of the tabs (root, triggers, content, indicator, etc.).
 *
 * @example
 * ```tsx
 * const classNames: TabsUi = {
 *   root: 'custom-root',
 *   list: 'custom-list',
 *   indicator: 'custom-indicator'
 * }
 * ```
 */
export type TabsUi = Partial<Record<TabsSlots, ClassValue>>;

/**
 * Props for the tabs root component.
 * Configures the main tabs container with fill style options.
 */
export interface TabsRootProps extends StyledComponentProps<Omit<_TabsRootProps, 'className'>> {
  /**
   * Determines how the tabs fill the available space.
   * Controls the layout style (e.g., fixed width, expand to fill, etc.).
   */
  fill?: TabsFill;
}

/**
 * Props for the tabs list component.
 * Wraps the individual tab triggers and manages the indicator.
 *
 * @example
 * ```tsx
 * <TabsList
 *   value="tab-1"
 *   enableIndicator={true}
 *   orientation="horizontal"
 *   classNames={{ indicator: 'custom-indicator' }}
 * />
 * ```
 */
export interface TabsListProps extends StyledComponentProps<Omit<_TabsListProps, 'className'>>, Pick<_TabsRootProps, 'value'> {
  /**
   * Class names for customizing the indicator and its container.
   */
  classNames?: Pick<TabsUi, 'indicator' | 'indicatorRoot'>;
  /**
   * Whether to display an animated indicator under the active tab.
   * Defaults to false.
   */
  enableIndicator?: boolean;
  /**
   * The direction of the tabs layout (horizontal or vertical).
   */
  orientation?: ThemeOrientation;
}

/**
 * Props for the tabs trigger component.
 * Represents a single clickable tab button.
 */
export interface TabsTriggerProps extends StyledComponentProps<Omit<_TabsTriggerProps, 'className'>> {
  /**
   * Whether to display an animated indicator under this trigger when active.
   * Overrides the list-level indicator setting if specified.
   */
  enableIndicator?: boolean;
}

/**
 * Props for the tabs content component.
 * Wraps the content displayed when a tab is active.
 *
 * @example
 * ```tsx
 * <TabsContent
 *   value="tab-1"
 *   orientation="horizontal"
 * >
 *   Content for tab 1
 * </TabsContent>
 * ```
 */
export interface TabsContentProps extends StyledComponentProps<Omit<_TabsContentProps, 'className'>> {
  /**
   * The direction of the content layout (horizontal or vertical).
   */
  orientation?: ThemeOrientation;
}

/**
 * Data structure for a single tabs item when using the data-driven approach.
 * Use this type to define items passed to the `items` prop of the Tabs component.
 *
 * @example
 * ```tsx
 * const items: TabsOptionData[] = [
 *   {
 *     value: 'tab-1',
 *     label: 'Tab 1',
 *     children: 'Content 1',
 *     disabled: false
 *   }
 * ]
 * ```
 */
export type TabsOptionData = Pick<TabsTriggerProps, 'disabled'> & {
  /**
   * The content to display when this tab is active.
   * Can be a static ReactNode or a render function that receives active state and item data.
   */
  children: React.ReactNode | ((props: { active: boolean; item: TabsOptionData }) => React.ReactNode);
  /**
   * The label/title displayed in the tab trigger button.
   */
  label: React.ReactNode;
  /**
   * Unique identifier for this tab.
   * Used to associate triggers with content and maintain tab state.
   */
  value: string;
};

/**
 * Props for the main Tabs component.
 * Supports data-driven rendering via the `items` prop.
 *
 * @template T - Type of tab item data, defaults to TabsOptionData
 *
 * @example
 * ```tsx
 * const items = [
 *   { value: 'tab-1', label: 'Tab 1', children: 'Content 1' },
 *   { value: 'tab-2', label: 'Tab 2', children: 'Content 2' }
 * ]
 *
 * <Tabs
 *   items={items}
 *   defaultValue="tab-1"
 *   enableIndicator={true}
 *   classNames={{ indicator: 'custom-indicator' }}
 * />
 * ```
 */
export type TabsProps<T extends TabsOptionData> = TabsRootProps
  & TabsListProps & {
    /**
     * Class names for customizing different slots of the tabs component.
     */
    classNames?: TabsUi;
    /**
     * Whether to display an animated indicator under the active tab.
     */
    enableIndicator?: boolean;
    /**
     * Whether to force mount all tab content even when inactive.
     * Useful for preserving component state across tab switches.
     */
    forceMountContent?: true;
    /**
     * Array of tab items to render.
     * Each item should have a unique `value` property.
     */
    items: T[];
    /**
     * Props for the tabs list component.
     */
    listProps?: TabsListProps;
    /**
     * Props for the tabs trigger component.
     */
    triggerProps?: TabsTriggerProps;
    /**
     * Props for the tabs content component.
     */
    contentProps?: TabsContentProps;
  };
