'use client';

import { useComponentConfig } from '../config-provider/context';
import VirtualGridUI from './VirtualGridUI';
import type { VirtualGridProps } from './types';

/**
 * A virtual grid component for efficiently rendering large grids.
 * Only renders visible cells, providing excellent performance for grids with thousands of cells.
 *
 * @example
 * ```tsx
 * // Basic usage with fixed cell size
 * <VirtualGrid
 *   data={items}
 *   columns={5}
 *   height={400}
 *   width={600}
 *   rowHeight={80}
 *   columnWidth={120}
 *   renderCell={(item, rowIndex, colIndex, style) => (
 *     <div key={item.id} style={style}>
 *       Cell {rowIndex},{colIndex}
 *     </div>
 *   )}
 * />
 *
 * // Variable size cells
 * <VirtualGrid
 *   data={items}
 *   columns={5}
 *   height={400}
 *   width={600}
 *   rowHeight={(index) => rowHeights[index]}
 *   columnWidth={(index) => columnWidths[index]}
 *   gap={8}
 *   renderCell={(item, rowIndex, colIndex, style) => (
 *     <div key={item.id} style={style}>
 *       {item.content}
 *     </div>
 *   )}
 * />
 * ```
 */
function VirtualGrid<T>(props: VirtualGridProps<T>) {
  const config = useComponentConfig('virtualGrid');

  const mergedProps = {
    ...config,
    ...props
  };

  return <VirtualGridUI {...mergedProps} />;
}

VirtualGrid.displayName = 'VirtualGrid';

export default VirtualGrid;

'use client';

import { VirtualGrid as VirtualGridPrimitive } from '@skyroc/virtualizer';
import { cn } from '@/lib/utils';
import { virtualizerVariants } from './virtualizer-variants';
import type { VirtualGridProps } from './types';

/**
 * A styled virtual grid component for efficiently rendering large grids.
 *
 * @example
 * ```tsx
 * // Basic usage
 * <VirtualGrid
 *   data={items}
 *   columns={5}
 *   height={400}
 *   width={600}
 *   rowHeight={80}
 *   columnWidth={120}
 *   renderCell={(item, rowIndex, colIndex, style) => (
 *     <div key={item.id} style={style} className="flex items-center justify-center border">
 *       {item.content}
 *     </div>
 *   )}
 * />
 *
 * // With custom styling
 * <VirtualGrid
 *   data={items}
 *   columns={5}
 *   height={400}
 *   width={600}
 *   rowHeight={80}
 *   columnWidth={120}
 *   classNames={{
 *     root: 'border rounded-lg',
 *   }}
 *   renderCell={(item, rowIndex, colIndex, style) => (
 *     <div key={item.id} style={style}>
 *       {item.content}
 *     </div>
 *   )}
 * />
 * ```
 */
function VirtualGridUI<T>(props: VirtualGridProps<T>) {
  const { classNames, ...rest } = props;

  const { root, inner } = virtualizerVariants();

  return (
    <VirtualGridPrimitive
      className={cn(root(), classNames?.root)}
      innerClassName={cn(inner(), classNames?.inner)}
      {...rest}
    />
  );
}

export default VirtualGridUI;

'use client';

import { useComponentConfig } from '../config-provider/context';
import VirtualListUI from './VirtualListUI';
import type { VirtualListProps } from './types';

/**
 * A virtual list component for efficiently rendering large lists.
 * Only renders visible items, providing excellent performance for lists with thousands of items.
 *
 * @example
 * ```tsx
 * // Basic usage with fixed item height
 * <VirtualList
 *   data={items}
 *   height={400}
 *   itemSize={50}
 *   renderItem={(item, index, style) => (
 *     <div key={item.id} style={style}>
 *       {item.name}
 *     </div>
 *   )}
 * />
 *
 * // Variable height items
 * <VirtualList
 *   data={items}
 *   height={400}
 *   itemSize={(index) => items[index].height}
 *   renderItem={(item, index, style) => (
 *     <div key={item.id} style={style}>
 *       {item.content}
 *     </div>
 *   )}
 * />
 *
 * // Horizontal list
 * <VirtualList
 *   data={items}
 *   width={800}
 *   height={100}
 *   horizontal
 *   itemSize={150}
 *   renderItem={(item, index, style) => (
 *     <div key={item.id} style={style}>
 *       {item.name}
 *     </div>
 *   )}
 * />
 * ```
 */
function VirtualList<T>(props: VirtualListProps<T>) {
  const config = useComponentConfig('virtualList');

  const mergedProps = {
    ...config,
    ...props
  };

  return <VirtualListUI {...mergedProps} />;
}

VirtualList.displayName = 'VirtualList';

export default VirtualList;

'use client';

import { VirtualList as VirtualListPrimitive } from '@skyroc/virtualizer';
import { cn } from '@/lib/utils';
import { virtualizerVariants } from './virtualizer-variants';
import type { VirtualListProps } from './types';

/**
 * A styled virtual list component for efficiently rendering large lists.
 *
 * @example
 * ```tsx
 * // Basic usage
 * <VirtualList
 *   data={items}
 *   height={400}
 *   itemSize={50}
 *   renderItem={(item, index, style) => (
 *     <div key={item.id} style={style} className="flex items-center px-4 border-b">
 *       {item.name}
 *     </div>
 *   )}
 * />
 *
 * // With custom styling
 * <VirtualList
 *   data={items}
 *   height={400}
 *   itemSize={50}
 *   classNames={{
 *     root: 'border rounded-lg',
 *     item: 'hover:bg-accent'
 *   }}
 *   renderItem={(item, index, style) => (
 *     <div key={item.id} style={style}>
 *       {item.name}
 *     </div>
 *   )}
 * />
 * ```
 */
function VirtualListUI<T>(props: VirtualListProps<T>) {
  const { classNames, ...rest } = props;

  const { root, inner, item } = virtualizerVariants();

  return (
    <VirtualListPrimitive
      className={cn(root(), classNames?.root)}
      innerClassName={cn(inner(), classNames?.inner)}
      {...rest}
    />
  );
}

export default VirtualListUI;

export { default as VirtualList } from './VirtualList';
export { default as VirtualGrid } from './VirtualGrid';

// Re-export hooks from @skyroc/virtualizer
export { useVirtualizer, useWindowVirtualizer } from '@skyroc/virtualizer';

export * from './types';

import type {
  UseVirtualizerOptions as BaseUseVirtualizerOptions,
  UseWindowVirtualizerOptions as BaseUseWindowVirtualizerOptions,
  VirtualGridProps as BaseVirtualGridProps,
  VirtualizerReturn as BaseVirtualizerReturn,
  VirtualItem,
  VirtualListProps as BaseVirtualListProps
} from '@skyroc/virtualizer';
import type { ClassValue } from '@/types/shared';

/**
 * Slot names for the Virtualizer component
 */
export type VirtualizerSlots = 'root' | 'inner' | 'item';

/**
 * Class names for different slots in the virtualizer component
 */
export type VirtualizerClassNames = Partial<Record<VirtualizerSlots, ClassValue>>;

/**
 * Props for the VirtualList UI component
 */
export interface VirtualListProps<T> extends Omit<BaseVirtualListProps<T>, 'className' | 'innerClassName'> {
  /**
   * Custom class names for component slots
   */
  classNames?: VirtualizerClassNames;
}

/**
 * Props for the VirtualGrid UI component
 */
export interface VirtualGridProps<T> extends Omit<BaseVirtualGridProps<T>, 'className' | 'innerClassName'> {
  /**
   * Custom class names for component slots
   */
  classNames?: VirtualizerClassNames;
}

/**
 * Re-export types from @skyroc/virtualizer for convenience
 */
export type {
  BaseUseVirtualizerOptions as UseVirtualizerOptions,
  BaseUseWindowVirtualizerOptions as UseWindowVirtualizerOptions,
  BaseVirtualizerReturn as VirtualizerReturn,
  VirtualItem
};

import { tv } from 'tailwind-variants';

export const virtualizerVariants = tv({
  slots: {
    inner: '',
    item: '',
    root: 'scrollbar-thin scrollbar-thumb-accent scrollbar-track-transparent'
  }
});
